com.mirth.connect.plugins.javascriptstep.script:
var interaction = sourceMap.get('fhirInteraction');

if (interaction == 'operation') {
	// Operation destinations will have a name of "$name".
	destinationSet.removeAllExcept([sourceMap.get('fhirOperationName')]);
} else if (interaction.startsWith('history')) {
	// This will match history-system, history-type, and history-instance
	destinationSet.removeAllExcept(['history']);
} else if (interaction.startsWith('search')) {
	// This will match search-system and search-type
	destinationSet.removeAllExcept(['search']);
} else {
	// All other destinations should have a name equal to the interaction
	destinationSet.removeAllExcept([interaction]);
}

properties.script:
/**
 * @author Frazer Smith
 * @description Rewritten example FHIR read destination to be usable for TrakCare calls.
 */
try {
	var type = $("fhirType").toLowerCase();
	var id = $("fhirId");

	// Turn array into multi-dimensional one to allow for up to four seperate WHERE clauses to be built
	var whereArray = [[], [], [], []];

	switch ("".concat(type)) {
		case "allergyintolerance":
			whereArray[0].push(
				"(alle.ALG_RowId = REPLACE(''".concat(id, "'', ''-'', ''||''))")
			);

			break;
		case "condition":
			break;
		case "documentreference":
			break;
		case "encounter":
			whereArray[0].push(
				"(app.APPT_RowId = REPLACE(''".concat(id, "'', ''-'', ''||''))")
			);

			whereArray[1].push(
				"(PAADM_ADMNo = REPLACE(''".concat(id, "'', ''-'', ''/''))")
			);

			whereArray[2].push(
				"(TRANS_ParRef->PAADM_ADMNo = REPLACE(''".concat(
					id,
					"'', ''-'', ''/''))"
				)
			);

			break;
		case "flag":
			whereArray[0].push(
				"(alert.ALM_RowID = REPLACE(''".concat(
					id,
					"'', ''-'', ''||''))"
				)
			);

			break;
		case "medicationstatement":
			whereArray[0].push(
				"(oi.OEORI_RowID = REPLACE(''".concat(id, "'', ''-'', ''||''))")
			);

			break;
		case "patient":
			whereArray[0].push("(patmas.PAPMI_No = ''".concat(id, "'')"));
			whereArray[1].push(
				"(NOK_PAPMI_ParRef->PAPMI_No = ''".concat(id, "'')")
			);
			break;
		default:
			break;
	}

	// Aggregrate all predicates in whereArray and build SQL WHERE clause from it
	var wherePredicates = [];
	for (var index = 0; index < whereArray.length; index += 1) {
		var element = whereArray[index];
		if (element.length > 0) {
			wherePredicates[index] = element.join(" AND ");
		}
	}

	logger.debug(
		"SQL WHERE clause predicate(s): ".concat(wherePredicates.toString())
	);

	var result = buildResourceQuery(type, wherePredicates);
	while (result.next()) {
		var data = void 0;
		switch ("".concat(type)) {
			case "allergyintolerance":
				data = buildAllergyIntoleranceResource(result);
				break;
			case "condition":
				// data = buildConditionResource(result);
				break;
			case "documentreference":
				// data = buildDocumentReferenceResource(result);
				break;
			case "encounter":
				data = buildEncounterResource(result);
				break;
			case "flag":
				data = buildFlagResource(result);
				break;
			case "medicationstatement":
				data = buildMedicationStatementResource(result);
				break;
			case "patient":
				data = buildPatientResource(result);
				break;
			default:
				break;
		}

		// Hard coded version as we don't keep past versions of records, only one
		var version = "1";
		var lastModified = new Date(getResultSetString(result, "lastUpdated"));

		var response = FhirResponseFactory.getReadResponse(
			JSON.stringify(data),
			version,
			lastModified,
			200,
			"application/fhir+json"
		);

		responseMap.put("response", response);
		return response.getMessage();
	}
	return createOperationOutcome(
		"error",
		"processing",
		"".concat($("fhirType"), " ID ").concat(id, " not found."),
		"STU3",
		404
	);
} catch (error) {
	return createOperationOutcome(
		"error",
		"transient",
		"Error reading resource.",
		"STU3",
		500,
		error
	);
}

/**
 * @author Frazer Smith
 * @description Rewritten example FHIR search destination to be usable for TrakCare calls.
 * Mirth Connect supports ES6 to a degree but not template literals and const/let.
 */

try {
	var type = $('fhirType').toLowerCase();

	// Append URL with forward slash and build Java URL object
	var requestURL = $('url');
	if (!requestURL.endsWith('/')) {
		requestURL += '/';
	}
	var bundle = buildBundleResource(new java.net.URI(requestURL));
	// Turn array into multi-dimensional one to allow for up to four seperate WHERE clauses to be built
	var whereArray = [[], [], [], []];

	var supportedTypeParams = {
		allergyintolerance: [
			'clinical-status',
			'criticality',
			'date',
			'patient',
			'patient.identifier',
			'type'
		],

		condition: [
			'asserted-date',
			'category',
			'clinical-status',
			'patient',
			'patient.identifier'
		],

		encounter: [
			'class',
			'date',
			'patient',
			'patient.identifier',
			'status',
			'type'
		],

		flag: ['date', 'patient', 'patient.identifier', 'status'],
		medicationstatement: [
			'effective',
			'patient',
			'patient.identifier',
			'status'
		],

		patient: [
			'address',
			'address-city',
			'address-postalcode',
			'birthdate',
			'deceased',
			'email',
			'family',
			'gender',
			'given',
			'identifier',
			'name',
			'phone'
		]
	};

	// If any param not supported, reject request
	$('parameters')
		.getKeys()
		.forEach(function (key) {
			if (
				supportedTypeParams[type.toLowerCase()].indexOf(
					''.concat(key.toLowerCase().trim())
				) < 0
			) {
				throw Error(
					''
						.concat(
							key,
							' is not a valid search query parameter for '
						)
						.concat(type.toLowerCase(), 's')
				);
			}
		});

	/**
	 * ================================
	 * AllergyIntolerance search params
	 * ================================
	 */
	if (type == 'allergyintolerance') {
		// GET [baseUrl]/AllergyIntolerance?patient=[id]
		if ($('parameters').contains('patient')) {
			whereArray[0].push(
				"(alle.ALG_PAPMI_ParRef->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			);
		}

		// GET [baseUrl]/AllergyIntolerance?patient.identifier=[system]|[code]
		if ($('parameters').contains('patient.identifier')) {
			if (
				$('parameters').getParameter('patient.identifier').contains('|')
			) {
				var allergyPatIdParam = String(
					$('parameters').getParameter('patient.identifier')
				).split('|');
				if (
					Packages.org.apache.commons.lang3.StringEscapeUtils.unescapeHtml4(
						allergyPatIdParam[0]
					) == 'https://fhir.nhs.uk/Id/nhs-number'
				) {
					whereArray[0].push(
						"(alle.ALG_PAPMI_ParRef->PAPMI_No = (SELECT PAPMI_No FROM PA_PatMas pm WHERE pm.PAPMI_ID = ''".concat(
							allergyPatIdParam[1],
							"'' AND PAPMI_Active IS NULL))"
						)
					);
				}
				if (
					Packages.org.apache.commons.lang3.StringEscapeUtils.unescapeHtml4(
						allergyPatIdParam[0]
					) == 'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				) {
					whereArray[0].push(
						"(alle.ALG_PAPMI_ParRef->PAPMI_No = ''".concat(
							allergyPatIdParam[1],
							"'')"
						)
					);
				}
			}
		}

		// GET [baseUrl]/AllergyIntolerance?patient=[id]&clinical-status=[code]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('clinical-status')
		) {
			// Loop through each clinical-status param and build SQL WHERE clause
			var clinicalStatusArray = $('parameters')
				.getParameterList('clinical-status')
				.toArray();

			if (clinicalStatusArray[0].substring(0, 1) == '[') {
				clinicalStatusArray = JSON.parse(clinicalStatusArray[0]);
			}

			allintClinicalStatusArray = [];

			clinicalStatusArray.forEach(function (clinicalStatusParam) {
				var clinicalStatus = clinicalStatusParam;
				clinicalStatus += '';

				allintClinicalStatusArray.push(
					"(clinicalStatusCode = '".concat(clinicalStatus, "')")
				);
			});

			if (allintClinicalStatusArray.length > 0) {
				whereArray[3].push(
					'('.concat(allintClinicalStatusArray.join(' OR '), ')')
				);
			}
		}

		// GET [baseUrl]/AllergyIntolerance?patient=[id]&criticality=[code]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('criticality')
		) {
			// Loop through each criticality param and build SQL WHERE clause
			var criticalityArray = $('parameters')
				.getParameterList('criticality')
				.toArray();

			if (criticalityArray[0].substring(0, 1) == '[') {
				criticalityArray = JSON.parse(criticalityArray[0]);
			}

			allintCriticalityArray = [];

			criticalityArray.forEach(function (criticalityParam) {
				var criticality = criticalityParam;
				criticality += '';

				allintCriticalityArray.push(
					"(criticalityCode = '".concat(criticality, "')")
				);
			});

			if (allintCriticalityArray.length > 0) {
				whereArray[3].push(
					'('.concat(allintCriticalityArray.join(' OR '), ')')
				);
			}
		}

		// GET [baseUrl]/AllergyIntolerance?patient=[id]&date=[date]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('date')
		) {
			// Loop through each date param and build SQL WHERE clause
			var dateArray = $('parameters').getParameterList('date').toArray();

			if (dateArray[0].substring(0, 1) == '[') {
				dateArray = JSON.parse(dateArray[0]);
			}

			dateArray.forEach(function (dateParam) {
				var date = dateParam;
				date += '';

				var operator = convertFhirParameterOperator(
					date.substring(0, 2)
				);

				if (isNaN(date.substring(0, 2))) {
					date = date.substring(2, date.length);
				}

				whereArray[0].push(
					'(alle.ALG_Date '
						.concat(operator, " ''")
						.concat(date, "'')")
				);
			});
		}

		// GET [baseUrl]/AllergyIntolerance?patient=[id]&type=[code]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('type')
		) {
			// Loop through each criticality param and build SQL WHERE clause
			var typeArray = $('parameters').getParameterList('type').toArray();

			if (typeArray[0].substring(0, 1) == '[') {
				typeArray = JSON.parse(typeArray[0]);
			}

			allintTypeArray = [];

			typeArray.forEach(function (typeParam) {
				var typeP = typeParam;
				typeP += '';

				allintTypeArray.push("(typeCode = '".concat(typeP, "')"));
			});

			if (allintTypeArray.length > 0) {
				whereArray[3].push(
					'('.concat(allintTypeArray.join(' OR '), ')')
				);
			}
		}
	}

	/**
	 * =======================
	 * Condition search params
	 * =======================
	 */
	if (type == 'condition') {
		// GET [baseUrl]/Condition?patient=[id]&asserted-date=[date]
		if ($('parameters').contains('asserted-date')) {
			whereArray[0].push('');
		}

		// GET [baseUrl]/Condition?patient=[id]&category=[code]
		if ($('parameters').contains('category')) {
			whereArray[0].push('');
		}

		// GET [baseUrl]/Condition?patient=[id]&clinical-status=[code]
		if ($('parameters').contains('clinical-status')) {
			whereArray[0].push('');
		}

		/**
		 * GET [baseUrl]/Condition?patient.identifier=[system]|[code]
		 * GET [baseUrl]/Condition?patient=[id]
		 */
		if ($('parameters').contains('patient')) {
			whereArray[0].push('');
		}
	}

	/**
	 * =======================
	 * Encounter search params
	 * =======================
	 */
	if (type == 'encounter') {
		// GET [baseUrl]/Encounter?patient=[id]
		if ($('parameters').contains('patient')) {
			// Build where clause for first query (outpats) in union
			whereArray[0].push(
				"(app.APPT_Adm_DR->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			);

			// Build where clause for second query (inpats, emerg) in union
			whereArray[1].push(
				"(PAADM_PAPMI_DR->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			);

			// Build where clause for subquery for inpat consultants
			whereArray[2].push(
				"(TRANS_ParRef->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			);
		}

		// GET [baseUrl]/Encounter?patient.identifier=[system]|[code]
		if ($('parameters').contains('patient.identifier')) {
			if (
				$('parameters').getParameter('patient.identifier').contains('|')
			) {
				var encounterPatIdParam = String(
					$('parameters').getParameter('patient.identifier')
				).split('|');
				if (
					Packages.org.apache.commons.lang3.StringEscapeUtils.unescapeHtml4(
						encounterPatIdParam[0]
					) == 'https://fhir.nhs.uk/Id/nhs-number'
				) {
					whereArray[0].push(
						"(app.APPT_Adm_DR->PAADM_PAPMI_DR->PAPMI_No = (SELECT PAPMI_No FROM PA_PatMas pm WHERE pm.PAPMI_ID = ''".concat(
							encounterPatIdParam[1],
							"'' AND PAPMI_Active IS NULL))"
						)
					);

					whereArray[1].push(
						"(PAADM_PAPMI_DR->PAPMI_No = (SELECT PAPMI_No FROM PA_PatMas pm WHERE pm.PAPMI_ID = ''".concat(
							encounterPatIdParam[1],
							"'' AND PAPMI_Active IS NULL))"
						)
					);

					whereArray[2].push(
						"(TRANS_ParRef->PAADM_PAPMI_DR->PAPMI_No = (SELECT PAPMI_No FROM PA_PatMas pm WHERE pm.PAPMI_ID = ''".concat(
							encounterPatIdParam[1],
							"'' AND PAPMI_Active IS NULL))"
						)
					);
				}
				if (
					Packages.org.apache.commons.lang3.StringEscapeUtils.unescapeHtml4(
						encounterPatIdParam[0]
					) == 'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				) {
					whereArray[0].push(
						"(app.APPT_Adm_DR->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
							encounterPatIdParam[1],
							"'')"
						)
					);

					whereArray[1].push(
						"(PAADM_PAPMI_DR->PAPMI_No = ''".concat(
							encounterPatIdParam[1],
							"'')"
						)
					);

					whereArray[2].push(
						"(TRANS_ParRef->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
							encounterPatIdParam[1],
							"'')"
						)
					);
				}
			}
		}

		// GET [baseUrl]/Encounter?patient=[id]&date=[date]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('date')
		) {
			// Loop through each date param and build SQL WHERE clause
			var _dateArray = $('parameters').getParameterList('date').toArray();

			if (_dateArray[0].substring(0, 1) == '[') {
				_dateArray = JSON.parse(_dateArray[0]);
			}

			_dateArray.forEach(function (dateParam) {
				var date = dateParam;
				date += '';

				var operator = convertFhirParameterOperator(
					date.substring(0, 2)
				);

				if (isNaN(date.substring(0, 2))) {
					date = date.substring(2, date.length);
				}

				whereArray[3].push(
					"(CONCAT(COALESCE(encounterPeriodStartDate, ''), 'T', COALESCE(encounterPeriodStartTime, '')) "
						.concat(operator, " '")
						.concat(date, "')")
				);
			});
		}

		// GET [baseUrl]/Encounter?patient=[id]&class=[token]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('class')
		) {
			// Loop through each class param and build SQL WHERE clause
			var classArray = $('parameters')
				.getParameterList('class')
				.toArray();

			if (classArray[0].substring(0, 1) == '[') {
				classArray = JSON.parse(classArray[0]);
			}

			encounterClassArray = [];

			classArray.forEach(function (classParam) {
				var classP = classParam;
				classP += '';

				var classCode = {
					inpatient: 'IMP',
					outpatient: 'AMB',
					emergency: 'EMER'
				};

				encounterClassArray.push(
					"(encounterClassCode = '".concat(
						classCode[classP.toLowerCase()],
						"')"
					)
				);
			});

			if (encounterClassArray.length > 0) {
				whereArray[3].push(
					'('.concat(encounterClassArray.join(' OR '), ')')
				);
			}
		}

		// GET [baseUrl]/Encounter?patient=[id]&type=[code]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('type')
		) {
			// Loop through each type param and build SQL WHERE clause
			var _typeArray = $('parameters').getParameterList('type').toArray();

			if (_typeArray[0].substring(0, 1) == '[') {
				_typeArray = JSON.parse(_typeArray[0]);
			}

			encounterTypeArray = [];

			_typeArray.forEach(function (typeParam) {
				var typeP = typeParam;
				typeP += '';

				// Build where clause for fourth query
				encounterTypeArray.push(
					"(encounterTypeCode = '".concat(typeP, "')")
				);
			});

			if (encounterTypeArray.length > 0) {
				whereArray[3].push(
					'('.concat(encounterTypeArray.join(' OR '), ')')
				);
			}
		}

		// GET [baseUrl]/Encounter?patient=[id]&status=[token]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('status')
		) {
			// Loop through each status param and build SQL WHERE clause
			var statusArray = $('parameters')
				.getParameterList('status')
				.toArray();

			if (statusArray[0].substring(0, 1) == '[') {
				statusArray = JSON.parse(statusArray[0]);
			}

			encounterStatusArray = [];

			statusArray.forEach(function (statusParam) {
				var status = statusParam;
				status += '';

				// Build where clause for fourth query
				encounterStatusArray.push(
					"(encounterStatusMapped = '".concat(status, "')")
				);
			});

			if (encounterStatusArray.length > 0) {
				whereArray[3].push(
					'('.concat(encounterStatusArray.join(' OR '), ')')
				);
			}
		}
	}

	/**
	 * =======================
	 * Flag search params
	 * =======================
	 */
	if (type == 'flag') {
		// GET [baseUrl]/Flag?patient=[id]
		if ($('parameters').contains('patient')) {
			whereArray[0].push(
				"(alert.ALM_PAPMI_ParRef->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			);
		}

		// GET [baseUrl]/Flag?patient.identifier=[system]|[code]
		if ($('parameters').contains('patient.identifier')) {
			if (
				$('parameters').getParameter('patient.identifier').contains('|')
			) {
				var flagPatIdParam = String(
					$('parameters').getParameter('patient.identifier')
				).split('|');
				if (
					Packages.org.apache.commons.lang3.StringEscapeUtils.unescapeHtml4(
						flagPatIdParam[0]
					) == 'https://fhir.nhs.uk/Id/nhs-number'
				) {
					whereArray[0].push(
						"(alert.ALM_PAPMI_ParRef->PAPMI_No = (SELECT PAPMI_No FROM PA_PatMas pm WHERE pm.PAPMI_ID = ''".concat(
							flagPatIdParam[1],
							"'' AND PAPMI_Active IS NULL))"
						)
					);
				}
				if (
					Packages.org.apache.commons.lang3.StringEscapeUtils.unescapeHtml4(
						flagPatIdParam[0]
					) == 'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				) {
					whereArray[0].push(
						"(alert.ALM_PAPMI_ParRef->PAPMI_No = ''".concat(
							flagPatIdParam[1],
							"'')"
						)
					);
				}
			}
		}

		// GET [baseUrl]/Flag?patient=[id]&date=[date]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('date')
		) {
			// Loop through each date param and build SQL WHERE clause
			var _dateArray2 = $('parameters')
				.getParameterList('date')
				.toArray();

			if (_dateArray2[0].substring(0, 1) == '[') {
				_dateArray2 = JSON.parse(_dateArray2[0]);
			}

			_dateArray2.forEach(function (dateParam) {
				var date = dateParam;
				date += '';

				var operator = convertFhirParameterOperator(
					date.substring(0, 2)
				);

				if (isNaN(date.substring(0, 2))) {
					date = date.substring(2, date.length);
				}

				whereArray[1].push(
					'(periodStart '.concat(operator, " '").concat(date, "')")
				);
			});
		}

		// GET [baseUrl]/Flag?patient=[id]&status=[code]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('status')
		) {
			whereArray[1].push(
				"(flagStatusCode = ''".concat(
					$('parameters').getParameter('status'),
					"'')"
				)
			);
		}
	}

	/**
	 * =================================
	 * MedicationStatement search params
	 * =================================
	 */
	if (type == 'medicationstatement') {
		// GET [baseUrl]/MedicationStatement?patient=[id]&effective=[date]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('effective')
		) {
			// Loop through each effective param and build SQL WHERE clause
			var _dateArray3 = $('parameters')
				.getParameterList('effective')
				.toArray();

			if (_dateArray3[0].substring(0, 1) == '[') {
				_dateArray3 = JSON.parse(_dateArray3[0]);
			}

			_dateArray3.forEach(function (dateParam) {
				var date = dateParam;
				date += '';

				var operator = convertFhirParameterOperator(
					date.substring(0, 2)
				);

				if (isNaN(date.substring(0, 2))) {
					date = date.substring(2, date.length);
				}

				whereArray[1].push(
					'(medstatEffectiveStart '
						.concat(operator, " '")
						.concat(date, "')")
				);
			});
		}

		// GET [baseUrl]/MedicationStatement?patient=[id]
		if ($('parameters').contains('patient')) {
			whereArray[0].push(
				"(oi.OEORI_OEORD_ParRef->OEORD_Adm_DR->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			);
		}

		// GET [baseUrl]/MedicationStatement?patient.identifier=[system]|[code]
		if ($('parameters').contains('patient.identifier')) {
			if (
				$('parameters').getParameter('patient.identifier').contains('|')
			) {
				var medStatPatIdParam = String(
					$('parameters').getParameter('patient.identifier')
				).split('|');
				if (
					Packages.org.apache.commons.lang3.StringEscapeUtils.unescapeHtml4(
						medStatPatIdParam[0]
					) == 'https://fhir.nhs.uk/Id/nhs-number'
				) {
					whereArray[0].push(
						"(oi.OEORI_OEORD_ParRef->OEORD_Adm_DR->PAADM_PAPMI_DR->PAPMI_No = (SELECT PAPMI_No FROM PA_PatMas pm WHERE pm.PAPMI_ID = ''".concat(
							medStatPatIdParam[1],
							"'' AND PAPMI_Active IS NULL))"
						)
					);
				}
				if (
					Packages.org.apache.commons.lang3.StringEscapeUtils.unescapeHtml4(
						medStatPatIdParam[0]
					) == 'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				) {
					whereArray[0].push(
						"(oi.OEORI_OEORD_ParRef->OEORD_Adm_DR->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
							medStatPatIdParam[1],
							"'')"
						)
					);
				}
			}
		}

		// GET [baseUrl]/MedicationStatement?patient=[id]&status=[code]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('status')
		) {
			whereArray[1].push(
				"(medstatStatusCode = '".concat(
					$('parameters').getParameter('status'),
					"')"
				)
			);
		}
	}

	/**
	 * =====================
	 * Patient search params
	 * =====================
	 */
	if (type == 'patient') {
		// GET [baseUrl]/Patient?address=[address]
		if ($('parameters').contains('address')) {
			var address = $('parameters').getParameter('address');
			whereArray[0].push(
				"(patmas.PAPMI_PAPER_DR->PAPER_StName = ''"
					.concat(
						address,
						"'' OR patmas.PAPMI_PAPER_DR->PAPER_ForeignAddress = ''"
					)
					.concat(
						address,
						"'' OR patmas.PAPMI_PAPER_DR->PAPER_CT_Province_DR->PROV_Desc = ''"
					)
					.concat(address, "'')")
			);
		}

		// GET [baseUrl]/Patient?address-city=[address-city]
		if ($('parameters').contains('address-city')) {
			whereArray[0].push(
				"(patmas.PAPMI_PAPER_DR->PAPER_CityCode_DR->CTCIT_Desc = ''".concat(
					$('parameters').getParameter('address-city'),
					"'')"
				)
			);
		}

		// GET [baseUrl]/Patient?address-postalcode=[address-postalcode]
		if ($('parameters').contains('address-postalcode')) {
			whereArray[0].push(
				"(patmas.PAPMI_PAPER_DR->PAPER_Zip_DR->CTZIP_Code = ''".concat(
					$('parameters').getParameter('address-postalcode'),
					"'')"
				)
			);
		}

		// GET [baseUrl]/Patient?birthdate=[date]
		if ($('parameters').contains('birthdate')) {
			whereArray[0].push(
				"(patmas.PAPMI_DOB = ''".concat(
					$('parameters').getParameter('birthdate'),
					"'')"
				)
			);
		}

		// GET [baseUrl]/Patient?deceased=[deceased]
		if ($('parameters').contains('deceased')) {
			var deceased = $('parameters').getParameter('deceased');

			switch (deceased) {
				case 'false':
					whereArray[0].push(
						"(patmas.PAPMI_PAPER_DR->PAPER_Deceased = ''N'')"
					);

					break;
				case 'true':
					whereArray[0].push(
						"(patmas.PAPMI_PAPER_DR->PAPER_Deceased = ''Y'')"
					);

					break;
				default:
					break;
			}
		}

		// GET [baseUrl]/Patient?email=[email]
		if ($('parameters').contains('email')) {
			whereArray[0].push(
				"(patmas.PAPMI_PAPER_DR->PAPER_Email = ''".concat(
					$('parameters').getParameter('email'),
					"'')"
				)
			);
		}

		// GET [baseUrl]/Patient?family=[family]
		if ($('parameters').contains('family')) {
			whereArray[0].push(
				"(patmas.PAPMI_PAPER_DR->PAPER_Name = ''".concat(
					$('parameters').getParameter('family'),
					"'')"
				)
			);
		}

		// GET [baseUrl]/Patient?gender=[code]
		if ($('parameters').contains('gender')) {
			whereArray[0].push(
				"(patmas.PAPMI_PAPER_DR->PAPER_Sex_DR->CTSEX_Desc = ''".concat(
					$('parameters').getParameter('gender'),
					"'')"
				)
			);
		}

		// GET [baseUrl]/Patient?given=[given]
		if ($('parameters').contains('given')) {
			whereArray[0].push(
				"(patmas.PAPMI_PAPER_DR->PAPER_Name2 = ''".concat(
					$('parameters').getParameter('given'),
					"'')"
				)
			);
		}

		/**
		 * GET [baseUrl]/Patient?identifier=[system]|[code]
		 * GET [baseUrl]/Patient?identifier=[code]
		 */
		if ($('parameters').contains('identifier')) {
			if ($('parameters').getParameter('identifier').contains('|')) {
				var identifierParam = String(
					$('parameters').getParameter('identifier')
				).split('|');
				if (
					Packages.org.apache.commons.lang3.StringEscapeUtils.unescapeHtml4(
						identifierParam[0]
					) == 'https://fhir.nhs.uk/Id/nhs-number'
				) {
					whereArray[0].push(
						"(patmas.PAPMI_ID = ''".concat(
							identifierParam[1],
							"'')"
						)
					);

					whereArray[1].push(
						"(NOK_PAPMI_ParRef->PAPMI_No = (SELECT PAPMI_No FROM PA_PatMas pm WHERE pm.PAPMI_ID = ''".concat(
							identifierParam[1],
							"'' AND PAPMI_Active IS NULL))"
						)
					);
				}
				if (
					Packages.org.apache.commons.lang3.StringEscapeUtils.unescapeHtml4(
						identifierParam[0]
					) == 'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				) {
					whereArray[0].push(
						"(patmas.PAPMI_No = ''".concat(
							identifierParam[1],
							"'')"
						)
					);

					whereArray[1].push(
						"(NOK_PAPMI_ParRef->PAPMI_No = ''".concat(
							identifierParam[1],
							"'')"
						)
					);
				}
			} else {
				whereArray[0].push(
					"(patmas.PAPMI_No = ''".concat(
						$('parameters').getParameter('identifier'),
						"'')"
					)
				);

				whereArray[1].push(
					"(NOK_PAPMI_ParRef->PAPMI_No = ''".concat(
						$('parameters').getParameter('identifier'),
						"'')"
					)
				);
			}
		}

		// GET [baseUrl]/Patient?name=[name]
		if ($('parameters').contains('name')) {
			var name = $('parameters').getParameter('name');
			whereArray[0].push(
				"(patmas.PAPMI_PAPER_DR->PAPER_Name = ''"
					.concat(
						name,
						"'' OR patmas.PAPMI_PAPER_DR->PAPER_Name2 = ''"
					)
					.concat(name, "'')")
			);
		}

		// GET [baseUrl]/Patient?phone=[phone]
		if ($('parameters').contains('phone')) {
			var phone = $('parameters').getParameter('phone');
			whereArray[0].push(
				"(patmas.PAPMI_PAPER_DR->PAPER_TelH = ''"
					.concat(
						phone,
						"'' OR patmas.PAPMI_PAPER_DR->PAPER_TelO = ''"
					)
					.concat(
						phone,
						"'' OR patmas.PAPMI_PAPER_DR->PAPER_MobPhone = ''"
					)
					.concat(phone, "'')")
			);
		}
	}

	// Aggregrate all predicates in whereArray and build SQL WHERE clause from it
	var wherePredicates = [];
	for (var index = 0; index < whereArray.length; index += 1) {
		var element = whereArray[index];
		if (element.length > 0) {
			wherePredicates[index] = element.join(' AND ');
		}
	}

	if (wherePredicates.length == 0) {
		throw Error('Error searching resources.');
	}

	logger.debug(
		'SQL WHERE clause predicate(s): '.concat(wherePredicates.toString())
	);

	var result = buildResourceQuery(type, wherePredicates);
	while (result.next()) {
		var data = void 0;
		switch (''.concat(type)) {
			case 'allergyintolerance':
				data = buildAllergyIntoleranceResource(result);
				break;
			case 'condition':
				// data = buildConditionResource(result);
				break;
			case 'documentreference':
				// data = buildDocumentReferenceResource(result);
				break;
			case 'encounter':
				data = buildEncounterResource(result);
				break;
			case 'flag':
				data = buildFlagResource(result);
				break;
			case 'medicationstatement':
				data = buildMedicationStatementResource(result);
				break;
			case 'patient':
				data = buildPatientResource(result);
				break;
			default:
				break;
		}

		// Add returned FHIR resources to bundle resource
		var resourceOuter = {};
		resourceOuter.resource = data;
		resourceOuter.fullUrl = ''
			.concat($cfg('apiUrl') + $('contextPath'), '/')
			.concat(data.id);

		bundle.entry.push(resourceOuter);
	}

	bundle.total = bundle.entry.length;
	bundle.link[0].url = $cfg('apiUrl') + $('uri');
	logger.debug(JSON.stringify(bundle));

	var response = FhirResponseFactory.getSearchResponse(
		JSON.stringify(bundle),
		200,
		'application/fhir+json'
	);

	responseMap.put('response', response);
	return response.getMessage();
} catch (error) {
	return createOperationOutcome(
		'error',
		'transient',
		'Error searching resources.',
		'STU3',
		500,
		error
	);
}

channel.postprocessingScript:
// This script executes once after a message has been processed
// Responses returned from here will be stored as "Postprocessor" in the response map

// Return an OperationOutcome if no destinations or anything else already handled the response
if (!responseMap.containsKey('response')) {
	if (sourceMap.get('fhirInteraction') == 'operation') {
		createOperationOutcome('error', 'processing', 'Invalid or unsupported operation: ' + sourceMap.get('fhirOperationName'), 'STU3');
	} else {
		createOperationOutcome('error', 'processing', 'Invalid request URI.', 'STU3');
	}
}
return;

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;

channel.undeployScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data

// Close the database connection
var dbConn = $gc('dbConn');
if (dbConn != null) {
	try { dbConn.close() } catch(e) {}
	globalChannelMap.remove('dbConn');
}

return;

properties.code:
/**
	Executes a prepared query on the FHIR database and returns a CachedRowSet.

	@param {String} expression - The prepared statement to be executed.
	@param {Array} parameters - The parameters for the prepared statement.
	@param {DatabaseConnection} dbConn - Optionally provide a specific connection object.
	@return {CachedRowSet} The result of the query, as a CachedRowSet.
*/
function executeCachedQuery(expression, parameters, dbConn) {
	return executeOperation('executeCachedQuery', expression, parameters, dbConn);
}

/**
	Retrieves the FHIR database connection from the global channel map, creating a new one if necessary.

	@param {Boolean} recreate - If true, the connection will always be closed and created anew.
	@return {DatabaseConnection} The database connection object.
*/
function getFHIRDBConnection(recreate) {
	var dbConn = $gc('dbConn');

	if (!dbConn || recreate) {
		if (dbConn) {
			try {
				dbConn.close();
			} catch (e) {}
		}
		dbConn = createFHIRDBConnection();
		$gc('dbConn', dbConn);
	}

	return dbConn;
}

function createFHIRDBConnection() {
	return DatabaseConnectionFactory.createDatabaseConnection($('fhirDBDriver'), $('fhirDBUrl'), $('fhirDBUsername'), $('fhirDBPassword'));
}

function executeOperation(operation, expression, parameters, dbConn) {
	var createDbConn = typeof dbConn == 'undefined';
	if (createDbConn) {
		dbConn = getFHIRDBConnection();
	}
	var attempts = 0;
	var maxAttempts = NumberUtils.toInt($('fhirDBMaxRetries') + '', 0) + 1;

	while (attempts < maxAttempts) {
		attempts++;

		try {
			if (parameters) {
				for (var i in parameters) {
					var param = parameters[i];
					if (param instanceof Number) {
						parameters[i] = new java.lang.Integer(param);
					} else if (param instanceof java.util.Date) {
						parameters[i] = convertDateParameter(param);
					}
				}

				return dbConn[operation](expression, java.util.Arrays.asList(parameters));
			} else {
				return dbConn[operation](expression);
			}
		} catch (e) {
			logger.info('Error executing statement, checking if valid...');
			var throwException = false;

			try {
				dbConn.executeCachedQuery('SELECT 1');
			} catch (e2) {
				if (createDbConn) {
					logger.info('Connection invalid, recreating...');

					try {
						dbConn = getFHIRDBConnection(true);
					} catch (e3) {
						throwException = true;
					}
				} else {
					throwException = true;
				}
			}

			if (attempts >= maxAttempts) {
				throwException = true;
			}

			if (throwException) {
				var errorMessage = 'Unable to execute statement.\n';
				errorMessage += 'Expression: ' + expression + '\n';
				if (parameters) {
					errorMessage += 'Parameters: ' + java.util.Arrays.asList(parameters).toString();
				}
				logger.error(errorMessage, e.javaException);
				throw e;
			}

			java.lang.Thread.sleep(1000);
		}
	}
}

function convertDateParameter(date) {
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		return new java.sql.Timestamp(date.getTime());
	} else if (dbType == 'sqlserver') {
		return convertDate(date, 'yyyy-MM-dd HH:mm:ss.SSSZZ');
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Converts ResultSet into JSON object for single result.
	This makes it easier to parse for FHIR resources builder code templates.

	@author Frazer Smith
	@param {object} resultSet - The ResultSet object to retrieve from.
	@returns {object} The ResultSet as a JSON object.
 */
function getResultSet(resultSet) {
	var rsmd = resultSet.getMetaData();
	var numColumns = rsmd.getColumnCount();
	var obj = {};

	for (var index = 1; index < numColumns + 1; index++) {
		var columnName = rsmd.getColumnName(index);

		switch (rsmd.getColumnType(index)) {
			case java.sql.Types.ARRAY:
				obj["".concat(columnName)] = resultSet.getArray(columnName);
				break;
			case java.sql.Types.BIGINT:
				obj["".concat(columnName)] = resultSet.getInt(columnName);
				break;
			case java.sql.Types.BLOB:
				obj["".concat(columnName)] = getResultSetString(
					resultSet,
					columnName
				);

				break;
			case java.sql.Types.BOOLEAN:
				obj["".concat(columnName)] = resultSet.getBoolean(columnName);
				break;
			case java.sql.Types.CHAR:
				obj["".concat(columnName)] = resultSet.getString(columnName);
				break;
			case java.sql.Types.CLOB:
				obj["".concat(columnName)] = getResultSetString(
					resultSet,
					columnName
				);

				break;
			case java.sql.Types.DATE:
				obj["".concat(columnName)] = resultSet.getDate(columnName);
				break;
			case java.sql.Types.DOUBLE:
				obj["".concat(columnName)] = resultSet.getDouble(columnName);
				break;
			case java.sql.Types.FLOAT:
				obj["".concat(columnName)] = resultSet.getFloat(columnName);
				break;
			case java.sql.Types.INTEGER:
				obj["".concat(columnName)] = resultSet.getInt(columnName);
				break;
			case java.sql.Types.NCHAR:
				obj["".concat(columnName)] = resultSet.getNString(columnName);
				break;
			case java.sql.Types.NVARCHAR:
				obj["".concat(columnName)] = resultSet.getNString(columnName);
				break;
			case java.sql.Types.SMALLINT:
				obj["".concat(columnName)] = resultSet.getInt(columnName);
				break;
			case java.sql.Types.TIMESTAMP:
				obj["".concat(columnName)] = resultSet.getTimestamp(columnName);
				break;
			case java.sql.Types.TINYINT:
				obj["".concat(columnName)] = resultSet.getInt(columnName);
				break;
			case java.sql.Types.VARCHAR:
				obj["".concat(columnName)] = resultSet.getString(columnName);
				break;
			default:
				obj["".concat(columnName)] = resultSet.getObject(columnName);
				break;
		}
	}

	return obj;
}

/**
	Retrives the string value for a particular column from a ResultSet. Handles instances of
	java.sql.Clob and java.sql.Blob as well (with the JVM default charset).

	@param {ResultSet} resultSet - The ResultSet object to retrieve from.
	@param {String} columnName - The name or alias of the column to retrieve.
	@return {String} The string value.
*/
function getResultSetString(resultSet, columnName) {
	var obj = resultSet.getObject(columnName);
	if (obj != null) {
		if (obj instanceof java.sql.Clob) {
			obj = org.apache.commons.io.IOUtils.toString(obj.getCharacterStream());
		} else if (obj instanceof java.sql.Blob) {
			obj = org.apache.commons.io.IOUtils.toString(obj.getBinaryStream());
		} else {
			obj = java.lang.String.valueOf(obj);
		}
	}
	return obj;
}

/**
	These functions are used with the search interaction.
*/
function getFhirSearchBaseQuery(usingLimit) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "SELECT name, id, version, data::TEXT, mimetype, request_method, request_url FROM resource r1 WHERE version = (SELECT MAX(version) FROM resource r2 WHERE r2.name = r1.name AND r2.id = r1.id) AND (deleted IS NULL OR deleted = FALSE)";
	} else if (dbType == 'sqlserver') {
		var query = "SELECT name, id, version, CAST(data AS NVARCHAR(MAX)) AS data, mimetype, request_method, request_url";
		if (usingLimit) {
			query += ", ROW_NUMBER() OVER (" + getOrderClause() + ") AS ROWNUM";
		}
		return query + " FROM resource r1 WHERE version = (SELECT MAX(version) FROM resource r2 WHERE r2.name = r1.name AND r2.id = r1.id) AND (deleted IS NULL OR deleted = 0)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirSearchContentWherePart() {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "data::TEXT ILIKE ?";
	} else if (dbType == 'sqlserver') {
		return "UPPER(CAST(data AS NVARCHAR(MAX))) LIKE UPPER(?)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirSearchLastUpdatedWherePart(lastUpdatedOperator) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "last_modified " + convertFhirParameterOperator(lastUpdatedOperator) + " ?::TIMESTAMP WITH TIME ZONE";
	} else if (dbType == 'sqlserver') {
		return "last_modified " + convertFhirParameterOperator(lastUpdatedOperator) + " CAST(? AS DATETIMEOFFSET)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function convertFhirParameterOperator(operator) {
	switch ((operator + '').toLowerCase()) {
		case 'eq': return '=';
		case 'ne': return '!=';
		case 'gt': return '>';
		case 'lt': return '<';
		case 'ge': return '>=';
		case 'le': return '<=';
		case 'sa': return '>';
		case 'eb': return '<';
		case 'ap': return '=';
		default: return '=';
	}
}

function convertXPathNodeSingleton(node) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return node;
	} else if (dbType == 'sqlserver') {
		return node.split('/').join('[1]/');
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function xpathLowerCase(value) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "translate(" + value + ", 'ABCDEFGHIJKLMNOPQRSTUVWXYZ', 'abcdefghijklmnopqrstuvwxyz')";
	} else if (dbType == 'sqlserver') {
		return "fn:lower-case(" + value + ")";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirSearchXPathWherePart() {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "XPATH_EXISTS(?, data)";
	} else if (dbType == 'sqlserver') {
		return "data.exist(?) = 1";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function addXPath(xpath, whereParts, params) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		whereParts.push(getFhirSearchXPathWherePart());
		params.push(xpath);
	} else if (dbType == 'sqlserver') {
		whereParts.push(getFhirSearchXPathWherePart().replace('?', "'" + xpath.replace(/'/g, "''") + "'"));
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function addOrder(query, usingLimit) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return query + " " + getOrderClause();
	} else if (dbType == 'sqlserver') {
		if (usingLimit) {
			return query;
		} else {
			return query + " " + getOrderClause();
		}
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getOrderClause() {
	return "ORDER BY name ASC, id ASC, version DESC";
}

function addLimit(query, limit) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return query + " LIMIT " + limit;
	} else if (dbType == 'sqlserver') {
		return "SELECT A.* FROM (" + query + ") AS A WHERE A.ROWNUM <= " + limit;
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Returns a ResultSet for a specific resource instance at a specific version.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@param {Number} versionId - The version ID of the resource.
	@return {ResultSet} The ResultSet.
*/
function getResourceVersion(type, id, versionId) {
	var params = [type, id, parseInt(versionId, 10)];
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		return executeCachedQuery("SELECT data::TEXT, mimetype, last_modified, deleted FROM resource WHERE name = ? AND id = ? AND version = ?", params);
	} else if (dbType == 'sqlserver') {
		return executeCachedQuery("SELECT CAST(data AS NVARCHAR(MAX)) AS data, mimetype, last_modified, deleted FROM resource WHERE name = ? AND id = ? AND version = ?", params);
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Supports parsing date strings in a variety of formats and outputting the date in a specific format.
*/
var patterns = [
	"yyyy-MM-dd HH:mm:ss.SSSSSSS ZZ",
	"yyyy-MM-dd'T'HH:mm:ss.SSSZ",
	"yyyy-MM-dd'T'HH:mm:ssZ",
	"yyyy-MM-dd'T'HH:mm:ss",
	"yyyy-MM-dd'T'HH:mm",
	'yyyy-MM-dd',
	'yyyy-MM-dd HH:mm:ss:SSS',
	'yyyy-MM-dd HH:mm:ss.SSS',
	'yyyy-MM-dd HH:mm:ss',
	'yyyy-MM-dd HH:mm',
	'EEE MMM dd HH:mm:ss:SSS zzz yyyy',
	'EEE MMM dd HH:mm:ss.SSS zzz yyyy',
	'EEE MMM dd HH:mm:ss zzz yyyy',
	'EEE MMM dd zzz yyyy',
	'dd-MMM-yyyy HH:mm:ss:SSS',
	'dd-MMM-yyyy HH:mm:ss.SSS',
	'dd-MMM-yyyy HH:mm:ss',
	'yyyy MM dd',
	'yyyy.MM.dd',
	'MM-dd-yyyy',
	'MM dd yyyy',
	'MM.dd.yyyy',
	'HH:mm:ss:SSS',
	'HH:mm:ss.SSS',
	'HH:mm:ss',
	'yyyyMMddHHmmssSSS',
	'yyyyMMddHHmmss',
	'yyyyMMddHHmm',
	'hh:mm aa'];

var formatters = [];
for each (pattern in patterns) {
	formatters.push(org.joda.time.format.DateTimeFormat.forPattern(pattern));
}

function getMillis(date) {
	var instant = 0;
	if (typeof date == 'number' || date instanceof java.lang.Number)
		instant = new Number(date);
	else if (date instanceof Date || date instanceof java.util.Date)
		instant = date.getTime();
	else if (date instanceof org.joda.time.ReadableInstant)
		instant = date.getMillis();
	else {
		for each (formatter in formatters) {
			try {
				instant = formatter.parseMillis(new String(date));
				break;
			} catch(e) {}
		}
	}

	return instant;
}

function getDate(date) {
	return new java.util.Date(getMillis(date));
}

function convertDate(date, outpattern) {
	return org.joda.time.format.DateTimeFormat.forPattern(outpattern).print(getMillis(date));
}

/**
	Creates a FHIR OperationOutcome resource and adds it to the response map with the key "response".

	@param {String} severity - Indicates whether the issue indicates a variation from successful
		processing. Values: fatal | error | warning | information
	@param {String} code - Describes the type of the issue. The system that creates an
		OperationOutcome SHALL choose the most applicable code from the IssueType value set, and may
		additional provide its own code for the error in the details element. Values: invalid | security |
		processing | transient | informational
	@param {String} details - Additional details about the error. This may be a text description of
		the error, or a system code that identifies the error.
	@param {String} fhirVersion - The FHIR version of the OperationOutcome resource. Values: DSTU2 | DSTU2_1 | 
		   DSTU_HL7ORG | STU3 | R4 | R5
	@param {int} httpStatusCode - The HTTP status code to send back with the response. Defaults to 400
		if not specified.
	@param {Error} e - If included, this will be used to build up the response status message and
		error.
	@param {Boolean} isFormatXML - If true, the resource will be formatted in XML. Otherwise, it will
		be formatted in JSON. If not specified, it will attempt to find the _format parameter in the source
		map, or return JSON.
	@return {Response} The created Response object.
*/
function createOperationOutcome(severity, code, details, fhirVersion, httpStatusCode, e, isFormatXML) {
	if (!httpStatusCode) {
		httpStatusCode = 400;
	}

	if (!isFormatXML && $s('parameters') && $s('parameters').contains('_format')) {
		isFormatXML = !FhirUtil.isJSON($s('parameters').getParameter('_format'));
	}

	var outcome = FhirUtil.createOperationOutcome(severity, code, details, fhirVersion);
	var message = isFormatXML ? FhirUtil.toXML(outcome, fhirVersion) : FhirUtil.toJSON(outcome, fhirVersion);
	responseMap.put('response', FhirResponseFactory.getResponse(message, httpStatusCode, isFormatXML ? FhirUtil.getMIMETypeXML() : FhirUtil.getMIMETypeJSON()));
	var response = new Response(message);
	response.setStatusMessage(severity.toUpperCase() + ' OperationOutcome created with status ' + httpStatusCode + '.');

	if (httpStatusCode >= 400) {
		response.setStatus(Status.ERROR);

		if (e) {
			var customMessage = 'Channel ' + channelName + ': ';
			if (e.rhinoException) {
				customMessage += 'Error on line ' + e.rhinoException.lineNumber() + ': ';
			}
			customMessage += e.toString();

			var errorContent = com.mirth.connect.util.ErrorMessageBuilder.buildErrorMessage('FHIR Listener', customMessage, e.javaException || null);
			if (e.javaException) {
				logger.error(errorContent, e.javaException);	
			} else {
				logger.error(errorContent);
			}
			response.setError(errorContent);
		}
	}

	return response;
}

/**
	Imports HAPI FHIR packages commonly used in the FHIR Listener channel.
*/
importPackage(Packages.ca.uhn.fhir.model.api);
importPackage(Packages.ca.uhn.fhir.model.base.resource);
importPackage(Packages.ca.uhn.fhir.model.primitive);
importPackage(Packages.org.hl7.fhir.dstu3.model);
importPackage(org.apache.commons.lang3);
importPackage(org.apache.commons.lang3.math);

/**
	Sets the resource ID, version ID, and last updated date/time.

	@param {IBaseResource} resource - The FHIR resource object.
	@param {String} id - The logical ID of the resource.
	@param {Number} versionId - The version ID of the resource.
	@return {Date} The last updated date/time.
*/
function updateResourceMeta(resource, id, versionId) {
	resource.setId(id);
	resource.getMeta().setVersionId(new String(versionId));
	var lastUpdated = new java.util.Date();
	resource.getMeta().getLastUpdatedElement().setTimeZone(java.util.TimeZone.getDefault());
	resource.getMeta().setLastUpdated(lastUpdated);
	return lastUpdated;
}

/**
	Builds AllergyIntolerance FHIR Resource that adheres to its Care-Connect profile,
	see https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-AllergyIntolerance-1 for more info.

	@author Frazer Smith
	@param {object} data - Java RowSet object.
	@returns {object} AllergyIntolerance FHIR resource.
 */
function buildAllergyIntoleranceResource(data) {
	var result = getResultSet(data);

	/**
	 * Hard-coding meta profile and resourceType into resource as this should not
	 * be changed for this resource, ever.
	 */
	var resource = {
		meta: {
			profile: [
				"https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-AllergyIntolerance-1",
			],
		},

		resourceType: "AllergyIntolerance",
	};

	// Add meta data
	if (
		result.lastUpdated != undefined &&
		result.lastUpdated.substring(0, 1) != "T" &&
		result.lastUpdated.substring(0, 4) != "1900"
	) {
		resource.meta.lastUpdated = result.lastUpdated;
	}

	/**
	 * Add SIDeR specific tags
	 * Set tag to 'Do not Display' for all resources; decided by Paul Foster CCIO on 2020-11-19
	 * due to low number of allergies recorded in TrakCare PAS
	 */
	resource.meta.tag = [
		{
			system:
				"https://fhir.blackpear.com/ui/shared-care-record-visibility",
			code: "none",
			display: "Do not Display",
		},
	];

	resource.id = newStringOrUndefined(result.id);
	resource.assertedDate = newStringOrUndefined(result.assertedDate);

	// Very unlikely that an allergy record will have multiple components like this
	// but better to be safe than sorry
	var allergyResult = [];
	allergyResult.push(newStringOrUndefined(result.allergyGroupDesc));
	allergyResult.push(newStringOrUndefined(result.allergyCodingDesc));
	allergyResult.push(newStringOrUndefined(result.allergyDrugDesc));
	allergyResult.push(newStringOrUndefined(result.allergyDrugGenericDesc));
	allergyResult.push(newStringOrUndefined(result.allergyDrugCategoryDesc));
	allergyResult.push(newStringOrUndefined(result.allergyDrugFormDesc));
	allergyResult.push(newStringOrUndefined(result.allergyDrugIngredientDesc));
	allergyResult.push(newStringOrUndefined(result.allergyComment));
	allergyResult = allergyResult.filter(function (element) {
		return element != null;
	});
	if (allergyResult.length > 0) {
		resource.code = {
			text: allergyResult.join("; "),
		};
	}

	resource.patient = {
		reference: ""
			.concat($cfg("apiUrl"), "/STU3/Patient/")
			.concat(result.patientReference),
	};

	resource.clinicalStatus = newStringOrUndefined(result.clinicalStatusCode);
	resource.verificationStatus = newStringOrUndefined(
		result.verificationStatusCode
	);

	resource.type = newStringOrUndefined(result.typeCode);
	resource.criticality = newStringOrUndefined(result.criticalityCode);

	return resource;
}

/**
	Builds Bundle FHIR Resource that adheres to the HL7 spec,
	see https://www.hl7.org/fhir/STU3/bundle.html for more info.

	@author Frazer Smith
	@param {string} requestUrl - URL that GET request was made to.
	@returns {object} Bundle FHIR resource.
 */
function buildBundleResource(requestUrl) {
	var resource = {
		resourceType: newStringOrUndefined("Bundle"),
		type: newStringOrUndefined("searchset"),
		total: 0,
		link: [],
		entry: [],
	};

	// Add link URL
	if (requestUrl) {
		var linkEntry = {
			relation: newStringOrUndefined("self"),
			url: newStringOrUndefined(requestUrl),
		};

		resource.link.push(linkEntry);
	}

	return resource;
}

/**
	Builds Encounter FHIR Resource that adheres to its Care-Connect profile,
	see https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Encounter-1 for more info.

	@author Frazer Smith
	@param {object} data - Java RowSet object.
	@returns {object} Encounter FHIR resource.
 */
function buildEncounterResource(data) {
	var result = getResultSet(data);
	/**
	 * Hard-coding meta profile and resourceType into resource as this should not
	 * be changed for this resource, ever.
	 */
	var resource = {
		meta: {
			profile: [
				"https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Encounter-1",
			],
		},

		resourceType: "Encounter",
	};

	resource.id = newStringOrUndefined(result.encounterIdentifier);
	resource.status = newStringOrUndefined(result.encounterStatusMapped);

	// Add meta data
	if (
		result.lastUpdated != undefined &&
		result.lastUpdated.substring(0, 1) != "T" &&
		result.lastUpdated.substring(0, 4) != "1900"
	) {
		resource.meta.lastUpdated = result.lastUpdated;
	}

	if (result.encounterClassDesc != undefined) {
		resource["class"] = {
			system: "https://hl7.org/fhir/v3/ActEncounterCode",
			code: newStringOrUndefined(result.encounterClassCode),
			display: newStringOrUndefined(result.encounterClassDesc),
		};
	}

	/**
	 * Add SIDeR specific tags
	 * Only display in summary and detail view if within last 30 days,
	 * don't display any planned/future encounters as they're out of scope
	 */
	if (
		result.encounterPeriodStart != undefined &&
		result.encounterPeriodStart.substring(0, 1) != "T" &&
		result.encounterPeriodStart.substring(0, 4) != "1900" &&
		Math.ceil(
			(new Date(result.encounterPeriodStart) - new Date()) /
				(24 * 60 * 60 * 1000)
		) >= -30
	) {
		resource.meta.tag = [
			{
				system:
					"https://fhir.blackpear.com/ui/shared-care-record-visibility",
				code: "summary",
				display: "Display in Summary and Detail View",
			},
		];
	} else {
		resource.meta.tag = [
			{
				system:
					"https://fhir.blackpear.com/ui/shared-care-record-visibility",
				code: "detail",
				display: "Display in Detail View",
			},
		];
	}

	if (
		result.encounterStatusMapped != undefined &&
		result.encounterStatusMapped == "planned"
	) {
		resource.meta.tag = [
			{
				system:
					"https://fhir.blackpear.com/ui/shared-care-record-visibility",
				code: "none",
				display: "Do not Display",
			},
		];
	}

	resource.type = [];

	var emptyType = {
		coding: [
			{
				system: "https://fhir.nhs.uk/STU3/CodeSystem/Specialty-1",
				code: undefined,
				display: undefined,
			},
		],

		extension: [
			{
				url:
					"https://fhir.ydh.nhs.uk/STU3/StructureDefinition/Extension-YDH-SpecialtyContext-1",
				valueCodeableConcept: {
					coding: [
						{
							system:
								"https://fhir.ydh.nhs.uk/STU3/ValueSet/Extension-YDH-SpecialtyContext-1",
							code: undefined,
							display: undefined,
						},
					],
				},
			},
		],
	};

	if (
		result.encounterClassCode != undefined &&
		result.encounterClassCode == "IMP"
	) {
		var admType = JSON.parse(JSON.stringify(emptyType));
		var disType = JSON.parse(JSON.stringify(emptyType));

		if (result.encounterTypeCodeAdm != undefined) {
			admType.coding[0].code = newStringOrUndefined(
				result.encounterTypeCodeAdm
			);

			admType.coding[0].display = newStringOrUndefined(
				result.encounterTypeDescAdm
			);

			admType.extension[0].valueCodeableConcept.coding[0].code = "ADM";
			admType.extension[0].valueCodeableConcept.coding[0].display =
				"Admitting";
			resource.type.push(admType);
		} else if (result.encounterTypeCode != undefined) {
			admType.coding[0].code = newStringOrUndefined(
				result.encounterTypeCode
			);

			admType.coding[0].display = newStringOrUndefined(
				result.encounterTypeDesc
			);

			delete admType.extension;
			resource.type.push(admType);
		}

		if (result.encounterTypeCodeDis != undefined) {
			disType.coding[0].code = newStringOrUndefined(
				result.encounterTypeCodeDis
			);

			disType.coding[0].display = newStringOrUndefined(
				result.encounterTypeDescDis
			);

			disType.extension[0].valueCodeableConcept.coding[0].code = "DIS";
			disType.extension[0].valueCodeableConcept.coding[0].display =
				"Discharging";
			resource.type.push(disType);
		} else if (result.encounterTypeCode != undefined) {
			disType.coding[0].code = newStringOrUndefined(
				result.encounterTypeCode
			);

			disType.coding[0].display = newStringOrUndefined(
				result.encounterTypeDesc
			);

			delete disType.extension;
			resource.type.push(disType);
		}

		if (resource.type.length > 1) {
			if (
				JSON.stringify(resource.type[0]) ==
				JSON.stringify(resource.type[1])
			) {
				delete resource.type[1];
			}
		}
	} else {
		var outType = JSON.parse(JSON.stringify(emptyType));
		if (result.encounterTypeCode != undefined) {
			outType.coding[0].code = newStringOrUndefined(
				result.encounterTypeCode
			);

			outType.coding[0].display = newStringOrUndefined(
				result.encounterTypeDesc
			);

			delete outType.extension;
			resource.type.push(outType);
		}
	}

	// Add participants
	resource.participant = [];
	if (
		result.encounterParticipantIndividualCode_admitting != undefined &&
		result.encounterParticipantIndividualCode_discharging != undefined &&
		result.encounterParticipantIndividualCode_discharging ==
			result.encounterParticipantIndividualCode_admitting
	) {
		var participantCombo = {
			type: [
				{
					coding: [
						{
							system: "https://hl7.org/fhir/v3/ParticipationType",
							code: "ADM",
							display: "admitter",
						},
					],
				},

				{
					coding: [
						{
							system: "https://hl7.org/fhir/v3/ParticipationType",
							code: "DIS",
							display: "discharger",
						},
					],
				},
			],

			individual: {
				identifier: {
					value: result.encounterParticipantIndividualCode_admitting,
				},

				display: result.encounterParticipantIndividualDisplay_admitting,
			},
		};

		resource.participant.push(participantCombo);
	}

	if (resource.participant.length == 0) {
		if (result.encounterParticipantIndividualCode_admitting != undefined) {
			var participantAdmitter = {
				type: [
					{
						coding: [
							{
								system:
									"https://hl7.org/fhir/v3/ParticipationType",
								code: "ADM",
								display: "admitter",
							},
						],
					},
				],

				individual: {
					identifier: {
						value:
							result.encounterParticipantIndividualCode_admitting,
					},

					display:
						result.encounterParticipantIndividualDisplay_admitting,
				},
			};

			resource.participant.push(participantAdmitter);
		}
		if (
			result.encounterParticipantIndividualCode_discharging != undefined
		) {
			var participantDischarger = {
				type: [
					{
						coding: [
							{
								system:
									"https://hl7.org/fhir/v3/ParticipationType",
								code: "DIS",
								display: "discharger",
							},
						],
					},
				],

				individual: {
					identifier: {
						value:
							result.encounterParticipantIndividualCode_discharging,
					},

					display:
						result.encounterParticipantIndividualDisplay_discharging,
				},
			};

			resource.participant.push(participantDischarger);
		}
	}
	if (result.encounterParticipantIndividualCode_opattending != undefined) {
		var participantConsultant = {
			type: [
				{
					coding: [
						{
							system: "https://hl7.org/fhir/v3/ParticipationType",
							code: "CON",
							display: "consultant",
						},
					],
				},
			],

			individual: {
				identifier:
					result.encounterParticipantIndividualCode_opattending,
				display:
					result.encounterParticipantIndividualDisplay_opattending,
			},
		};

		resource.participant.push(participantConsultant);
	}

	resource.period = {};
	if (
		result.encounterPeriodStart != undefined &&
		result.encounterPeriodStart.substring(0, 1) != "T" &&
		result.encounterPeriodStart.substring(0, 4) != "1900"
	) {
		resource.period.start = result.encounterPeriodStart;
	}
	if (
		result.encounterPeriodEnd != undefined &&
		result.encounterPeriodEnd.substring(0, 1) != "T" &&
		result.encounterPeriodEnd.substring(0, 4) != "1900"
	) {
		resource.period.end = result.encounterPeriodEnd;
	}

	// Add admission and discharge inpatient details
	resource.hospitalization = {};

	if (
		result.encounterAdmissionmethodCodingCode != undefined ||
		result.encounterDischargemethodCodingCode != undefined
	) {
		resource.hospitalization.extension = [];
	}

	if (result.encounterAdmissionmethodCodingCode != undefined) {
		var admissionMethod = {
			url:
				"https://fhir.hl7.org.uk/STU3/StructureDefinition/Extension-CareConnect-AdmissionMethod-1",
			valueCodeableConcept: {
				coding: [
					{
						system:
							"https://fhir.hl7.org.uk/STU3/ValueSet/CareConnect-AdmissionMethod-1",
						code: result.encounterAdmissionmethodCodingCode,
						display: newStringOrUndefined(
							result.encounterAdmissionmethodCodingDesc
						),
					},
				],
			},
		};

		resource.hospitalization.extension.push(admissionMethod);
	}

	if (result.encounterDischargemethodCodingCode != undefined) {
		var dischargeMethod = {
			url:
				"https://fhir.hl7.org.uk/STU3/StructureDefinition/Extension-CareConnect-DischargeMethod-1",
			valueCodeableConcept: {
				coding: [
					{
						system:
							"https://fhir.hl7.org.uk/STU3/ValueSet/CareConnect-DischargeMethod-1",
						code: result.encounterDischargemethodCodingCode,
						display: newStringOrUndefined(
							result.encounterDischargemethodCodingDesc
						),
					},
				],
			},
		};

		resource.hospitalization.extension.push(dischargeMethod);
	}

	if (result.encounterHospitalizationAdmitsourceCodingCode != undefined) {
		resource.hospitalization.admitSource = {
			coding: [
				{
					system:
						"https://fhir.hl7.org.uk/STU3/CodeSystem/CareConnect-SourceOfAdmission-1",
					code: result.encounterHospitalizationAdmitsourceCodingCode,
					display: newStringOrUndefined(
						result.encounterHospitalizationAdmitsourceCodingDesc
					),
				},
			],
		};
	}
	if (
		result.encounterHospitalizationDischargedispositionCodingCode !=
		undefined
	) {
		resource.hospitalization.dischargeDisposition = {
			coding: [
				{
					system:
						"https://fhir.hl7.org.uk/STU3/CodeSystem/CareConnect-DischargeDestination-1",
					code:
						result.encounterHospitalizationDischargedispositionCodingCode,
					display: newStringOrUndefined(
						result.encounterHospitalizationDischargedispositionCodingDesc
					),
				},
			],
		};
	}

	// Add location details
	if (
		result.encounterClassCode != undefined &&
		result.encounterClassCode == "IMP"
	) {
		resource.location = [];

		var emptyLocation = {
			location: {
				identifier: {
					value: undefined,
				},

				display: undefined,
			},

			period: {
				start: undefined,
				end: undefined,
			},
		};

		if (
			result.encounterLocation1Identifier != undefined &&
			typeof resource.period.start !== "undefined" &&
			result.encounterLocation2Identifier != undefined &&
			typeof resource.period.end !== "undefined" &&
			result.encounterLocation1Identifier ==
				result.encounterLocation2Identifier
		) {
			var ward = JSON.parse(JSON.stringify(emptyLocation));

			ward.location.identifier.value = newStringOrUndefined(
				result.encounterLocation1Identifier
			);

			ward.location.display = newStringOrUndefined(
				result.encounterLocation1Display
			);

			ward.period.start = resource.period.start;
			ward.period.end = resource.period.end;
			resource.location.push(ward);
		}

		if (resource.location.length == 0) {
			if (
				result.encounterLocation1Identifier != undefined &&
				typeof resource.period.start !== "undefined"
			) {
				var admittingWard = JSON.parse(JSON.stringify(emptyLocation));

				admittingWard.location.identifier.value = newStringOrUndefined(
					result.encounterLocation1Identifier
				);

				admittingWard.location.display = newStringOrUndefined(
					result.encounterLocation1Display
				);

				admittingWard.period.start = resource.period.start;
				resource.location.push(admittingWard);
			}

			if (
				result.encounterLocation2Identifier != undefined &&
				typeof resource.period.end !== "undefined"
			) {
				var dischargeWard = JSON.parse(JSON.stringify(emptyLocation));

				dischargeWard.location.identifier.value = newStringOrUndefined(
					result.encounterLocation2Identifier
				);

				dischargeWard.location.display = newStringOrUndefined(
					result.encounterLocation2Display
				);

				dischargeWard.period.end = resource.period.end;
				resource.location.push(dischargeWard);
			}
		}
	}

	resource.subject = {
		reference: ""
			.concat($cfg("apiUrl"), "/STU3/Patient/")
			.concat(result.subjectReference),
	};

	return resource;
}

/**
	Builds Flag FHIR Resource that adheres to its Care-Connect profile,
	see https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Flag-1 for more info.

	@author Frazer Smith
	@param {object} data - Java RowSet object.
	@returns {object} Flag FHIR resource.
 */
function buildFlagResource(data) {
	var result = getResultSet(data);

	/**
	 * Hard-coding meta profile and resourceType into resource as this should not
	 * be changed for this resource, ever.
	 */
	var resource = {
		meta: {
			profile: [
				"https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Flag-1",
			],
		},

		resourceType: "Flag",
	};

	// Add meta data
	if (
		result.lastUpdated != undefined &&
		result.lastUpdated.substring(0, 1) != "T" &&
		result.lastUpdated.substring(0, 4) != "1900"
	) {
		resource.meta.lastUpdated = result.lastUpdated;
	}

	/**
	 * Add SIDeR specific tags
	 * Set tag to 'Do not Display' if not in set of accepted SNOMED codes or if inactive
	 */
	var siderAcceptedFlagSnomedCodes = [
		"13790001000004100",
		"15188001",
		"32000005",
		"713673000",
	];

	if (
		result.flagStatusCode == "inactive" ||
		!siderAcceptedFlagSnomedCodes.includes(result.flagCodeCodingSnomedCode)
	) {
		resource.meta.tag = [
			{
				system:
					"https://fhir.blackpear.com/ui/shared-care-record-visibility",
				code: "none",
				display: "Do not Display",
			},
		];
	} else {
		resource.meta.tag = [
			{
				system:
					"https://fhir.blackpear.com/ui/shared-care-record-visibility",
				code: "summary",
				display: "Display in Summary and Detail View",
			},
		];
	}

	resource.id = newStringOrUndefined(result.flagId);
	resource.status = newStringOrUndefined(result.flagStatusCode);

	if (result.flagCategoryCodingCode != undefined) {
		resource.category = {
			coding: [
				{
					system: "https://trakcare.ydh.nhs.uk",
					code: newStringOrUndefined(result.flagCategoryCodingCode),
					display: newStringOrUndefined(
						result.flagCategoryCodingDisplay
					),
				},
			],
		};
	}

	resource.code = {
		coding: [],
	};

	if (result.flagCodeCodingCode != undefined) {
		var ydhCode = {
			system: "https://trakcare.ydh.nhs.uk",
			code: newStringOrUndefined(result.flagCodeCodingCode),
			display: newStringOrUndefined(result.flagCodeCodingDisplay),
		};

		resource.code.coding.push(ydhCode);
	}

	if (result.flagCodeCodingSnomedCode != undefined) {
		var snomedCode = {
			system: "https://snomed.info/sct",
			code: newStringOrUndefined(result.flagCodeCodingSnomedCode),
			display: newStringOrUndefined(result.flagCodeCodingSnomedDisplay),
		};

		resource.code.coding.push(snomedCode);
	}

	resource.period = {};
	if (
		result.periodStart != undefined &&
		result.periodStart.substring(0, 1) != "T" &&
		result.periodStart.substring(0, 4) != "1900"
	) {
		resource.period.start = result.periodStart;
	}
	if (
		result.periodStart != undefined &&
		result.periodStart.substring(0, 1) != "T" &&
		result.periodStart.substring(0, 4) != "1900"
	) {
		resource.period.end = result.periodStart;
	}

	resource.subject = {
		reference: ""
			.concat($cfg("apiUrl"), "/STU3/Patient/")
			.concat(result.flagSubjectReference),
	};

	return resource;
}

/**
	Builds MedicationStatement FHIR Resource that adheres to its Care-Connect profile,
	see https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-MedicationStatement-1 for more info.

	@author Frazer Smith
	@param {object} data - Java RowSet object.
	@returns {object} MedicationStatement FHIR resource.
 */
function buildMedicationStatementResource(data) {
	var result = getResultSet(data);

	/**
	 * Hard-coding meta profile and resourceType into resource as this should not
	 * be changed for this resource, ever.
	 */
	var resource = {
		meta: {
			profile: [
				"https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-MedicationStatement-1",
			],
		},

		resourceType: "MedicationStatement",
	};

	resource.id = newStringOrUndefined(result.medstatId);
	resource.status = newStringOrUndefined(result.medstatStatusCode);

	// Add meta data
	if (
		result.lastUpdated != undefined &&
		result.lastUpdated.substring(0, 1) != "T" &&
		result.lastUpdated.substring(0, 4) != "1900"
	) {
		resource.meta.lastUpdated = result.lastUpdated;
	}

	/**
	 * Add SIDeR specific tags
	 * Set tag for meds from within the last 60 days
	 */
	if (
		result.medstatEffectiveStart != undefined &&
		result.medstatEffectiveStart.substring(0, 1) != "T" &&
		result.medstatEffectiveStart.substring(0, 4) != "1900" &&
		Math.ceil(
			(new Date(result.medstatEffectiveStart) - new Date()) /
				(24 * 60 * 60 * 1000)
		) >= -60
	) {
		resource.meta.tag = [
			{
				system:
					"https://fhir.blackpear.com/ui/shared-care-record-visibility",
				code: "summary",
				display: "Display in Summary and Detail View",
			},
		];
	} else {
		resource.meta.tag = [
			{
				system:
					"https://fhir.blackpear.com/ui/shared-care-record-visibility",
				code: "none",
				display: "Do not Display",
			},
		];
	}

	resource.medicationReference = {
		reference: newStringOrUndefined("#".concat(result.medicationId)),
	};

	// Add contained Medication resource
	var contained = [];
	if (result.medicationId != undefined) {
		var containedMedication = {
			resourceType: "Medication",
			id: result.medicationId,
			code: {
				coding: [
					{
						code: newStringOrUndefined(
							result.medicationCodeCodingCode
						),

						display: newStringOrUndefined(
							result.medicationCodeCodingDisplay
						),
					},
				],

				text: result.medicationCodeText,
			},
		};

		contained.push(containedMedication);

		if (
			result.medicationCodeCodingCode != undefined &&
			result.medicationCodeCodingDisplay != undefined
		) {
			containedMedication.code.coding[0].system =
				"https://snomed.info/sct";
		}
	}

	if (contained.length > 0) {
		resource.contained = contained;
	}

	// Add dosages
	var dosage = [];
	var dosageObject = {
		patientInstruction: newStringOrUndefined(
			result.medstatDosagePatientinstruction
		),

		route: {
			text: newStringOrUndefined(result.medstatDosageRouteText),
		},
	};

	if (
		result.medstatDosageDoseQuantityValue != undefined &&
		result.medstatDosageDoseQuantityUnit != undefined
	) {
		dosageObject.doseQuantity = {
			value: newStringOrUndefined(result.medstatDosageDoseQuantityValue),
			unit: newStringOrUndefined(
				result.medstatDosageDoseQuantityUnit
			).toLowerCase(),
		};
	}
	if (
		result.medstatDosageTimingRepeatDuration != undefined &&
		result.medstatDosageTimingRepeatDurationUnit != undefined &&
		result.medstatDosageTimingRepeatDurationUnit != "DO"
	) {
		dosageObject.timing = {
			repeat: {
				duration: newStringOrUndefined(
					result.medstatDosageTimingRepeatDuration
				),

				durationUnit: newStringOrUndefined(
					result.medstatDosageTimingRepeatDurationUnit
				).toLowerCase(),
			},
		};
	}

	dosage.push(dosageObject);
	if (dosage.length > 0) {
		resource.dosage = dosage;
	}

	resource.effectivePeriod = {};
	if (
		result.medstatEffectiveStart != undefined &&
		result.medstatEffectiveStart.substring(0, 1) != "T" &&
		result.medstatEffectiveStart.substring(0, 4) != "1900"
	) {
		resource.effectivePeriod.start = result.medstatEffectiveStart;
	}
	if (
		result.medstatEffectiveEnd != undefined &&
		result.medstatEffectiveEnd.substring(0, 1) != "T" &&
		result.medstatEffectiveEnd.substring(0, 4) != "1900"
	) {
		resource.effectivePeriod.end = result.medstatEffectiveEnd;
	}

	if (result.medStatContextEncounterReference != undefined) {
		resource.context = {
			reference: ""
				.concat($cfg("apiUrl"), "/STU3/Encounter/")
				.concat(result.medStatContextEncounterReference),
		};
	}

	resource.subject = {
		reference: ""
			.concat($cfg("apiUrl"), "/STU3/Patient/")
			.concat(result.medstatSubjectReference),
	};

	// Hard-coded as TrakCare doesn't record whether a patient has taken medication
	resource.taken = "unk";

	return resource;
}

/**
	Builds Patient FHIR resource that adheres to its Care-Connect profile,
	see https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Patient-1 for more info.

	@author Frazer Smith
	@param {object} data - Java RowSet object.
	@returns {object} Patient FHIR resource.
 */
function buildPatientResource(data) {
	var result = getResultSet(data);

	if (
		result.nhsNumberTraceStatusCode == undefined ||
		result.nhsNumberTraceStatusCode == null ||
		result.nhsNumberTraceStatusCode == "0"
	) {
		result.nhsNumberTraceStatusCode = "2";
		result.nhsNumberTraceStatusDesc = "Number present but not traced";
	}

	if (result.deceased == undefined || result.deceased == null) {
		result.deceased = false;
	} else {
		result.deceased = true;
	}

	/**
	 * Hard-coding meta profile and resourceType into resource as this should not
	 * be changed for this resource, ever.
	 */
	var resource = {
		fullUrl: ""
			.concat($cfg("apiUrl") + $("contextPath"), "/")
			.concat(result.patientNo),
		meta: {
			profile: [
				"https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Patient-1",
			],
		},

		resourceType: newStringOrUndefined("Patient"),
		identifier: [
			{
				use: newStringOrUndefined("usual"),
				system: newStringOrUndefined(
					"https://fhir.ydh.nhs.uk/Id/local-patient-identifier"
				),

				value: newStringOrUndefined(result.patientNo),
			},
		],

		name: [
			{
				use: newStringOrUndefined("usual"),
				family: newStringOrUndefined(result.nameFamily),
				given: result.nameGiven1First,
				prefix: result.namePrefix,
			},
		],

		gender: newStringOrUndefined(result.gender),
		birthDate: newStringOrUndefined(result.birthdate),
		deceasedBoolean: newBooleanOrUndefined(result.deceased),
		address: [
			{
				use: newStringOrUndefined("home"),
				type: newStringOrUndefined("postal"),
				line: [
					newStringOrUndefined(result.addressLine1),
					newStringOrUndefined(result.addressLine2),
				],

				city: newStringOrUndefined(result.city),
				district: newStringOrUndefined(result.district),
				postalCode: newStringOrUndefined(result.postalCode),
			},
		],

		id: newStringOrUndefined(result.patientNo),
		language: newStringOrUndefined("English (Great Britain)"),
	};

	// Add meta data
	if (
		result.lastUpdated != undefined &&
		result.lastUpdated.substring(0, 1) != "T" &&
		result.lastUpdated.substring(0, 1) != "1900"
	) {
		resource.meta.lastUpdated = newStringOrUndefined(result.lastUpdated);
	}

	// Add NHS No
	if (result.nhsNumber != undefined) {
		var nhsIdentifier = {
			use: newStringOrUndefined("official"),
			system: newStringOrUndefined("https://fhir.nhs.uk/Id/nhs-number"),
			value: newStringOrUndefined(result.nhsNumber),
			extension: [
				{
					url: newStringOrUndefined(
						"https://fhir.hl7.org.uk/STU3/StructureDefinition/Extension-CareConnect-NHSNumberVerificationStatus-1"
					),

					valueCodeableConcept: {
						coding: [
							{
								system: newStringOrUndefined(
									"https://fhir.hl7.org.uk/STU3/CodeSystem/CareConnect-NHSNumberVerificationStatus-1"
								),

								code: newStringOrUndefined(
									pad(result.nhsNumberTraceStatusCode, 2)
								),

								display: newStringOrUndefined(
									result.nhsNumberTraceStatusDesc
								),
							},
						],
					},
				},
			],
		};

		resource.identifier.push(nhsIdentifier);
	}

	resource.contact = [];

	// Add Next of kin contact details
	if (result.contactName != undefined) {
		var nokContact = {
			relationship: {
				coding: [
					{
						system: "https://hl7.org/fhir/v2/0131",
						code: "N",
						display: "Next-of-kin",
					},
				],
			},

			name: {
				use: "usual",
				text: newStringOrUndefined(result.contactName),
			},
		};

		if (result.contactPhone != undefined) {
			var contactTelecom = [
				{
					system: "phone",
					value: newStringOrUndefined(result.contactPhone),
				},
			];

			nokContact.telecom = contactTelecom;
		}

		resource.contact.push(nokContact);
	}

	// Add school contact details
	if (result.schoolName != undefined) {
		var schoolContact = {
			relationship: {
				coding: [
					{
						system: "https://trakcare.ydh.nhs.uk",
						code: "SCH",
						display: "School",
					},
				],
			},

			name: {
				use: "anonymous",
				text: "School nurse",
			},

			organization: {
				identifier: {
					system: "https://trakcare.ydh.nhs.uk",
					value: newStringOrUndefined(result.schoolId),
				},

				display: newStringOrUndefined(result.schoolName),
			},
		};

		if (result.schoolPhone != undefined) {
			var _contactTelecom = [
				{
					system: "phone",
					value: newStringOrUndefined(result.schoolPhone),
				},
			];

			schoolContact.telecom = _contactTelecom;
		}

		resource.contact.push(schoolContact);
	}

	// Add YDH Switchboard contact details
	var switchboardContact = {
		name: {
			use: "anonymous",
			text: "Switchboard",
		},

		telecom: [
			{
				system: "phone",
				value: "01935475122",
			},
		],

		organization: {
			reference:
				"https://directory.spineservices.nhs.uk/STU3/Organization/RA4",
			display: "YEOVIL DISTRICT HOSPITAL NHS FOUNDATION TRUST",
		},
	};

	resource.contact.push(switchboardContact);

	// Add Telecom contact details
	var telecom = [];
	if (result.homePhone != undefined) {
		var homePhone = {
			system: newStringOrUndefined("phone"),
			value: newStringOrUndefined(result.homePhone),
			use: newStringOrUndefined("home"),
		};

		telecom.push(homePhone);
	}
	if (result.mobilePhone != undefined) {
		var mobilePhone = {
			system: newStringOrUndefined("phone"),
			value: newStringOrUndefined(result.mobilePhone),
			use: newStringOrUndefined("mobile"),
		};

		telecom.push(mobilePhone);
	}
	if (result.email != undefined) {
		var email = {
			system: newStringOrUndefined("email"),
			value: newStringOrUndefined(result.email),
		};

		telecom.push(email);
	}
	if (telecom.length > 0) {
		resource.telecom = telecom;
	}

	// Extensions (Care Connect or otherwise)
	var extension = [];
	// Add Ethnic Category extension
	if (result.ethnicCategoryCode != undefined) {
		var ethCatExtension = {
			url: newStringOrUndefined(
				"https://fhir.hl7.org.uk/STU3/StructureDefinition/Extension-CareConnect-EthnicCategory-1"
			),

			valueCodeableConcept: {
				coding: [
					{
						system: newStringOrUndefined(
							"https://fhir.hl7.org.uk/STU3/CodeSystem/CareConnect-EthnicCategory-1"
						),

						code: newStringOrUndefined(
							result.ethnicCategoryCareConnectCode
						),

						display: newStringOrUndefined(
							result.ethnicCategoryCareConnectDesc
						),
					},

					{
						system: newStringOrUndefined(
							"https://trakcare.ydh.nhs.uk"
						),

						code: newStringOrUndefined(result.ethnicCategoryCode),
						display: newStringOrUndefined(
							result.ethnicCategoryDesc
						),
					},
				],
			},
		};

		extension.push(ethCatExtension);
	}

	// Add Religious Affiliation extension
	if (result.religiousAffiliationCode != undefined) {
		var relAffExtension = {
			url: newStringOrUndefined(
				"https://fhir.hl7.org.uk/STU3/StructureDefinition/Extension-CareConnect-ReligiousAffiliation-1"
			),

			valueCodeableConcept: {
				coding: [
					{
						system: newStringOrUndefined(
							"https://datadictionary.nhs.uk"
						),

						code: newStringOrUndefined(
							result.religiousAffiliationCode
						),

						display: newStringOrUndefined(
							result.religiousAffiliationDesc
						),
					},
				],
			},
		};

		extension.push(relAffExtension);
	}

	// Add NHS Communication extension
	if (result.preferredLanguageCode != undefined) {
		var nhsComExtension = {
			url: newStringOrUndefined(
				"https://fhir.hl7.org.uk/STU3/StructureDefinition/Extension-CareConnect-NHSCommunication-1"
			),

			extension: [
				{
					url: "language",
					valueCodeableConcept: {
						coding: [
							{
								system: newStringOrUndefined(
									"https://fhir.hl7.org.uk/STU3/CodeSystem/CareConnect-HumanLanguage-1"
								),

								code: newStringOrUndefined(
									result.preferredLanguageCode
								),

								display: newStringOrUndefined(
									result.preferredLanguageDesc
								),
							},
						],
					},
				},
			],
		};

		// Add interpreterRequired extension to NHS Communication extensions array
		if (
			result.interpreterRequired != undefined &&
			result.interpreterRequired != "NS"
		) {
			var intReqExtension = {
				url: "interpreterRequired",
			};

			if (result.interpreterRequired == "Y") {
				intReqExtension.valueBoolean = true;
			} else {
				intReqExtension.valueBoolean = false;
			}

			nhsComExtension.extension.push(intReqExtension);
		}

		extension.push(nhsComExtension);
	}

	if (extension.length > 0) {
		resource.extension = extension;
	}

	// Add contained GP organization resource
	var contained = [];
	if (result.gpIdentifier != undefined) {
		var containedOrganisation = {
			resourceType: "Organization",
			id: newStringOrUndefined(result.gpIdentifier),
			meta: {
				profile: [
					"https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Organization-1",
				],
			},

			name: newStringOrUndefined(result.gpDesc),
			address: [
				{
					use: newStringOrUndefined("work"),
					type: newStringOrUndefined("both"),
					line: [
						newStringOrUndefined(result.gpAddressLine1),
						newStringOrUndefined(result.gpAddressLine2),
					],

					city: newStringOrUndefined(result.gpCity),
					postalCode: newStringOrUndefined(result.gpPostalCode),
				},
			],
		};

		contained.push(containedOrganisation);
	}
	if (contained.length > 0) {
		resource.contained = contained;
	}

	// Add GP
	var generalPractitioner = [];
	if (result.gpIdentifier != undefined) {
		var gpReference = {
			reference: newStringOrUndefined("#".concat(result.gpIdentifier)),
			display: newStringOrUndefined(result.gpDesc),
		};

		generalPractitioner.push(gpReference);
	}

	if (generalPractitioner.length > 0) {
		resource.generalPractitioner = generalPractitioner;
	}

	// Add Marital Status
	if (
		result.maritalStatusCode != undefined &&
		result.maritalStatusDesc != undefined
	) {
		resource.maritalStatus = {
			coding: [
				{
					system: newStringOrUndefined(
						"https://hl7.org/fhir/stu3/v3/MaritalStatus"
					),

					code: newStringOrUndefined(result.maritalStatusCode),
					display: newStringOrUndefined(result.maritalStatusDesc),
				},
			],
		};
	}

	// If patient has a 'Do Not Distribute Patient Address' alert, strip out contact details
	if (result.DND != undefined) {
		delete resource.telecom;
		delete resource.address;
		resource.meta.security = [
			{
				system: "https://hl7.org/fhir/ValueSet/v3-Confidentiality",
				code: "R",
				display: "restricted",
			},
		];
	}

	return resource;
}

/**
	Queries database for data needed to build FHIR resource.

	@author Frazer Smith
	@param {string} type - Resource name.
	@param {object} params - An array of predicates to be added to SQL queries.
	@returns {object} Java ResultSet object.
 */
function buildResourceQuery(type, params) {
	var firstParams = "";
	var secondParams = "";
	var thirdParams = "";
	var fourthParams = "";

	if (params[0]) {
		firstParams = "AND ".concat(params[0]);
	}
	if (params[1]) {
		secondParams = "AND ".concat(params[1]);
	}
	if (params[2]) {
		thirdParams = "AND ".concat(params[2]);
	}
	// Used exclusively by encounter and allergyintolerance resource queries for now
	if (params[3]) {
		fourthParams = "WHERE ".concat(params[3]);
	}

	// type is Java object so has to be coerced to a JS object by adding an empty string
	switch ("".concat(type)) {
		case "allergyintolerance":
			return executeCachedQuery(
				"SELECT DISTINCT id, patientReference, allergyGroupDesc, allergyCodingDesc, allergyDrugDesc, allergyDrugGenericDesc, allergyDrugCategoryDesc, allergyDrugFormDesc, allergyDrugIngredientDesc, allergyComment, clinicalStatusCode, verificationStatusCode, typeCode, criticalityCode, CONCAT(COALESCE(assertedDate, ''), 'T', COALESCE(assertedTime, '')) AS assertedDate, CONCAT(COALESCE(lastUpdateDate, ''), 'T', COALESCE(lastUpdateTime, '')) AS lastUpdated FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT DISTINCT REPLACE(alle.ALG_RowId, ''||'', ''-'') AS id, alle.ALG_PAPMI_ParRef->PAPMI_No AS patientReference, alle.ALG_AllergyGrp_DR->ALGR_Desc AS allergyGroupDesc, alle.ALG_TYPE_DR->ALG_Desc AS allergyCodingDesc, alle.ALG_PHCDM_DR->PHCD_ProductName AS allergyDrugDesc, alle.ALG_PHCGE_DR->PHCGE_Name AS allergyDrugGenericDesc, alle.ALG_PHCSC_DR->PHCSC_Desc AS allergyDrugCategoryDesc, alle.ALG_PHCDRGForm_DR->PHCDF_Description AS allergyDrugFormDesc, alle.ALG_Ingred_DR->INGR_Desc AS allergyDrugIngredientDesc, alle.ALG_Comments AS allergyComment, CASE alle.ALG_Status WHEN ''A'' THEN ''active'' WHEN ''I'' THEN ''inactive'' WHEN ''R'' THEN ''resolved'' ELSE NULL END AS clinicalStatusCode, CASE WHEN alle.ALG_Status = ''C'' THEN ''unconfirmed'' WHEN alle.ALG_ConfirmedDate IS NOT NULL OR (alle.ALG_Status != ''C'' AND alle.ALG_Status IS NOT NULL) THEN ''confirmed'' ELSE ''unconfirmed'' END as verificationStatusCode, CASE alle.ALG_Category_DR->ALRGCAT_DESC WHEN ''ALLERGY'' THEN ''allergy'' WHEN ''SIDEEFFECT'' THEN ''intolerance'' ELSE NULL END AS typeCode, CASE alle.ALG_Severity_DR WHEN 1 THEN ''high'' WHEN 2 THEN ''low'' WHEN 5 THEN ''high'' WHEN 4 THEN ''unable-to-assess'' ELSE NULL END AS criticalityCode, alle.ALG_Date AS assertedDate, alle.ALG_Time AS assertedTime, alle.ALG_LastUpdateDate AS lastUpdateDate, alle.ALG_LastUpdateTime as lastUpdateTime FROM PA_Allergy alle WHERE (alle.ALG_PAPMI_ParRef->PAPMI_No IS NOT NULL) "
					.concat(firstParams, "') ")
					.concat(fourthParams, ";")
			);

		case "condition":
			return executeCachedQuery();
		case "documentreference":
			return executeCachedQuery();
		case "encounter":
			return executeCachedQuery(
				"WITH encounter_CTE(encounterIdentifier, encounterClassDesc, encounterClassCode, encounterTypeDesc, encounterTypeCode, encounterPeriodStartDate, encounterPeriodStartTime, encounterPeriodEndDate, encounterPeriodEndTime, encounterParticipantIndividualCode_opattending, encounterParticipantIndividualDisplay_opattending, encounterHospitalizationAdmitsourceCodingCode, encounterHospitalizationAdmitsourceCodingDesc, encounterHospitalizationDischargedispositionCodingCode, encounterHospitalizationDischargedispositionCodingDesc, encounterAdmissionmethodCodingCode, encounterAdmissionmethodCodingDesc, encounterDischargemethodCodingCode, encounterDischargemethodCodingDesc, subjectReference, encounterStatus, lastUpdateDate, lastUpdateTime, encounterStatusMapped) AS (SELECT DISTINCT *, CASE WHEN encounterStatus IN ('C', 'N', 'X', 'T', 'J', 'H') THEN 'cancelled' WHEN encounterStatus IN ('D', 'R') OR (encounterStatus IN ('A') AND encounterPeriodStartDate IS NOT NULL AND encounterPeriodEndDate IS NOT NULL) THEN 'finished' WHEN (encounterPeriodStartDate > CURRENT_TIMESTAMP AND encounterPeriodStartDate IS NOT NULL) OR encounterStatus IN ('P') THEN 'planned' WHEN encounterStatus IN ('A', 'S', 'W') THEN 'arrived' WHEN encounterPeriodStartDate IS NOT NULL AND encounterPeriodEndDate IS NULL THEN 'in-progress' ELSE 'unknown' END AS encounterStatusMapped FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT REPLACE(app.APPT_RowId, ''||'', ''-'') AS encounterIdentifier, ''ambulatory'' AS encounterClassDesc, ''AMB'' AS encounterClassCode, app.APPT_AS_ParRef->AS_RES_ParRef->RES_CTLOC_DR->CTLOC_Desc AS encounterTypeDesc, app.APPT_AS_ParRef->AS_RES_ParRef->RES_CTLOC_DR->CTLOC_Code AS encounterTypeCode, COALESCE(app.APPT_ArrivalDate, app.APPT_DateComp) AS encounterPeriodStartDate, COALESCE(app.APPT_ArrivalTime, app.APPT_TimeComp) AS encounterPeriodStartTime, NULL AS encounterPeriodEndDate, NULL AS encounterPeriodEndTime, app.APPT_SeenDoctor_DR->CTPCP_Code AS encounterParticipantIndividualCode_opattending, app.APPT_SeenDoctor_DR->CTPCP_Desc AS encounterParticipantIndividualDisplay_opattending, NULL AS encounterHospitalizationAdmitsourceCodingCode, NULL AS encounterHospitalizationAdmitsourceCodingDesc, NULL AS encounterHospitalizationDischargedispositionCodingCode, NULL AS encounterHospitalizationDischargedispositionCodingDesc, NULL AS encounterAdmissionmethodCodingCode, NULL AS encounterAdmissionmethodCodingDesc, NULL AS encounterDischargemethodCodingCode, NULL AS encounterDischargemethodCodingDesc, app.APPT_Adm_DR->PAADM_PAPMI_DR->PAPMI_No AS subjectReference, app.APPT_Status AS encounterStatus, app.APPT_LastUpdateDate AS lastUpdateDate, NULL AS lastUpdateTime FROM RB_Appointment app WHERE app.APPT_Adm_DR->PAADM_PAPMI_DR->PAPMI_No IS NOT NULL "
					.concat(
						firstParams,
						"') UNION SELECT DISTINCT *, CASE WHEN encounterStatus IN ('C', 'N', 'X', 'T', 'J', 'H') THEN 'cancelled' WHEN encounterStatus IN ('D', 'R') OR (encounterStatus IN ('A') AND encounterPeriodStartDate IS NOT NULL AND encounterPeriodEndDate IS NOT NULL) THEN 'finished' WHEN (encounterPeriodStartDate > CURRENT_TIMESTAMP AND encounterPeriodStartDate IS NOT NULL) OR encounterStatus IN ('P') THEN 'planned' WHEN encounterStatus IN ('A', 'S', 'W') THEN 'arrived' WHEN encounterPeriodStartDate IS NOT NULL AND encounterPeriodEndDate IS NULL THEN 'in-progress' ELSE 'unknown' END AS encounterStatusMapped FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT REPLACE(PAADM_ADMNo, ''/'', ''-'') AS encounterIdentifier, CASE PAADM_Type WHEN ''I'' THEN ''inpatient'' WHEN ''E'' THEN ''emergency'' END as encounterClassDesc, CASE PAADM_Type WHEN ''I'' THEN ''IMP'' WHEN ''E'' THEN ''EMER'' END as encounterClassCode, PAADM_DepCode_DR->CTLOC_Desc AS encounterTypeDesc, PAADM_DepCode_DR->CTLOC_Code AS encounterTypeCode, PAADM_AdmDate AS encounterPeriodStartDate, PAADM_AdmTime AS encounterPeriodStartTime, COALESCE(PAADM_DischgDate, PAADM_EstimDischargeDate) AS encounterPeriodEndDate, COALESCE(PAADM_DischgTime, PAADM_EstimDischargeTime) AS encounterPeriodEndTime, NULL AS encounterParticipantIndividualCode_opattending, NULL AS encounterParticipantIndividualDisplay_opattending, PAADM_AdmSrc_DR->ADSOU_Code AS encounterHospitalizationAdmitsourceCodingCode, PAADM_AdmSrc_DR->ADSOU_Desc AS encounterHospitalizationAdmitsourceCodingDesc, CASE PAADM_Type WHEN ''I'' THEN PAADM_MainMRADM_DR->MRADM_DischDestin_DR->DDEST_Code ELSE NULL END AS encounterHospitalizationDischargedispositionCodingCode, CASE PAADM_Type WHEN ''I'' THEN PAADM_MainMRADM_DR->MRADM_DischDestin_DR->DDEST_Desc ELSE NULL END AS encounterHospitalizationDischargedispositionCodingDesc, PAADM_AdmMethod_DR->ADMETH_Code AS encounterAdmissionmethodCodingCode, PAADM_AdmMethod_DR->ADMETH_Desc AS encounterAdmissionmethodCodingDesc, CASE PAADM_Type WHEN ''I'' THEN PAADM_MainMRADM_DR->MRADM_ConditAtDisch_DR->DISCON_Code ELSE NULL END AS encounterDischargemethodCodingCode, CASE PAADM_Type WHEN ''I'' THEN PAADM_MainMRADM_DR->MRADM_ConditAtDisch_DR->DISCON_Desc ELSE NULL END AS encounterDischargemethodCodingDesc, PAADM_PAPMI_DR->PAPMI_No AS subjectReference, PAADM_VisitStatus AS encounterStatus, PAADM_UpdateDate AS lastUpdateDate, PAADM_UpdateTime AS lastUpdateTime FROM PA_Adm WHERE PAADM_Type IN (''I'', ''E'') AND PAADM_PAPMI_DR->PAPMI_No IS NOT NULL "
					)
					.concat(
						secondParams,
						"')) SELECT encounterIdentifier, encounterStatusMapped, encounterStatus, encounterClassDesc, encounterClassCode, CASE WHEN ISNUMERIC(encounterTypeCode) <> 1 THEN NULL ELSE UPPER(encounterTypeDesc) END AS encounterTypeDesc, CASE WHEN ISNUMERIC(encounterTypeCode) <> 1 THEN NULL ELSE encounterTypeCode END AS encounterTypeCode, CONCAT(COALESCE(encounterPeriodStartDate, ''), 'T', COALESCE(encounterPeriodStartTime, '')) AS encounterPeriodStart, CONCAT(COALESCE(encounterPeriodEndDate, ''), 'T', COALESCE(encounterPeriodEndTime, '')) AS encounterPeriodEnd, encounterParticipantIndividualCode_opattending, encounterParticipantIndividualDisplay_opattending, wards.admissionWardCode AS encounterLocation1Identifier, UPPER(wards.admissionWardDesc) AS encounterLocation1Display, wards.dischargeWardCode AS encounterLocation2Identifier, UPPER(wards.dischargeWardDesc) AS encounterLocation2Display, consultants.admissionConsultantCode AS encounterParticipantIndividualCode_admitting, consultants.admissionConsultantDesc AS encounterParticipantIndividualDisplay_admitting, consultants.admissionConsultantSpecCode AS encounterTypeCodeAdm, consultants.admissionConsultantSpecDesc AS encounterTypeDescAdm, consultants.dischargeConsultantCode AS encounterParticipantIndividualCode_discharging, consultants.dischargeConsultantDesc AS encounterParticipantIndividualDisplay_discharging, consultants.dischargeConsultantSpecCode AS encounterTypeCodeDis, consultants.dischargeConsultantSpecDesc AS encounterTypeDescDis, encounterHospitalizationAdmitsourceCodingCode, encounterHospitalizationAdmitsourceCodingDesc, encounterHospitalizationDischargedispositionCodingCode, encounterHospitalizationDischargedispositionCodingDesc, encounterAdmissionmethodCodingCode, encounterAdmissionmethodCodingDesc, encounterDischargemethodCodingCode, encounterDischargemethodCodingDesc, subjectReference, CONCAT(COALESCE(lastUpdateDate, ''), 'T', COALESCE(lastUpdateTime, '')) AS lastUpdated FROM encounter_CTE LEFT JOIN (SELECT * FROM (SELECT REPLACE(PAADM_ADMNo, '/', '-') AS PAADM_ADMNo, dischargeConsultantCode, dischargeConsultantDesc, dischargeConsultantSpecCode, dischargeConsultantSpecDesc, admissionConsultantCode, admissionConsultantDesc, admissionConsultantSpecCode, admissionConsultantSpecDesc, row_number() OVER (PARTITION BY PAADM_ADMNo ORDER BY TRANS_ChildSub)\tAS transOrder FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT TRANS_ParRef->PAADM_AdmDocCodeDR->CTPCP_Code AS dischargeConsultantCode, TRANS_ParRef->PAADM_AdmDocCodeDR->CTPCP_Desc AS dischargeConsultantDesc, TRANS_ParRef->PAADM_AdmDocCodeDR->CTPCP_Spec_DR->CTSPC_Code dischargeConsultantSpecCode, TRANS_ParRef->PAADM_AdmDocCodeDR->CTPCP_Spec_DR->CTSPC_Desc dischargeConsultantSpecDesc, TRANS_CTCP_DR->CTPCP_Code AS admissionConsultantCode, TRANS_CTCP_DR->CTPCP_Desc AS admissionConsultantDesc, TRANS_CTCP_DR->CTPCP_Spec_DR->CTSPC_Code admissionConsultantSpecCode, TRANS_CTCP_DR->CTPCP_Spec_DR->CTSPC_Desc admissionConsultantSpecDesc, TRANS_ChildSub, TRANS_ParRef->PAADM_ADMNo FROM PA_AdmTransaction WHERE TRANS_ParRef->PAADM_Type = ''I'' AND TRANS_ParRef->PAADM_Epissubtype_DR->SUBT_Code = ''1'' AND TRANS_CTCP_DR IS NOT NULL "
					)
					.concat(
						thirdParams,
						"')) a WHERE transOrder = 1) consultants ON encounter_CTE.encounterIdentifier = consultants.PAADM_ADMNo LEFT JOIN (SELECT * FROM (SELECT REPLACE(PAADM_ADMNo, '/', '-') AS PAADM_ADMNo, dischargeWardCode, dischargeWardDesc, admissionWardCode, admissionWardDesc, row_number() OVER (PARTITION BY PAADM_ADMNo ORDER BY TRANS_ChildSub)\tAS transOrder FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT TRANS_ParRef->PAADM_CurrentWard_DR->WARD_Code AS dischargeWardCode, TRANS_ParRef->PAADM_CurrentWard_DR->WARD_Desc AS dischargeWardDesc, TRANS_Ward_DR->WARD_Code AS admissionWardCode, TRANS_Ward_DR->WARD_Desc AS admissionWardDesc, TRANS_ChildSub, TRANS_ParRef->PAADM_ADMNo FROM PA_AdmTransaction WHERE TRANS_ParRef->PAADM_Type = ''I'' AND TRANS_ParRef->PAADM_Epissubtype_DR->SUBT_Code = ''1'' AND TRANS_Ward_DR IS NOT NULL "
					)
					.concat(
						thirdParams,
						"')) a WHERE transOrder = 1) wards ON encounter_CTE.encounterIdentifier = wards.PAADM_ADMNo "
					)
					.concat(fourthParams, ";")
			);

		case "flag":
			return executeCachedQuery(
				"WITH flag_CTE AS (SELECT DISTINCT flagId, CASE ALM_Status WHEN 'I' THEN 'inactive' WHEN 'A' THEN 'active' END AS flagStatusCode, flagCategoryCodingDisplay, flagCategoryCodingCode, flagCodeCodingDisplay, flagCodeCodingCode, flagCodeText, flagSubjectReference, CONCAT(COALESCE(periodStartDate, ''),'T', COALESCE(periodStartTime, '')) AS periodStart, CONCAT(COALESCE(periodEndDate, ''),'T00:00:00') AS periodEnd FROM OPENQUERY( [ENYH-PRD-ANALYTICS], 'SELECT DISTINCT ALM_Status, COALESCE(ALM_OnsetDate, ALM_CreateDate) AS periodStartDate, COALESCE(ALM_OnsetTime, ALM_CreateTime) AS periodStartTime, COALESCE(ALM_ExpiryDate, ALM_ClosedDate) AS periodEndDate, ALM_ClosedTime AS periodEndTime,     REPLACE(alert.ALM_RowID, ''||'', ''-'') AS flagId, alert.ALM_Alert_DR->ALERT_Desc AS flagCodeCodingDisplay, alert.ALM_Alert_DR->ALERT_Code AS flagCodeCodingCode, alert.ALM_Message AS flagCodeText, alert.ALM_AlertCategory_DR->ALERTCAT_Desc AS flagCategoryCodingDisplay, alert.ALM_AlertCategory_DR->ALERTCAT_Code AS flagCategoryCodingCode, alert.ALM_PAPMI_ParRef->PAPMI_No AS flagSubjectReference FROM PA_AlertMsg alert WHERE alert.ALM_Alert_DR IS NOT NULL "
					.concat(
						firstParams,
						"')) SELECT flag_CTE.*, snom.SNOMED_Code AS flagCodeCodingSnomedCode, snom.SNOMED_Display AS flagCodeCodingSnomedDisplay, CASE WHEN periodEnd > periodStart THEN periodEnd ELSE periodStart END AS lastUpdated FROM flag_CTE LEFT JOIN lookup.dbo.ydh_alert_list AS snom WITH (NOLOCK) ON flag_CTE.flagCodeCodingCode = snom.YDH_TrakCare_Code WHERE flagStatusCode IS NOT NULL "
					)
					.concat(secondParams, ";")
			);

		case "medicationstatement":
			return executeCachedQuery(
				"WITH medicationStatement_CTE AS (SELECT DISTINCT medstatId, COALESCE(medStatContextEncounterReferenceIPEmerg, medStatContextEncounterReferenceOutpatient) AS medStatContextEncounterReference, encounterClassDesc, CONCAT(COALESCE(medstatDateassertedDate, ''),'T', COALESCE(medstatDateassertedTime, '')) AS medstatDateasserted, CONCAT(COALESCE(medstatEffectiveStart_Datepart, ''),'T', COALESCE(medstatEffectiveStart_Timepart, '')) AS medstatEffectiveStart, CONCAT(COALESCE(medstatEffectiveEnd_Datepart, ''),'T', COALESCE(medstatEffectiveEnd_Timepart, '')) AS medstatEffectiveEnd, medstatSubjectReference, medstatDosageTimingRepeatDuration, CASE medstatDosageTimingRepeatDurationUnit WHEN 'DO' THEN NULL WHEN 'M' THEN 'min' WHEN 'H' THEN 'h' WHEN 'D' THEN 'd' WHEN 'W' THEN 'wk' END AS medstatDosageTimingRepeatDurationUnit, medstatDosagePatientinstruction, medstatDosageRouteText, medstatDosageDoseQuantityValue, medstatDosageDoseQuantityUnit, CASE WHEN (CURRENT_TIMESTAMP BETWEEN CAST(medstatEffectiveStart_Datepart AS DATETIME) + CAST(medstatEffectiveStart_Timepart AS DATETIME) AND CAST(medstatEffectiveEnd_Datepart AS DATETIME) + CAST(medstatEffectiveEnd_Timepart AS DATETIME)) AND orderItemStatus != 'Discontinued' THEN 'active' WHEN CURRENT_TIMESTAMP < CAST(medstatEffectiveStart_Datepart AS DATETIME) + CAST(medstatEffectiveStart_Timepart AS DATETIME) AND orderItemStatus != 'Discontinued' THEN 'intended' WHEN CURRENT_TIMESTAMP > CAST(medstatEffectiveEnd_Datepart AS DATETIME) + CAST(medstatEffectiveEnd_Timepart AS DATETIME) AND orderItemStatus != 'Discontinued' THEN 'completed' WHEN orderItemStatus = 'Discontinued' AND orderItemVariance IN ('DATA', 'ERROR') THEN 'entered-in-error' WHEN orderItemStatus = 'Discontinued' THEN 'stopped' END AS medstatStatusCode, medicationId, medicationCodeText, medicationCodeCodingDisplay, medicationCodeCodingCode, CONCAT(COALESCE(lastUpdateDate, ''), 'T', COALESCE(lastUpdateTime, '')) AS lastUpdated FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT DISTINCT REPLACE(oi.OEORI_RowID, ''||'', ''-'') AS medstatId, REPLACE(adm.PAADM_ADMNo, ''/'', ''-'') AS medStatContextEncounterReferenceIPEmerg, REPLACE(oi.OEORI_APPT_DR->APPT_RowId, ''||'', ''-'') AS medStatContextEncounterReferenceOutpatient, oi.OEORI_Date AS medstatDateassertedDate, oi.OEORI_TimeOrd AS medstatDateassertedTime, oi.OEORI_SttDat AS medstatEffectiveStart_Datepart, oi.OEORI_SttTim AS medstatEffectiveStart_Timepart, oi.OEORI_EndDate AS medstatEffectiveEnd_Datepart, oi.OEORI_EndTime AS medstatEffectiveEnd_Timepart, oi.OEORI_OEORD_ParRef->OEORD_Adm_DR->PAADM_PAPMI_DR->PAPMI_No AS medstatSubjectReference, oi.OEORI_OEOrdItem2_DR->ITM2_DurationValue AS medstatDosageTimingRepeatDuration, oi.OEORI_OEOrdItem2_DR->ITM2_DurationUnit AS medstatDosageTimingRepeatDurationUnit, oi.OEORI_Instr_DR->PHCIN_Desc1 AS medstatDosagePatientinstruction, oi.OEORI_AdminRoute_DR->ADMR_Desc AS medstatDosageRouteText, oi.OEORI_DoseQty AS medstatDosageDoseQuantityValue, oi.OEORI_Unit_DR->CTUOM_Desc AS medstatDosageDoseQuantityUnit, oi.OEORI_ItemStat_DR->OSTAT_Desc AS orderItemStatus, oi.OEORI_VarianceReason_DR->VR_Code AS orderItemVariance, oi.OEORI_UpdateDate AS lastUpdateDate, oi.OEORI_UpdateTime AS lastUpdateTime, NULL AS RESOURCE_LINEBREAK, oi.OEORI_ItmMast_DR->ARCIM_Abbrev AS medicationCodeText, REPLACE(oi.OEORI_ItmMast_DR->ARCIM_RowID, ''||'', ''-'') AS medicationId, arcex.EXT_Desc AS medicationCodeCodingDisplay, arcex.EXT_Code AS medicationCodeCodingCode, CASE WHEN adm.PAADM_Type = ''I'' THEN ''inpatient'' WHEN adm.PAADM_Type = ''E'' THEN ''emergency'' WHEN oi.OEORI_APPT_DR->APPT_RowId IS NOT NULL THEN ''outpatient'' END as encounterClassDesc FROM OE_OrdItem oi LEFT JOIN ARC_ItmMast arc ON oi.OEORI_ItmMast_DR = arc.ARCIM_RowId LEFT JOIN ARC_ItemExternalCodes arcex ON arc.ARCIM_RowId = arcex.EXT_ParRef AND arcex.EXT_HL7SendingFacility = ''FDB'' AND arcex.EXT_HL7SendingApp IN (''AMPP'', ''VMPP'') LEFT JOIN OE_ORDER ord ON oi.OEORI_OEORD_ParRef = ord.OEORD_RowId LEFT JOIN PA_ADM adm ON ord.OEORD_Adm_DR = adm.PAADM_RowId AND adm.PAADM_Type IN (''I'', ''E'') WHERE oi.OEORI_Categ_DR->ORCAT_Desc IN (''PHARMACY'', ''PHARM'') "
					.concat(
						firstParams,
						"')) SELECT * FROM medicationStatement_CTE WHERE medstatStatusCode IS NOT NULL "
					)
					.concat(secondParams, ";")
			);

		case "patient":
			return executeCachedQuery(
				"SELECT nhsNumber, nhsNumberTraceStatusDesc, nhsNumberTraceStatusCode, patient.patientNo, active, ethnicCategoryCode, ethnicCategoryDesc, ethnic.CareConnect_Code AS ethnicCategoryCareConnectCode, ethnic.CareConnect_Display AS ethnicCategoryCareConnectDesc, UPPER(religiousAffiliationCode) AS religiousAffiliationCode, religiousAffiliationDesc, homePhone, businessPhone, mobilePhone, appointmentSMS, email, preferredContactMethod, preferredLanguageCode, preferredLanguageDesc, interpreterRequired, nameFamily, nameGiven1First, nameGiven2Middle, namePrefix, maritalStatusDesc, maritalStatusCode, addressLine1, addressLine2, city, district, postalCode, gender, birthdate, deceased, gpDesc, gpAddressLine1, gpAddressLine2, gpCity, gpPostalCode, gpIdentifier, contactName, contactPhone, contactText, dnd.DND, school.schoolName, school.schoolId, school.schoolPhone, CONCAT(COALESCE(lastUpdateDate, ''), 'T', COALESCE(lastUpdateTime, '')) AS lastUpdated FROM OPENQUERY( [ENYH-PRD-ANALYTICS], 'SELECT patmas.PAPMI_PAPER_DR->PAPER_ID AS nhsNumber, patmas.PAPMI_TraceStatus_DR->TRACE_Desc AS nhsNumberTraceStatusDesc, patmas.PAPMI_TraceStatus_DR AS nhsNumberTraceStatusCode, patmas.PAPMI_No AS patientNo, patmas.PAPMI_Active, CASE WHEN patmas.PAPMI_Active IS NULL THEN ''true'' WHEN patmas.PAPMI_Active = ''Y'' THEN ''true'' ELSE NULL END AS active, patmas.PAPMI_PAPER_DR->PAPER_IndigStat_DR->INDST_Desc AS ethnicCategoryDesc, patmas.PAPMI_PAPER_DR->PAPER_IndigStat_DR->INDST_Code AS ethnicCategoryCode, patmas.PAPMI_PAPER_DR->PAPER_Religion_DR->CTRLG_Desc AS religiousAffiliationDesc, patmas.PAPMI_PAPER_DR->PAPER_Religion_DR->CTRLG_Code AS religiousAffiliationCode, patmas.PAPMI_PAPER_DR->PAPER_TelH AS homePhone, patmas.PAPMI_PAPER_DR->PAPER_TelO AS businessPhone, patmas.PAPMI_PAPER_DR->PAPER_MobPhone AS mobilePhone, patmas.PAPMI_PAPER_DR->PAPER_AppointmentSMS AS appointmentSMS, patmas.PAPMI_PAPER_DR->PAPER_Email AS Email, patmas.PAPMI_PAPER_DR->PAPER_PreferredContactMethod AS preferredContactMethod, patmas.PAPMI_PAPER_DR->PAPER_PrefLanguage_DR->PREFL_Desc AS preferredLanguageDesc, patmas.PAPMI_PAPER_DR->PAPER_PrefLanguage_DR->PREFL_Code AS preferredLanguageCode, patmas.PAPMI_PAPER_DR->PAPER_InterpreterRequired AS interpreterRequired, patmas.PAPMI_PAPER_DR->PAPER_UpdateDate AS lastUpdateDate, patmas.PAPMI_PAPER_DR->PAPER_UpdateTime AS lastUpdateTime, patmas.PAPMI_PAPER_DR->PAPER_Name AS nameFamily, patmas.PAPMI_PAPER_DR->PAPER_Name2 AS nameGiven1First, patmas.PAPMI_PAPER_DR->PAPER_Name3 AS nameGiven2Middle, patmas.PAPMI_PAPER_DR->PAPER_Title_DR->TTL_Desc AS nameprefix, patmas.PAPMI_PAPER_DR->PAPER_NokName AS contactName, patmas.PAPMI_PAPER_DR->PAPER_NokPhone AS contactPhone, patmas.PAPMI_PAPER_DR->PAPER_NokText AS contactText, CASE patmas.PAPMI_PAPER_DR->PAPER_Marital_DR->CTMAR_RowId WHEN 1 THEN ''Married'' WHEN 2 THEN ''unknown'' WHEN 3 THEN ''Widowed'' WHEN 4 THEN ''unmarried'' WHEN 5 THEN ''Legally Seperated'' WHEN 6 THEN ''Divorced'' END AS maritalStatusDesc, CASE patmas.PAPMI_PAPER_DR->PAPER_Marital_DR->CTMAR_Code WHEN ''N'' THEN ''U'' ELSE patmas.PAPMI_PAPER_DR->PAPER_Marital_DR->CTMAR_Code END AS maritalStatusCode, patmas.PAPMI_PAPER_DR->PAPER_StName AS addressLine1, patmas.PAPMI_PAPER_DR->PAPER_ForeignAddress AS addressLine2, patmas.PAPMI_PAPER_DR->PAPER_CityCode_DR->CTCIT_Desc AS city, patmas.PAPMI_PAPER_DR->PAPER_CT_Province_DR->PROV_Desc AS district, patmas.PAPMI_PAPER_DR->PAPER_Zip_DR->CTZIP_Code AS postalCode, CASE patmas.PAPMI_PAPER_DR->PAPER_Sex_DR->CTSEX_RowId WHEN 1 THEN ''female'' WHEN 2 THEN ''unknown'' WHEN 3 THEN ''other'' WHEN 4 THEN ''male'' END AS gender, patmas.PAPMI_DOB AS birthDate, CASE WHEN patmas.PAPMI_PAPER_DR->PAPER_Deceased = ''Y'' THEN ''true'' WHEN patmas.PAPMI_PAPER_DR->PAPER_Deceased = ''N'' THEN NULL END AS deceased, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctor_DR->REFD_Desc AS gpDesc, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctorClinic_DR->CLN_Address1 AS gpAddressLine1, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctorClinic_DR->CLN_Address2 AS gpAddressLine2, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctorClinic_DR->CLN_City_DR->CTCIT_Desc AS gpCity, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctorClinic_DR->CLN_Zip_DR->CTZIP_Code AS gpPostalCode, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctorClinic_DR->CLN_Code AS gpIdentifier FROM PA_PatMas patmas WHERE COALESCE(PAPMI_Active,''Y'') = ''Y'' AND (patmas.PAPMI_PAPER_DR->PAPER_ID IS NOT NULL OR patmas.PAPMI_No IS NOT NULL) "
					.concat(
						firstParams,
						"') AS patient LEFT JOIN lookup.dbo.ydh_dnd AS dnd WITH (NOLOCK) ON patient.patientNo = dnd.patientNo LEFT JOIN OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT DISTINCT NOK_NonGovOrg_DR->NGO_Code AS schoolId, NOK_NonGovOrg_DR->NGO_Desc AS schoolName, NOK_NonGovOrg_DR->NGO_Phone AS schoolPhone, NOK_PAPMI_ParRef->PAPMI_No AS patientNo FROM PA_NOK WHERE NOK_Relation_DR->CTRLT_Code = ''SCH'' AND NOK_Inactive = ''N'' "
					)
					.concat(
						secondParams,
						"') AS school ON patient.patientNo = school.patientNo LEFT JOIN lookup.dbo.ydh_ethnicity_list ethnic WITH (NOLOCK) ON patient.ethnicCategoryCode = ethnic.YDH_TrakCare_Code;"
					)
			);

		default:
			break;
	}
}

/**
	Add leading zeros to string.

	@author Dave Reed
	@param {string} num - String to prepend zeros to.
	@param {number} size - Wanted size of resulting string, with zero prepended to string.
	@returns {string} - String with prepended zeros.
 */
function pad(num, size) {
	var s = "".concat(num);
	while (s.length < size) {
		s = "0".concat(s);
	}
	return s;
}
