com.mirth.connect.plugins.javascriptstep.script:
var interaction = sourceMap.get('fhirInteraction');

if (interaction == 'operation') {
	// Operation destinations will have a name of "$name".
	destinationSet.removeAllExcept([sourceMap.get('fhirOperationName')]);
} else if (interaction.startsWith('history')) {
	// This will match history-system, history-type, and history-instance
	destinationSet.removeAllExcept(['history']);
} else if (interaction.startsWith('search')) {
	// This will match search-system and search-type
	destinationSet.removeAllExcept(['search']);
} else {
	// All other destinations should have a name equal to the interaction
	destinationSet.removeAllExcept([interaction]);
}

properties.script:
/**
 * @author Frazer Smith
 * @description Rewritten example FHIR read destination to be usable for TrakCare calls.
 */
try {
	var type = $('fhirType').toLowerCase();
	var id = $('fhirId'); // Build up WHERE clause then pass to buildResourceQuery to be called

	var wherePredicate;

	switch (''.concat(type)) {
		case 'allergyintolerance':
			wherePredicate = [
				"(REPLACE(alle.ALG_RowId, ''||'', ''-'') = ''".concat(id, "'')")
			];
			break;

		case 'condition':
			break;

		case 'documentreference':
			break;

		case 'encounter':
			wherePredicate = [
				"(REPLACE(app.APPT_RowId, ''||'', ''-'') = ''".concat(
					id,
					"'')"
				),
				"(REPLACE(PAADM_ADMNo, ''/'', ''-'') = ''".concat(id, "'')"),
				"(REPLACE(TRANS_ParRef->PAADM_ADMNo, ''/'', ''-'') = ''".concat(
					id,
					"'')"
				)
			];
			break;

		case 'medicationstatement':
			wherePredicate = [
				"(REPLACE(oi.OEORI_RowID, ''||'', ''-'') = ''".concat(
					id,
					"'')"
				),
				''
			];
			break;

		case 'patient':
			wherePredicate = ["(patmas.PAPMI_No = ''".concat(id, "'')")];
			break;

		default:
			break;
	}

	var result = buildResourceQuery(type, wherePredicate);

	if (result.next()) {
		// Pass it out to external channel that will transform into
		// Care Connect FHIR Resource and return
		var data;

		switch (''.concat(type)) {
			case 'allergyintolerance':
				data = buildAllergyIntoleranceResource(result);
				break;

			case 'condition':
				// data = buildConditionResource(result);
				break;

			case 'documentreference':
				// data = buildDocumentReferenceResource(result);
				break;

			case 'encounter':
				data = buildEncounterResource(result);
				break;

			case 'flag':
				date = buildFlagResource(result);
				break;

			case 'medicationstatement':
				data = buildMedicationStatementResource(result);
				break;

			case 'patient':
				data = buildPatientResource(result);
				break;

			default:
				break;
		} // Hard coded version as we don't keep past versions of records, only one

		var version = '1';
		var lastModified = new Date(getResultSetString(result, 'lastUpdated'));
		var response = FhirResponseFactory.getReadResponse(
			JSON.stringify(data),
			version,
			lastModified,
			200,
			'application/fhir+json'
		);
		responseMap.put('response', response);
		return response.getMessage();
	}

	return createOperationOutcome(
		'error',
		'processing',
		''.concat($('fhirType'), ' ID ').concat(id, ' not found.'),
		404
	);
} catch (error) {
	return createOperationOutcome(
		'error',
		'transient',
		'Error reading resource.',
		500,
		e
	);
}

try {
	var type = $('fhirType').toLowerCase();
	var id = $('fhirId');
	var versionId = $('fhirVid');

	var result = getResourceVersion(type, id, versionId);

	if (result.next()) {
		var data = getResultSetString(result, 'data');
		var contentType = getResultSetString(result, 'mimetype');
		var lastModified = getResultSetDate(result, 'last_modified');
		var deleted = result.getBoolean('deleted');

		if (deleted) {
			return createOperationOutcome('error', 'processing', $('fhirType') + ' ID ' + id + ' at version ' + versionId + ' has been deleted.', 410);
		} else {
			var response = FhirResponseFactory.getVreadResponse(data, lastModified, 200, contentType);
			responseMap.put('response', response);
			return response.getMessage();
		}
	} else {
		return createOperationOutcome('error', 'processing', $('fhirType') + ' ID ' + id + ' at version ' + versionId + ' not found.', 404);
	}
} catch (e) {
	return createOperationOutcome('error', 'transient', 'Error reading resource.', 500, e);
}

/**
 * @author Frazer Smith
 * @description Rewritten example FHIR search destination to be usable for TrakCare calls.
 * Mirth Connect supports ES6 to a degree but not template literals and const/let.
 */
try {
	var type = $('fhirType').toLowerCase(); // Append URL with forward slash and build Java URL object

	var requestURL = $('url');

	if (!requestURL.endsWith('/')) {
		requestURL += '/';
	}

	var bundle = buildBundleResource(new java.net.URI(requestURL));
	var whereArray = [];
	var whereParts = [];
	var supportedTypeParams = {
		allergyintolerance: ['clinical-status', 'date', 'patient'],
		condition: ['asserted-date', 'category', 'clinical-status', 'patient'],
		encounter: ['date', 'patient'],
		flag: ['date', 'patient', 'status'],
		medicationstatement: ['effective', 'patient', 'status'],
		patient: [
			'birthdate',
			'family',
			'gender',
			'given',
			'identifier',
			'name'
		]
	}; // If any param not supported, reject request

	Object.keys($('parameters')).forEach(function(key) {
		if (
			supportedTypeParams[type.toLowerCase()].indexOf(
				''.concat(key.toLowerCase())
			) < 0
		) {
			return createOperationOutcome(
				'error',
				'invalid',
				'Unknown or unsupported parameter '.concat(key, '.')
			);
		}
	});
	/**
	 * ================================
	 * AllergyIntolerance search params
	 * ================================
	 */

	if (type == 'allergyintolerance') {
		// GET [baseUrl]/AllergyIntolerance?patient=[id]&clinical-status=[code]
		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('clinical-status')
		) {
			var clinicalStatus = $('parameters').getParameter(
				'clinical-status'
			);
			var clinicalStatusCode = {
				active: 'A',
				inactive: 'I',
				resolved: 'R'
			};
			clinicalStatus = clinicalStatusCode[clinicalStatus.toLowerCase()];
			whereParts.push(
				"(alle.ALG_Status = ''".concat(clinicalStatus, "'')")
			);
		} // GET [baseUrl]/AllergyIntolerance?patient=[id]&date=[date]

		if ($('parameters').contains('date')) {
			// Loop through each date param and build SQL WHERE clause
			$('parameters')
				.getParameterList('date')
				.toArray()
				.forEach(function(paramDate) {
					date = paramDate;
					date += '';
					var operator = convertFhirParameterOperator(
						date.substring(0, 2)
					); // eslint-disable-next-line no-restricted-globals

					if (isNaN(date.substring(0, 2))) {
						date = date.substring(2, date.length);
					}

					whereParts.push(
						'(alle.ALG_Date '
							.concat(operator, " ''")
							.concat(date, "'')")
					);
				});
		} // GET [baseUrl]/AllergyIntolerance?patient=[id]

		if ($('parameters').contains('patient')) {
			whereParts.push(
				"(alle.ALG_PAPMI_ParRef->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			);
		} // GET [baseUrl]/AllergyIntolerance?patient.identifier=[system]|[code]

		if ($('parameters').contains('patient.identifier')) {
			if (
				$('parameters')
					.getParameter('patient.identifier')
					.contains('|')
			) {
				var allergyPatIdParam = String(
					$('parameters').getParameter('patient.identifier')
				).split('|');

				if (
					allergyPatIdParam[0] == 'https://fhir.nhs.uk/Id/nhs-number'
				) {
					whereArray[0].push(
						"(alle.ALG_PAPMI_ParRef->PAPMI_ID = ''".concat(
							allergyPatIdParam[1],
							"'')"
						)
					);
				}

				if (
					allergyPatIdParam[0] ===
					'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				) {
					whereArray[0].push(
						"(alle.ALG_PAPMI_ParRef->PAPMI_No = ''".concat(
							allergyPatIdParam[1],
							"'')"
						)
					);
				}
			}
		}

		whereArray.push(whereParts);
	}
	/**
	 * =======================
	 * Condition search params
	 * =======================
	 */

	if (type == 'condition') {
		// GET [baseUrl]/Condition?patient=[id]&asserted-date=[date]
		if ($('parameters').contains('asserted-date')) {
			whereParts.push('');
		} // GET [baseUrl]/Condition?patient=[id]&category=[code]

		if ($('parameters').contains('category')) {
			whereParts.push('');
		} // GET [baseUrl]/Condition?patient=[id]&clinical-status=[code]

		if ($('parameters').contains('clinical-status')) {
			whereParts.push('');
		}
		/**
		 * GET [baseUrl]/Condition?patient.identifier=[system]|[code]
		 * GET [baseUrl]/Condition?patient=[id]
		 */

		if ($('parameters').contains('patient')) {
			whereParts.push('');
		}
	}
	/**
	 * =======================
	 * Encounter search params
	 * =======================
	 */

	if (type == 'encounter') {
		// Turn array into multi-dimensional one to allow for three seperate WHERE clauses to be built
		whereArray = [[], [], []]; // GET [baseUrl]/Encounter?patient=[id]

		if ($('parameters').contains('patient')) {
			// Build where clause for first query (outpats) in union
			whereArray[0].push(
				"(app.APPT_Adm_DR->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			); // Build where clause for second query (inpats, emerg) in union

			whereArray[1].push(
				"(PAADM_PAPMI_DR->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			); // Build where clause for subquery for inpat consultants

			whereArray[2].push(
				"(TRANS_ParRef->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			);
		} // GET [baseUrl]/Encounter?patient.identifier=[system]|[code]

		if ($('parameters').contains('patient.identifier')) {
			if (
				$('parameters')
					.getParameter('patient.identifier')
					.contains('|')
			) {
				var encounterPatIdParam = String(
					$('parameters').getParameter('patient.identifier')
				).split('|');

				if (
					encounterPatIdParam[0] ===
					'https://fhir.nhs.uk/Id/nhs-number'
				) {
					whereArray[0].push(
						"(app.APPT_Adm_DR->PAADM_PAPMI_DR->PAPMI_ID = ''".concat(
							encounterPatIdParam[1],
							"'')"
						)
					);
					whereArray[1].push(
						"(PAADM_PAPMI_DR->PAPMI_ID = ''".concat(
							encounterPatIdParam[1],
							"'')"
						)
					);
					whereArray[2].push(
						"(TRANS_ParRef->PAADM_PAPMI_DR->PAPMI_ID = ''".concat(
							encounterPatIdParam[1],
							"'')"
						)
					);
				}

				if (
					encounterPatIdParam[0] ===
					'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				) {
					whereArray[0].push(
						"(app.APPT_Adm_DR->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
							encounterPatIdParam[1],
							"'')"
						)
					);
					whereArray[1].push(
						"(PAADM_PAPMI_DR->PAPMI_No = ''".concat(
							encounterPatIdParam[1],
							"'')"
						)
					);
					whereArray[2].push(
						"(TRANS_ParRef->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
							encounterPatIdParam[1],
							"'')"
						)
					);
				}
			}
		} // GET [baseUrl]/Encounter?patient=[id]&date=[date]

		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('date')
		) {
			// Loop through each date param and build SQL WHERE clause
			$('parameters')
				.getParameterList('date')
				.toArray()
				.forEach(function(paramDate) {
					date = paramDate;
					date += '';
					var operator = convertFhirParameterOperator(
						date.substring(0, 2)
					); // eslint-disable-next-line no-restricted-globals

					if (isNaN(date.substring(0, 2))) {
						date = date.substring(2, date.length);
					} // Build where clause for first query (outpats) in union

					whereArray[0].push(
						'(COALESCE(app.APPT_ArrivalDate, app.APPT_DateComp) '
							.concat(operator, " ''")
							.concat(date, "'')")
					); // Build where clause for second query (inpats, emerg) in union

					whereArray[1].push(
						'(PAADM_AdmDate'
							.concat(operator, " ''")
							.concat(date, "'')")
					);
				});
		} // GET [baseUrl]/Encounter?patient=[id]&class=[token]

		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('class')
		) {
			// Loop through each class param and build SQL WHERE clause
			$('parameters')
				.getParameterList('class')
				.toArray()
				.forEach(function(paramclass) {
					var classCode = {
						inpatient: 'I',
						outpatient: 'AMB',
						emergency: 'E'
					}; // Build where clause for first query (outpats) in union

					whereArray[0].push(
						"(''AMB'' = ''".concat(
							classCode[paramclass.toLowerCase()],
							"'')"
						)
					); // Build where clause for second query (inpats, emerg) in union

					whereArray[1].push(
						"(PAADM_Type = ''".concat(
							classCode[paramclass.toLowerCase()],
							"'')"
						)
					);
				});
		} // GET [baseUrl]/Encounter?patient=[id]&type=[code]

		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('type')
		) {
			// Loop through each type param and build SQL WHERE clause
			$('parameters')
				.getParameterList('type')
				.toArray()
				.forEach(function(paramType) {
					// Build where clause for first query (outpats) in union
					whereArray[0].push(
						"(app.APPT_AS_ParRef->AS_RES_ParRef->RES_CTLOC_DR->CTLOC_Code = ''".concat(
							paramType,
							"'')"
						)
					); // Build where clause for second query (inpats, emerg) in union

					whereArray[1].push(
						"(PAADM_DepCode_DR->CTLOC_Code = ''".concat(
							paramType,
							"'')"
						)
					);
				});
		}
	}
	/**
	 * =======================
	 * Flag search params
	 * =======================
	 */

	if (type == 'flag') {
		// Turn array into multi-dimensional one to allow for two seperate WHERE clauses to be built
		whereArray = [[], []]; // GET [baseUrl]/Flag?patient=[id]

		if ($('parameters').contains('patient')) {
			whereArray[0].push(
				"(alert.ALM_PAPMI_ParRef->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			);
		} // GET [baseUrl]/Flag?patient.identifier=[system]|[code]

		if ($('parameters').contains('patient.identifier')) {
			if (
				$('parameters')
					.getParameter('patient.identifier')
					.contains('|')
			) {
				var flagPatIdParam = String(
					$('parameters').getParameter('patient.identifier')
				).split('|');

				if (flagPatIdParam[0] == 'https://fhir.nhs.uk/Id/nhs-number') {
					whereArray[0].push(
						"(alert.ALM_PAPMI_ParRef->PAPMI_Id = ''".concat(
							flagPatIdParam[1],
							"'')"
						)
					);
				}

				if (
					flagPatIdParam[0] ===
					'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				) {
					whereArray[0].push(
						"(alert.ALM_PAPMI_ParRef->PAPMI_No = ''".concat(
							flagPatIdParam[1],
							"'')"
						)
					);
				}
			}
		} // GET [baseUrl]/Flag?patient=[id]&status=[code]

		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('status')
		) {
			whereArray[1].push(
				"(flagStatusCode = ''".concat(
					$('parameters').getParameter('status'),
					"'')"
				)
			);
		}
	}
	/**
	 * =================================
	 * MedicationStatement search params
	 * =================================
	 */

	if (type == 'medicationstatement') {
		// Turn array into multi-dimensional one to allow for two seperate WHERE clauses to be built
		whereArray = [[], []]; // GET [baseUrl]/MedicationStatement?patient=[id]&effective=[date]

		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('effective')
		) {
			// Loop through each date param and build SQL WHERE clause
			$('parameters')
				.getParameterList('effective')
				.toArray()
				.forEach(function(paramDate) {
					date = paramDate;
					date += '';
					var operator = convertFhirParameterOperator(
						date.substring(0, 2)
					); // eslint-disable-next-line no-restricted-globals

					if (isNaN(date.substring(0, 2))) {
						date = date.substring(2, date.length);
					}

					whereArray[1].push(
						'(medstatEffectiveStart '
							.concat(operator, " ''")
							.concat(date, "'')")
					);
				});
		} // GET [baseUrl]/MedicationStatement?patient=[id]

		if ($('parameters').contains('patient')) {
			whereArray[0].push(
				"(oi.OEORI_OEORD_ParRef->OEORD_Adm_DR->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
					$('parameters').getParameter('patient'),
					"'')"
				)
			);
		} // GET [baseUrl]/MedicationStatement?patient.identifier=[system]|[code]

		if ($('parameters').contains('patient.identifier')) {
			if (
				$('parameters')
					.getParameter('patient.identifier')
					.contains('|')
			) {
				var medStatPatIdParam = String(
					$('parameters').getParameter('patient.identifier')
				).split('|');

				if (
					medStatPatIdParam[0] == 'https://fhir.nhs.uk/Id/nhs-number'
				) {
					whereArray[0].push(
						"(oi.OEORI_OEORD_ParRef->OEORD_Adm_DR->PAADM_PAPMI_DR->PAPMI_ID = ''".concat(
							medStatPatIdParam[1],
							"'')"
						)
					);
				}

				if (
					medStatPatIdParam[0] ===
					'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				) {
					whereArray[0].push(
						"(oi.OEORI_OEORD_ParRef->OEORD_Adm_DR->PAADM_PAPMI_DR->PAPMI_No = ''".concat(
							medStatPatIdParam[1],
							"'')"
						)
					);
				}
			}
		} // GET [baseUrl]/MedicationStatement?patient=[id]&status=[code]

		if (
			($('parameters').contains('patient') ||
				$('parameters').contains('patient.identifier')) &&
			$('parameters').contains('status')
		) {
			whereArray[1].push(
				"(medstatStatusCode = ''".concat(
					$('parameters').getParameter('status'),
					"'')"
				)
			);
		}
	}
	/**
	 * =====================
	 * Patient search params
	 * =====================
	 */

	if (type == 'patient') {
		// GET [baseUrl]/Patient?birthdate=[date]
		if ($('parameters').contains('birthdate')) {
			whereParts.push(
				"(patmas.PAPMI_DOB = ''".concat(
					$('parameters').getParameter('birthdate'),
					"'')"
				)
			);
		} // GET [baseUrl]/Patient?family=[family]

		if ($('parameters').contains('family')) {
			whereParts.push(
				"(patmas.PAPMI_PAPER_DR->PAPER_Name = ''".concat(
					$('parameters').getParameter('family'),
					"'')"
				)
			);
		} // GET [baseUrl]/Patient?gender=[code]

		if ($('parameters').contains('gender')) {
			whereParts.push(
				"(patmas.PAPMI_PAPER_DR->PAPER_Sex_DR->CTSEX_Desc = ''".concat(
					$('parameters').getParameter('gender'),
					"'')"
				)
			);
		} // GET [baseUrl]/Patient?given=[given]

		if ($('parameters').contains('given')) {
			whereParts.push(
				"(patmas.PAPMI_PAPER_DR->PAPER_Name2 = ''".concat(
					$('parameters').getParameter('given'),
					"'')"
				)
			);
		}
		/**
		 * GET [baseUrl]/Patient?identifier=[system]|[code]
		 * GET [baseUrl]/Patient?identifier=[code]
		 */

		if ($('parameters').contains('identifier')) {
			if (
				$('parameters')
					.getParameter('identifier')
					.contains('|')
			) {
				var identifierParam = String(
					$('parameters').getParameter('identifier')
				).split('|');

				if (identifierParam[0] == 'https://fhir.nhs.uk/Id/nhs-number') {
					whereParts.push(
						"(patmas.PAPMI_ID = ''".concat(
							identifierParam[1],
							"'')"
						)
					);
				}

				if (
					identifierParam[0] ===
					'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				) {
					whereParts.push(
						"(patmas.PAPMI_No = ''".concat(
							identifierParam[1],
							"'')"
						)
					);
				}
			} else {
				whereParts.push(
					"(patmas.PAPMI_No = ''".concat(
						$('parameters').getParameter('identifier'),
						"'')"
					)
				);
			}
		} // GET [baseUrl]/Patient?name=[name]

		if ($('parameters').contains('name')) {
			var name = $('parameters').getParameter('name');
			whereParts.push(
				"(patmas.PAPMI_PAPER_DR->PAPER_Name = ''"
					.concat(
						name,
						"'' OR patmas.PAPMI_PAPER_DR->PAPER_Name2 = ''"
					)
					.concat(name, "'')")
			);
		}

		whereArray.push(whereParts);
	} // Aggregrate all predicates in whereArray and build SQL WHERE clause from it

	var wherePredicates = [];

	for (var index = 0; index < whereArray.length; index += 1) {
		var element = whereArray[index];

		if (element.length > 0) {
			wherePredicates[index] = element.join(' AND ');
		}
	}

	if (wherePredicates.length == 0) {
		return createOperationOutcome(
			'error',
			'transient',
			'Error searching resources.',
			500,
			''
		);
	}

	logger.debug(
		'SQL WHERE clause predicate(s): '.concat(wherePredicates.toString())
	);
	var result = buildResourceQuery(type, wherePredicates);

	while (result.next()) {
		var data = void 0;

		switch (''.concat(type)) {
			case 'allergyintolerance':
				data = buildAllergyIntoleranceResource(result);
				break;

			case 'condition':
				// data = buildConditionResource(result);
				break;

			case 'documentreference':
				// data = buildDocumentReferenceResource(result);
				break;

			case 'encounter':
				data = buildEncounterResource(result);
				break;

			case 'flag':
				data = buildFlagResource(result);
				break;

			case 'medicationstatement':
				data = buildMedicationStatementResource(result);
				break;

			case 'patient':
				data = buildPatientResource(result);
				break;

			default:
				break;
		} // Add returned FHIR resources to bundle resource

		var resourceOuter = {};
		resourceOuter.resource = data;
		resourceOuter.fullUrl = ''
			.concat($cfg('apiUrl') + $('contextPath'), '/')
			.concat(data.id);
		bundle.entry.push(resourceOuter);
	}

	bundle.total = bundle.entry.length;
	bundle.link[0].url = $cfg('apiUrl') + $('uri');
	logger.debug(JSON.stringify(bundle));
	var response = FhirResponseFactory.getSearchResponse(
		JSON.stringify(bundle),
		200,
		'application/fhir+json'
	);
	responseMap.put('response', response);
	return response.getMessage();
} catch (error) {
	return createOperationOutcome(
		'error',
		'transient',
		'Error searching resources.',
		500,
		error
	);
}

channel.postprocessingScript:
// This script executes once after a message has been processed
// Responses returned from here will be stored as "Postprocessor" in the response map

// Return an OperationOutcome if no destinations or anything else already handled the response
if (!responseMap.containsKey('response')) {
	if (sourceMap.get('fhirInteraction') == 'operation') {
		createOperationOutcome('error', 'processing', 'Invalid or unsupported operation: ' + sourceMap.get('fhirOperationName'));
	} else {
		createOperationOutcome('error', 'processing', 'Invalid request URI.');
	}
}
return;

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;

channel.undeployScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data

// Close the database connection
var dbConn = $gc('dbConn');
if (dbConn != null) {
	try { dbConn.close() } catch(e) {}
	globalChannelMap.remove('dbConn');
}

return;

properties.code:
/**
	Executes a prepared query on the FHIR database and returns a CachedRowSet.

	@param {String} expression - The prepared statement to be executed.
	@param {Array} parameters - The parameters for the prepared statement.
	@param {DatabaseConnection} dbConn - Optionally provide a specific connection object.
	@return {CachedRowSet} The result of the query, as a CachedRowSet.
*/
function executeCachedQuery(expression, parameters, dbConn) {
	return executeOperation('executeCachedQuery', expression, parameters, dbConn);
}

/**
	Executes an INSERT/UPDATE statement on the database and returns a CachedRowSet containing any
	generated keys.

	@param {String} expression - The prepared statement to be executed.
	@param {Array} parameters - The parameters for the prepared statement.
	@param {DatabaseConnection} dbConn - Optionally provide a specific connection object.
	@return {CachedRowSet} A CachedRowSet containing any generated keys.
*/
function executeUpdateAndGetGeneratedKeys(expression, parameters, dbConn) {
	return executeOperation('executeUpdateAndGetGeneratedKeys', expression, parameters, dbConn);
}

/**
	Executes a prepared INSERT/UPDATE statement on the database and returns the row count.

	@param {String} expression - The prepared statement to be executed.
	@param {Array} parameters - The parameters for the prepared statement.
	@param {DatabaseConnection} dbConn - Optionally provide a specific connection object.
	@return {int} A count of the number of updated rows.
*/
function executeUpdate(expression, parameters, dbConn) {
	return executeOperation('executeUpdate', expression, parameters, dbConn);
}

/**
	Retrieves the FHIR database connection from the global channel map, creating a new one if necessary.

	@param {Boolean} recreate - If true, the connection will always be closed and created anew.
	@return {DatabaseConnection} The database connection object.
*/
function getFHIRDBConnection(recreate) {
	var dbConn = $gc('dbConn');

	if (!dbConn || recreate) {
		if (dbConn) {
			try {
				dbConn.close();
			} catch (e) {}
		}
		dbConn = createFHIRDBConnection();
		$gc('dbConn', dbConn);
	}

	return dbConn;
}

function createFHIRDBConnection() {
	return DatabaseConnectionFactory.createDatabaseConnection($('fhirDBDriver'), $('fhirDBUrl'), $('fhirDBUsername'), $('fhirDBPassword'));
}

function executeOperation(operation, expression, parameters, dbConn) {
	var createDbConn = typeof dbConn == 'undefined';
	if (createDbConn) {
		dbConn = getFHIRDBConnection();
	}
	var attempts = 0;
	var maxAttempts = NumberUtils.toInt($('fhirDBMaxRetries') + '', 0) + 1;

	while (attempts < maxAttempts) {
		attempts++;

		try {
			if (parameters) {
				for (var i in parameters) {
					var param = parameters[i];
					if (param instanceof Number) {
						parameters[i] = new java.lang.Integer(param);
					} else if (param instanceof java.util.Date) {
						parameters[i] = convertDateParameter(param);
					}
				}

				return dbConn[operation](expression, java.util.Arrays.asList(parameters));
			} else {
				return dbConn[operation](expression);
			}
		} catch (e) {
			logger.info('Error executing statement, checking if valid...');
			var throwException = false;

			try {
				dbConn.executeCachedQuery('SELECT 1');
			} catch (e2) {
				if (createDbConn) {
					logger.info('Connection invalid, recreating...');

					try {
						dbConn = getFHIRDBConnection(true);
					} catch (e3) {
						throwException = true;
					}
				} else {
					throwException = true;
				}
			}

			if (attempts >= maxAttempts) {
				throwException = true;
			}

			if (throwException) {
				var errorMessage = 'Unable to execute statement.\n';
				errorMessage += 'Expression: ' + expression + '\n';
				if (parameters) {
					errorMessage += 'Parameters: ' + java.util.Arrays.asList(parameters).toString();
				}
				logger.error(errorMessage, e.javaException);
				throw e;
			}

			java.lang.Thread.sleep(1000);
		}
	}
}

function convertDateParameter(date) {
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		return new java.sql.Timestamp(date.getTime());
	} else if (dbType == 'sqlserver') {
		return convertDate(date, 'yyyy-MM-dd HH:mm:ss.SSSZZ');
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
    Converts ResultSet into JSON object for single result.
    This makes it easier to parse for FHIR resources builder code templates.

	@author Frazer Smith
	@param {ResultSet} resultSet - The ResultSet object to retrieve from.
	@return {Object} The ResultSet as a JSON object.
*/
function getResultSet(resultSet) {
	var rsmd = resultSet.getMetaData();
	var numColumns = rsmd.getColumnCount();
	var obj = {};

	for (var index = 1; index < numColumns + 1; index++) {
		var columnName = rsmd.getColumnName(index);

		if (rsmd.getColumnType(index) == java.sql.Types.ARRAY) {
			obj[''.concat(columnName)] = resultSet.getArray(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.BIGINT) {
			obj[''.concat(columnName)] = resultSet.getInt(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.BOOLEAN) {
			obj[''.concat(columnName)] = resultSet.getBoolean(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.BLOB) {
			obj[''.concat(columnName)] = resultSet.getBlob(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.DOUBLE) {
			obj[''.concat(columnName)] = resultSet.getDouble(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.FLOAT) {
			obj[''.concat(columnName)] = resultSet.getFloat(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.INTEGER) {
			obj[''.concat(columnName)] = resultSet.getInt(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.NVARCHAR) {
			obj[''.concat(columnName)] = resultSet.getNString(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.VARCHAR) {
			obj[''.concat(columnName)] = resultSet.getString(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.TINYINT) {
			obj[''.concat(columnName)] = resultSet.getInt(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.SMALLINT) {
			obj[''.concat(columnName)] = resultSet.getInt(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.DATE) {
			obj[''.concat(columnName)] = resultSet.getDate(columnName);
		} else if (rsmd.getColumnType(index) == java.sql.Types.TIMESTAMP) {
			obj[''.concat(columnName)] = resultSet.getTimestamp(columnName);
		} else {
			obj[''.concat(columnName)] = resultSet.getObject(columnName);
		}
	}

	return obj;
}

/**
	Retrives the java.util.Date value for a particular column from a ResultSet. Handles cases where the
	object returned from the ResultSet is a string.

	@param {ResultSet} resultSet - The ResultSet object to retrieve from.
	@param {String} columnName - The name or alias of the column to retrieve.
	@return {Date} The java.util.Date value.
*/
function getResultSetDate(resultSet, columnName) {
	var obj = resultSet.getObject(columnName);
	if (obj != null) {
		if (obj instanceof java.lang.String) {
			obj = getDate(obj);
		}
	}
	return obj;
}

/**
	Retrives the string value for a particular column from a ResultSet. Handles instances of
	java.sql.Clob and java.sql.Blob as well (with the JVM default charset).

	@param {ResultSet} resultSet - The ResultSet object to retrieve from.
	@param {String} columnName - The name or alias of the column to retrieve.
	@return {String} The string value.
*/
function getResultSetString(resultSet, columnName) {
	var obj = resultSet.getObject(columnName);
	if (obj != null) {
		if (obj instanceof java.sql.Clob) {
			obj = org.apache.commons.io.IOUtils.toString(obj.getCharacterStream());
		} else if (obj instanceof java.sql.Blob) {
			obj = org.apache.commons.io.IOUtils.toString(obj.getBinaryStream());
		} else {
			obj = java.lang.String.valueOf(obj);
		}
	}
	return obj;
}

/**
	These functions are used with the history interaction.
*/
function getFhirHistoryBaseQuery() {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "SELECT name, id, version, data::TEXT, mimetype, deleted, request_method, request_url FROM resource";
	} else if (dbType == 'sqlserver') {
		return "SELECT name, id, version, CAST(data AS NVARCHAR(MAX)) AS data, mimetype, deleted, request_method, request_url FROM resource";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirHistorySinceWherePart() {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "last_modified >= ?::TIMESTAMP WITH TIME ZONE";
	} else if (dbType == 'sqlserver') {
		return "last_modified >= CAST(? AS DATETIME)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Inserts a deleted entry for a resource and returns its DB sequence ID.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@param {Number} versionId - The version ID of the resource.
	@param {Date} lastUpdated - The last updated time of the resource.
	@param {String} method - The HTTP method used to create the resource.
	@param {String} url - The HTTP URL used to create the resource.
	@return {Number} The database sequence ID for the inserted resource.
*/
function insertFhirDeletedResource(type, id, versionId, lastUpdated, method, url) {
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		var params = [type, id, versionId, lastUpdated, $('method'), $('url')];
		var result = executeUpdateAndGetGeneratedKeys("INSERT INTO resource (name, id, version, last_modified, deleted, request_method, request_url) VALUES (?, ?, ?, ?, TRUE, ?, ?)", params);
		result.next();
		return result.getInt(1);
	} else if (dbType == 'sqlserver') {
		var dbConn = createFHIRDBConnection();

		try {
			dbConn.setAutoCommit(false);

			var idResult = executeCachedQuery('SELECT id FROM resource_sequence WITH (UPDLOCK)', [], dbConn);
			idResult.next();
			var sequenceId = idResult.getInt(1);
			executeUpdate('UPDATE resource_sequence SET ID = ID + 1', [], dbConn);
			dbConn.commit();

			var params = [sequenceId, type, id, versionId, lastUpdated, $('method'), $('url')];
			executeUpdate("INSERT INTO resource (sequence_id, name, id, version, last_modified, deleted, request_method, request_url) VALUES (?, ?, ?, ?, ?, 1, ?, ?)", params, dbConn);
			dbConn.commit();

			return sequenceId;
		} catch (e) {
			try { dbConn.rollback() } catch (e2) {}
			throw e;
		} finally {
			try { dbConn.close() } catch (e) {}
		}
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Inserts a new resource and returns its DB sequence ID.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@param {Number} versionId - The version ID of the resource.
	@param {Date} lastUpdated - The last updated time of the resource.
	@param {String} data - The resource data.
	@param {String} contentType - The MIME type of the resource data.
	@param {String} method - The HTTP method used to create the resource.
	@param {String} url - The HTTP URL used to create the resource.
	@return {Number} The database sequence ID for the inserted resource.
*/
function insertFhirResource(type, id, versionId, lastUpdated, data, contentType, method, url) {
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		var params = [type, id, versionId, lastUpdated, data, contentType, method, url];
		var result = executeUpdateAndGetGeneratedKeys("INSERT INTO resource (name, id, version, last_modified, data, mimetype, request_method, request_url) VALUES (?, ?, ?, ?, ?::XML, ?, ?, ?)", params);
		result.next();
		return result.getInt(1);
	} else if (dbType == 'sqlserver') {
		var dbConn = createFHIRDBConnection();

		try {
			dbConn.setAutoCommit(false);

			var idResult = executeCachedQuery('SELECT id FROM resource_sequence WITH (UPDLOCK)', [], dbConn);
			idResult.next();
			var sequenceId = idResult.getInt(1);
			executeUpdate('UPDATE resource_sequence SET ID = ID + 1', [], dbConn);
			dbConn.commit();

			var params = [sequenceId, type, id, versionId, lastUpdated, data, contentType, method, url];
			executeUpdate("INSERT INTO resource (sequence_id, name, id, version, last_modified, data, mimetype, request_method, request_url) VALUES (?, ?, ?, ?, ?, CAST(? AS XML), ?, ?, ?)", params, dbConn);
			dbConn.commit();

			return sequenceId;
		} catch (e) {
			try { dbConn.rollback() } catch (e2) {}
			throw e;
		} finally {
			try { dbConn.close() } catch (e) {}
		}
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	These functions are used with the search interaction.
*/
function getFhirSearchBaseQuery(usingLimit) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "SELECT name, id, version, data::TEXT, mimetype, request_method, request_url FROM resource r1 WHERE version = (SELECT MAX(version) FROM resource r2 WHERE r2.name = r1.name AND r2.id = r1.id) AND (deleted IS NULL OR deleted = FALSE)";
	} else if (dbType == 'sqlserver') {
		var query = "SELECT name, id, version, CAST(data AS NVARCHAR(MAX)) AS data, mimetype, request_method, request_url";
		if (usingLimit) {
			query += ", ROW_NUMBER() OVER (" + getOrderClause() + ") AS ROWNUM";
		}
		return query + " FROM resource r1 WHERE version = (SELECT MAX(version) FROM resource r2 WHERE r2.name = r1.name AND r2.id = r1.id) AND (deleted IS NULL OR deleted = 0)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirSearchContentWherePart() {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "data::TEXT ILIKE ?";
	} else if (dbType == 'sqlserver') {
		return "UPPER(CAST(data AS NVARCHAR(MAX))) LIKE UPPER(?)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirSearchLastUpdatedWherePart(lastUpdatedOperator) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "last_modified " + convertFhirParameterOperator(lastUpdatedOperator) + " ?::TIMESTAMP WITH TIME ZONE";
	} else if (dbType == 'sqlserver') {
		return "last_modified " + convertFhirParameterOperator(lastUpdatedOperator) + " CAST(? AS DATETIMEOFFSET)";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function convertFhirParameterOperator(operator) {
	switch ((operator + '').toLowerCase()) {
		case 'eq': return '=';
		case 'ne': return '!=';
		case 'gt': return '>';
		case 'lt': return '<';
		case 'ge': return '>=';
		case 'le': return '<=';
		case 'sa': return '>';
		case 'eb': return '<';
		case 'ap': return '=';
		default: return '=';
	}
}

function convertXPathNodeSingleton(node) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return node;
	} else if (dbType == 'sqlserver') {
		return node.split('/').join('[1]/');
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function xpathLowerCase(value) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "translate(" + value + ", 'ABCDEFGHIJKLMNOPQRSTUVWXYZ', 'abcdefghijklmnopqrstuvwxyz')";
	} else if (dbType == 'sqlserver') {
		return "fn:lower-case(" + value + ")";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getFhirSearchXPathWherePart() {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return "XPATH_EXISTS(?, data)";
	} else if (dbType == 'sqlserver') {
		return "data.exist(?) = 1";
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function addXPath(xpath, whereParts, params) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		whereParts.push(getFhirSearchXPathWherePart());
		params.push(xpath);
	} else if (dbType == 'sqlserver') {
		whereParts.push(getFhirSearchXPathWherePart().replace('?', "'" + xpath.replace(/'/g, "''") + "'"));
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function addOrder(query, usingLimit) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return query + " " + getOrderClause();
	} else if (dbType == 'sqlserver') {
		if (usingLimit) {
			return query;
		} else {
			return query + " " + getOrderClause();
		}
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

function getOrderClause() {
	return "ORDER BY name ASC, id ASC, version DESC";
}

function addLimit(query, limit) {
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return query + " LIMIT " + limit;
	} else if (dbType == 'sqlserver') {
		return "SELECT A.* FROM (" + query + ") AS A WHERE A.ROWNUM <= " + limit;
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Returns a ResultSet for a specific resource instance.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@return {ResultSet} The ResultSet.
*/
function getResource(type, id) {
	var params = [type, id];
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		return executeCachedQuery("SELECT version, data::TEXT, mimetype, last_modified, deleted FROM resource WHERE name = ? AND id = ? ORDER BY version DESC LIMIT 1", params);
	} else if (dbType == 'sqlserver') {
		if (type == 'patient') {
			return executeCachedQuery("SELECT DISTINCT nhsNumber, nhsNumberTraceStatusCode, nhsNumberTraceStatusDesc, PatientNo, active, ethnicCategoryDesc, ethnicCategoryCode, homePhone, businessPhone, mobilePhone, appointmentSMS, email, preferredContactMethod, preferredLanguage, interpreterRequired, nameFamily, nameGiven1First, nameGiven2Middle, namePrefix, maritalStatusDesc, maritalStatusCode, addressLine1, addressLine2, city, district, postalCode, LOWER(gender) AS gender, birthdate, deceased FROM OPENQUERY( [ENYH-PRD-ANALYTICS], 'SELECT DISTINCT patmas.PAPMI_PAPER_DR->PAPER_ID AS nhsNumber, patmas.PAPMI_TraceStatus_DR->TRACE_Desc AS nhsNumberTraceStatusDesc, patmas.PAPMI_TraceStatus_DR AS nhsNumberTraceStatusCode, patmas.PAPMI_No AS PatientNo, patmas.PAPMI_Active, CASE WHEN patmas.PAPMI_Active IS NULL THEN ''true'' WHEN patmas.PAPMI_Active = ''Y'' THEN ''true'' ELSE NULL END AS active, patmas.PAPMI_PAPER_DR->PAPER_IndigStat_DR->INDST_Desc AS ethnicCategoryDesc, patmas.PAPMI_PAPER_DR->PAPER_IndigStat_DR->INDST_Code AS ethnicCategoryCode, patmas.PAPMI_PAPER_DR->PAPER_TelH AS homePhone, patmas.PAPMI_PAPER_DR->PAPER_TelO AS businessPhone, patmas.PAPMI_PAPER_DR->PAPER_MobPhone AS mobilePhone, patmas.PAPMI_PAPER_DR->PAPER_AppointmentSMS AS appointmentSMS, patmas.PAPMI_PAPER_DR->PAPER_Email AS Email, patmas.PAPMI_PAPER_DR->PAPER_PreferredContactMethod AS PreferredContactMethod, patmas.PAPMI_PAPER_DR->PAPER_PrefLanguage_DR->PREFL_Desc AS PreferredLanguage, patmas.PAPMI_PAPER_DR->PAPER_InterpreterRequired AS InterpreterRequired, patmas.PAPMI_PAPER_DR->PAPER_Name AS nameFamily, patmas.PAPMI_PAPER_DR->PAPER_Name2 AS nameGiven1First, patmas.PAPMI_PAPER_DR->PAPER_Name3 AS nameGiven2Middle, patmas.PAPMI_PAPER_DR->PAPER_Title_DR->TTL_Desc AS nameprefix, CASE patmas.PAPMI_PAPER_DR->PAPER_Marital_DR->CTMAR_RowId WHEN 1 THEN ''Married'' WHEN 2 THEN ''unknown'' WHEN 3 THEN ''Widowed'' WHEN 4 THEN ''unmarried'' WHEN 5 THEN ''Legally Seperated'' WHEN 6 THEN ''Divorced'' END AS maritalStatusDesc, CASE patmas.PAPMI_PAPER_DR->PAPER_Marital_DR->CTMAR_Code WHEN ''N'' THEN ''U'' ELSE patmas.PAPMI_PAPER_DR->PAPER_Marital_DR->CTMAR_Code END AS maritalStatusCode, patmas.PAPMI_PAPER_DR->PAPER_StName AS addressLine1, patmas.PAPMI_PAPER_DR->PAPER_ForeignAddress AS addressLine2, patmas.PAPMI_PAPER_DR->PAPER_CityCode_DR->CTCIT_Desc AS city, patmas.PAPMI_PAPER_DR->PAPER_CT_Province_DR->PROV_Desc AS district, patmas.PAPMI_PAPER_DR->PAPER_Zip_DR->CTZIP_Code AS postalCode, patmas.PAPMI_PAPER_DR->PAPER_Sex_DR->CTSEX_Desc AS gender, patmas.PAPMI_DOB AS birthDate, CASE WHEN patmas.PAPMI_PAPER_DR->PAPER_Deceased = ''Y'' THEN ''true'' WHEN patmas.PAPMI_PAPER_DR->PAPER_Deceased = ''N'' THEN NULL END AS deceased FROM %ALLINDEX PA_PatMas patmas WHERE patmas.PAPMI_ID = ''" + id + "'' AND COALESCE(PAPMI_Active,''Y'') = ''Y''') AS patient;");
		}

	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Returns a ResultSet for a specific resource instance at a specific version.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@param {Number} versionId - The version ID of the resource.
	@return {ResultSet} The ResultSet.
*/
function getResourceVersion(type, id, versionId) {
	var params = [type, id, parseInt(versionId, 10)];
	var dbType = $cfg('fhirDBDatabaseType');

	if (dbType == 'postgres') {
		return executeCachedQuery("SELECT data::TEXT, mimetype, last_modified, deleted FROM resource WHERE name = ? AND id = ? AND version = ?", params);
	} else if (dbType == 'sqlserver') {
		return executeCachedQuery("SELECT CAST(data AS NVARCHAR(MAX)) AS data, mimetype, last_modified, deleted FROM resource WHERE name = ? AND id = ? AND version = ?", params);
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Returns the version ID for a given resource, or 0 if it doesn't exist.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@return {Number} The version ID, or 0 if the resource doesn't exist.
*/
function getFhirVersion(type, id) {
	var params = [type, id];
	var result;

	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		result = executeCachedQuery("SELECT version FROM resource WHERE name = ? AND id = ? ORDER BY version DESC LIMIT 1", params);
	} else if (dbType == 'sqlserver') {
		result = executeCachedQuery("SELECT TOP 1 version FROM resource WHERE name = ? AND id = ? ORDER BY version DESC", params);
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}

	if (result.next()) {
		return result.getInt(1);
	}
	return 0;
}

/**
	Returns a ResultSet with the version and deleted flag for a given resource.

	@param {String} type - The FHIR resource type.
	@param {String} id - The logical ID of the resource.
	@return {ResultSet} The ResultSet.
*/
function getFhirVersionAndDeleted(type, id) {
	var params = [type, id];
	var dbType = $cfg('fhirDBDatabaseType');
	if (dbType == 'postgres') {
		return executeCachedQuery("SELECT version, deleted FROM resource WHERE name = ? AND id = ? ORDER BY version DESC LIMIT 1", params);
	} else if (dbType == 'sqlserver') {
		return executeCachedQuery("SELECT TOP 1 version, deleted FROM resource WHERE name = ? AND id = ? ORDER BY version DESC", params);
	} else {
		throw 'Unsupported DB type: ' + dbType;
	}
}

/**
	Supports parsing date strings in a variety of formats and outputting the date in a specific format.
*/
var patterns = [
	"yyyy-MM-dd HH:mm:ss.SSSSSSS ZZ",
	"yyyy-MM-dd'T'HH:mm:ss.SSSZ",
	"yyyy-MM-dd'T'HH:mm:ssZ",
	"yyyy-MM-dd'T'HH:mm:ss",
	"yyyy-MM-dd'T'HH:mm",
	'yyyy-MM-dd',
	'yyyy-MM-dd HH:mm:ss:SSS',
	'yyyy-MM-dd HH:mm:ss.SSS',
	'yyyy-MM-dd HH:mm:ss',
	'yyyy-MM-dd HH:mm',
	'EEE MMM dd HH:mm:ss:SSS zzz yyyy',
	'EEE MMM dd HH:mm:ss.SSS zzz yyyy',
	'EEE MMM dd HH:mm:ss zzz yyyy',
	'EEE MMM dd zzz yyyy',
	'dd-MMM-yyyy HH:mm:ss:SSS',
	'dd-MMM-yyyy HH:mm:ss.SSS',
	'dd-MMM-yyyy HH:mm:ss',
	'yyyy MM dd',
	'yyyy.MM.dd',
	'MM-dd-yyyy',
	'MM dd yyyy',
	'MM.dd.yyyy',
	'HH:mm:ss:SSS',
	'HH:mm:ss.SSS',
	'HH:mm:ss',
	'yyyyMMddHHmmssSSS',
	'yyyyMMddHHmmss',
	'yyyyMMddHHmm',
	'hh:mm aa'];

var formatters = [];
for each (pattern in patterns) {
	formatters.push(org.joda.time.format.DateTimeFormat.forPattern(pattern));
}

function getMillis(date) {
	var instant = 0;
	if (typeof date == 'number' || date instanceof java.lang.Number)
		instant = new Number(date);
	else if (date instanceof Date || date instanceof java.util.Date)
		instant = date.getTime();
	else if (date instanceof org.joda.time.ReadableInstant)
		instant = date.getMillis();
	else {
		for each (formatter in formatters) {
			try {
				instant = formatter.parseMillis(new String(date));
				break;
			} catch(e) {}
		}
	}

	return instant;
}

function getDate(date) {
	return new java.util.Date(getMillis(date));
}

function convertDate(date, outpattern) {
	return org.joda.time.format.DateTimeFormat.forPattern(outpattern).print(getMillis(date));
}

/**
	Creates a FHIR OperationOutcome resource and adds it to the response map with the key "response".

	@param {String} severity - Indicates whether the issue indicates a variation from successful
		processing. Values: fatal | error | warning | information
	@param {String} code - Describes the type of the issue. The system that creates an
		OperationOutcome SHALL choose the most applicable code from the IssueType value set, and may
		additional provide its own code for the error in the details element. Values: invalid | security |
		processing | transient | informational
	@param {String} details - Additional details about the error. This may be a text description of
		the error, or a system code that identifies the error.
	@param {int} httpStatusCode - The HTTP status code to send back with the response. Defaults to 400
		if not specified.
	@param {Error} e - If included, this will be used to build up the response status message and
		error.
	@param {Boolean} isFormatXML - If true, the resource will be formatted in XML. Otherwise, it will
		be formatted in JSON. If not specified, it will attempt to find the _format parameter in the source
		map, or return JSON.
	@return {Response} The created Response object.
*/
function createOperationOutcome(severity, code, details, httpStatusCode, e, isFormatXML) {
	if (!httpStatusCode) {
		httpStatusCode = 400;
	}

	if (!isFormatXML && $s('parameters') && $s('parameters').contains('_format')) {
		isFormatXML = !FhirUtil.isJSON($s('parameters').getParameter('_format'));
	}

	var outcome = FhirUtil.createOperationOutcome(severity, code, details);
	var message = isFormatXML ? FhirUtil.toXML(outcome) : FhirUtil.toJSON(outcome);
	responseMap.put('response', FhirResponseFactory.getResponse(message, httpStatusCode, isFormatXML ? FhirUtil.getMIMETypeXML() : FhirUtil.getMIMETypeJSON()));
	var response = new Response(message);
	response.setStatusMessage(severity.toUpperCase() + ' OperationOutcome created with status ' + httpStatusCode + '.');

	if (httpStatusCode >= 400) {
		response.setStatus(Status.ERROR);

		if (e) {
			var customMessage = 'Channel ' + channelName + ': ';
			if (e.rhinoException) {
				customMessage += 'Error on line ' + e.rhinoException.lineNumber() + ': ';
			}
			customMessage += e.toString();

			var errorContent = com.mirth.connect.util.ErrorMessageBuilder.buildErrorMessage('FHIR Listener', customMessage, e.javaException || null);
			if (e.javaException) {
				logger.error(errorContent, e.javaException);	
			} else {
				logger.error(errorContent);
			}
			response.setError(errorContent);
		}
	}

	return response;
}

/**
	Returns the "return" element of the Prefer HTTP header if specified.

	@return {String} The "return" element of the Prefer header, or undefined if not present
*/
function getPreferValue() {
	var preferReturn;
	var preferHeader = $('headers').getHeader('Prefer');

	if (preferHeader) {
		for each (element in new org.apache.http.message.BasicHeader('Prefer', preferHeader).getElements()) {
			if (element.getName() == 'return') {
				preferReturn = element.getValue();
				break;
			}
		}
	}

	return preferReturn;
}

/**
	Imports HAPI FHIR packages commonly used in the FHIR Listener channel.
*/
importPackage(Packages.ca.uhn.fhir.model.api);
importPackage(Packages.ca.uhn.fhir.model.base.resource);
importPackage(Packages.ca.uhn.fhir.model.primitive);
importPackage(Packages.org.hl7.fhir.dstu3.model);
importPackage(org.apache.commons.lang3);
importPackage(org.apache.commons.lang3.math);

/**
	Sets the resource ID, version ID, and last updated date/time.

	@param {IBaseResource} resource - The FHIR resource object.
	@param {String} id - The logical ID of the resource.
	@param {Number} versionId - The version ID of the resource.
	@return {Date} The last updated date/time.
*/
function updateResourceMeta(resource, id, versionId) {
	resource.setId(id);
	resource.getMeta().setVersionId(new String(versionId));
	var lastUpdated = new java.util.Date();
	resource.getMeta().getLastUpdatedElement().setTimeZone(java.util.TimeZone.getDefault());
	resource.getMeta().setLastUpdated(lastUpdated);
	return lastUpdated;
}

/**
	Builds AllergyIntolerance FHIR Resource that adheres to its Care-Connect profile,
	see https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-AllergyIntolerance-1 for more info.

	@author Frazer Smith
	@param {Object} data - Java RowSet object.
	@return {Object} AllergyIntolerance FHIR resource.
 */
function buildAllergyIntoleranceResource(data) {
	var result = getResultSet(data);
	/**
	 * Hard-coding meta profile and resourceType into resource as this should not
	 * be changed for this resource, ever.
	 */

	var resource = {
		meta: {
			profile: [
				'https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-AllergyIntolerance-1'
			]
		},
		resourceType: 'AllergyIntolerance'
	}; // Add meta data

	if (
		result.lastUpdated != undefined &&
		result.lastUpdated.substring(0, 1) != 'T' &&
		result.lastUpdated.substring(0, 4) != '1900'
	) {
		resource.meta.lastUpdated = result.lastUpdated;
	}

	resource.id = newStringOrUndefined(result.id);
	resource.assertedDate = newStringOrUndefined(result.assertedDate); // Very unlikely that an allergy record will have multiple components like this
	// but better to be safe than sorry

	var allergyResult = [];
	allergyResult.push(newStringOrUndefined(result.allergyGroupDesc));
	allergyResult.push(newStringOrUndefined(result.allergyCodingDesc));
	allergyResult.push(newStringOrUndefined(result.allergyDrugDesc));
	allergyResult.push(newStringOrUndefined(result.allergyDrugGenericDesc));
	allergyResult.push(newStringOrUndefined(result.allergyDrugCategoryDesc));
	allergyResult.push(newStringOrUndefined(result.allergyDrugFormDesc));
	allergyResult.push(newStringOrUndefined(result.allergyDrugIngredientDesc));
	allergyResult.push(newStringOrUndefined(result.allergyComment));
	allergyResult = allergyResult.filter(function(element) {
		return element != null;
	});

	if (allergyResult.length > 0) {
		resource.code = {
			text: allergyResult.join('; ')
		};
	}

	resource.patient = {
		reference: ''
			.concat($cfg('apiUrl'), '/r3/Patient/')
			.concat(result.patientReference)
	};
	resource.clinicalStatus = newStringOrUndefined(result.clinicalStatusCode);
	resource.verificationStatus = newStringOrUndefined(
		result.verificationStatusCode
	);
	resource.type = newStringOrUndefined(result.typeCode);
	resource.criticality = newStringOrUndefined(result.criticalityCode);
	return resource;
}

/**
	Builds Bundle FHIR Resource that adheres to the HL7 spec,
	see https://www.hl7.org/fhir/STU3/bundle.html for more info.

	@author Frazer Smith
	@param {string} requestUrl - URL that GET request was made to.
	@return {Object} Bundle FHIR resource.
 */
function buildBundleResource(requestUrl) {
	var resource = {
		resourceType: newStringOrUndefined('Bundle'),
		type: newStringOrUndefined('searchset'),
		total: 0,
		link: [],
		entry: []
	}; // Add link URL

	if (requestUrl) {
		var linkEntry = {
			relation: newStringOrUndefined('self'),
			url: newStringOrUndefined(requestUrl)
		};
		resource.link.push(linkEntry);
	}

	return resource;
}

/**
	Builds Encounter FHIR Resource that adheres to its Care-Connect profile,
	see https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Encounter-1 for more info.

	@author Frazer Smith
	@param {Object} data - Java RowSet object.
	@return {Object} Encounter FHIR resource.
 */
function buildEncounterResource(data) {
	var result = getResultSet(data);
	/**
	 * Hard-coding meta profile and resourceType into resource as this should not
	 * be changed for this resource, ever.
	 */

	var resource = {
		meta: {
			profile: [
				'https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Encounter-1'
			]
		},
		resourceType: 'Encounter'
	};
	resource.id = newStringOrUndefined(result.encounterIdentifier);
	resource.status = newStringOrUndefined(result.encounterStatusMapped); // Add meta data

	if (
		result.lastUpdated != undefined &&
		result.lastUpdated.substring(0, 1) != 'T' &&
		result.lastUpdated.substring(0, 4) != '1900'
	) {
		resource.meta.lastUpdated = result.lastUpdated;
	}

	if (result.encounterClassDesc != undefined) {
		resource['class'] = {
			system: 'http://hl7.org/fhir/v3/ActEncounterCode',
			code: newStringOrUndefined(result.encounterClassCode),
			display: newStringOrUndefined(result.encounterClassDesc)
		};
	}

	resource.type = [];
	var emptyType = {
		coding: [
			{
				system: 'https://fhir.nhs.uk/STU3/CodeSystem/DCH-Specialty-1',
				code: undefined,
				display: undefined
			}
		],
		extension: [
			{
				url:
					'https://fhir.ydh.nhs.uk/STU3/StructureDefinition/Extension-YDH-SpecialtyContext-1',
				valueCodeableConcept: {
					coding: [
						{
							system:
								'https://fhir.ydh.nhs.uk/STU3/ValueSet/Extension-YDH-SpecialtyContext-1',
							code: undefined,
							display: undefined
						}
					]
				}
			}
		]
	};

	if (
		result.encounterClassCode != undefined &&
		result.encounterClassCode == 'IMP'
	) {
		var admType = JSON.parse(JSON.stringify(emptyType));
		var disType = JSON.parse(JSON.stringify(emptyType));

		if (result.encounterTypeCodeAdm != undefined) {
			admType.coding[0].code = newStringOrUndefined(
				result.encounterTypeCodeAdm
			);
			admType.coding[0].display = newStringOrUndefined(
				result.encounterTypeDescAdm
			);
			admType.extension[0].valueCodeableConcept.coding[0].code = 'ADM';
			admType.extension[0].valueCodeableConcept.coding[0].display =
				'Admitting';
			resource.type.push(admType);
		} else if (result.encounterTypeCode != undefined) {
			admType.coding[0].code = newStringOrUndefined(
				result.encounterTypeCode
			);
			admType.coding[0].display = newStringOrUndefined(
				result.encounterTypeDesc
			);
			delete admType.extension;
			resource.type.push(admType);
		}

		if (result.encounterTypeCodeDis != undefined) {
			disType.coding[0].code = newStringOrUndefined(
				result.encounterTypeCodeDis
			);
			disType.coding[0].display = newStringOrUndefined(
				result.encounterTypeDescDis
			);
			disType.extension[0].valueCodeableConcept.coding[0].code = 'DIS';
			disType.extension[0].valueCodeableConcept.coding[0].display =
				'Discharging';
			resource.type.push(disType);
		} else if (result.encounterTypeCode != undefined) {
			disType.coding[0].code = newStringOrUndefined(
				result.encounterTypeCode
			);
			disType.coding[0].display = newStringOrUndefined(
				result.encounterTypeDesc
			);
			delete disType.extension;
			resource.type.push(disType);
		}

		if (resource.type.length > 1) {
			if (
				JSON.stringify(resource.type[0]) ==
				JSON.stringify(resource.type[1])
			) {
				delete resource.type[1];
			}
		}
	} else {
		var outType = JSON.parse(JSON.stringify(emptyType));

		if (result.encounterTypeCode != undefined) {
			outType.coding[0].code = newStringOrUndefined(
				result.encounterTypeCode
			);
			outType.coding[0].display = newStringOrUndefined(
				result.encounterTypeDesc
			);
			delete outType.extension;
			resource.type.push(outType);
		}
	} // Add participants

	resource.participant = [];

	if (
		result.encounterParticipantIndividualCode_admitting != undefined &&
		result.encounterParticipantIndividualCode_discharging != undefined &&
		result.encounterParticipantIndividualCode_discharging ===
			result.encounterParticipantIndividualCode_admitting
	) {
		var participantCombo = {
			type: [
				{
					coding: [
						{
							system: 'http://hl7.org/fhir/v3/ParticipationType',
							code: 'ADM',
							display: 'admitter'
						}
					]
				},
				{
					coding: [
						{
							system: 'http://hl7.org/fhir/v3/ParticipationType',
							code: 'DIS',
							display: 'discharger'
						}
					]
				}
			],
			individual: {
				identifier: {
					value: result.encounterParticipantIndividualCode_admitting
				},
				display: result.encounterParticipantIndividualDisplay_admitting
			}
		};
		resource.participant.push(participantCombo);
	}

	if (resource.participant.length == 0) {
		if (result.encounterParticipantIndividualCode_admitting != undefined) {
			var participantAdmitter = {
				type: [
					{
						coding: [
							{
								system:
									'http://hl7.org/fhir/v3/ParticipationType',
								code: 'ADM',
								display: 'admitter'
							}
						]
					}
				],
				individual: {
					identifier: {
						value:
							result.encounterParticipantIndividualCode_admitting
					},
					display:
						result.encounterParticipantIndividualDisplay_admitting
				}
			};
			resource.participant.push(participantAdmitter);
		}

		if (
			result.encounterParticipantIndividualCode_discharging != undefined
		) {
			var participantDischarger = {
				type: [
					{
						coding: [
							{
								system:
									'http://hl7.org/fhir/v3/ParticipationType',
								code: 'DIS',
								display: 'discharger'
							}
						]
					}
				],
				individual: {
					identifier: {
						value:
							result.encounterParticipantIndividualCode_discharging
					},
					display:
						result.encounterParticipantIndividualDisplay_discharging
				}
			};
			resource.participant.push(participantDischarger);
		}
	}

	if (result.encounterParticipantIndividualCode_opattending != undefined) {
		var participantConsultant = {
			type: [
				{
					coding: [
						{
							system: 'http://hl7.org/fhir/v3/ParticipationType',
							code: 'CON',
							display: 'consultant'
						}
					]
				}
			],
			individual: {
				identifier:
					result.encounterParticipantIndividualCode_opattending,
				display:
					result.encounterParticipantIndividualDisplay_opattending
			}
		};
		resource.participant.push(participantConsultant);
	}

	resource.period = {};

	if (
		result.encounterPeriodStart != undefined &&
		result.encounterPeriodStart.substring(0, 1) != 'T' &&
		result.encounterPeriodStart.substring(0, 4) != '1900'
	) {
		resource.period.start = result.encounterPeriodStart;
	}

	if (
		result.encounterPeriodEnd != undefined &&
		result.encounterPeriodEnd.substring(0, 1) != 'T' &&
		result.encounterPeriodEnd.substring(0, 4) != '1900'
	) {
		resource.period.end = result.encounterPeriodEnd;
	} // Add admission and discharge inpatient details

	resource.hospitalization = {};

	if (
		result.encounterAdmissionmethodCodingCode != undefined ||
		result.encounterDischargemethodCodingCode != undefined
	) {
		resource.hospitalization.extension = [];
	}

	if (result.encounterAdmissionmethodCodingCode != undefined) {
		var admissionMethod = {
			url:
				'https://fhir.hl7.org.uk/STU3/StructureDefinition/Extension-CareConnect-AdmissionMethod-1',
			valueCodeableConcept: {
				coding: [
					{
						system:
							'https://fhir.hl7.org.uk/STU3/ValueSet/CareConnect-AdmissionMethod-1',
						code: result.encounterAdmissionmethodCodingCode,
						display: newStringOrUndefined(
							result.encounterAdmissionmethodCodingDesc
						)
					}
				]
			}
		};
		resource.hospitalization.extension.push(admissionMethod);
	}

	if (result.encounterDischargemethodCodingCode != undefined) {
		var dischargeMethod = {
			url:
				'https://fhir.hl7.org.uk/STU3/StructureDefinition/Extension-CareConnect-DischargeMethod-1',
			valueCodeableConcept: {
				coding: [
					{
						system:
							'https://fhir.hl7.org.uk/STU3/ValueSet/CareConnect-DischargeMethod-1',
						code: result.encounterDischargemethodCodingCode,
						display: newStringOrUndefined(
							result.encounterDischargemethodCodingDesc
						)
					}
				]
			}
		};
		resource.hospitalization.extension.push(dischargeMethod);
	}

	if (result.encounterHospitalizationAdmitsourceCodingCode != undefined) {
		resource.hospitalization.admitSource = {
			coding: [
				{
					system:
						'https://fhir.hl7.org.uk/STU3/CodeSystem/CareConnect-SourceOfAdmission-1',
					code: result.encounterHospitalizationAdmitsourceCodingCode,
					display: newStringOrUndefined(
						result.encounterHospitalizationAdmitsourceCodingDesc
					)
				}
			]
		};
	}

	if (
		result.encounterHospitalizationDischargedispositionCodingCode !=
		undefined
	) {
		resource.hospitalization.dischargeDisposition = {
			coding: [
				{
					system:
						'https://fhir.hl7.org.uk/STU3/CodeSystem/CareConnect-DischargeDestination-1',
					code:
						result.encounterHospitalizationDischargedispositionCodingCode,
					display: newStringOrUndefined(
						result.encounterHospitalizationDischargedispositionCodingDesc
					)
				}
			]
		};
	} // Add location details

	if (
		result.encounterClassCode != undefined &&
		result.encounterClassCode == 'IMP'
	) {
		resource.location = [];
		var emptyLocation = {
			location: {
				identifier: {
					value: undefined
				},
				display: undefined
			},
			period: {
				start: undefined,
				end: undefined
			}
		};

		if (
			result.encounterLocation1Identifier != undefined &&
			typeof resource.period.start !== 'undefined'
		) {
			var admittingWard = JSON.parse(JSON.stringify(emptyLocation));
			admittingWard.location.identifier.value = newStringOrUndefined(
				result.encounterLocation1Identifier
			);
			admittingWard.location.display = newStringOrUndefined(
				result.encounterLocation1Display
			);
			admittingWard.period.start = resource.period.start;
			resource.location.push(admittingWard);
		}

		if (
			result.encounterLocation2Identifier != undefined &&
			typeof resource.period.end !== 'undefined'
		) {
			var dischargeWard = JSON.parse(JSON.stringify(emptyLocation));
			dischargeWard.location.identifier.value = newStringOrUndefined(
				result.encounterLocation2Identifier
			);
			dischargeWard.location.display = newStringOrUndefined(
				result.encounterLocation2Display
			);
			dischargeWard.period.end = resource.period.end;
			resource.location.push(dischargeWard);
		}
	}

	resource.subject = {
		reference: ''
			.concat($cfg('apiUrl'), '/r3/Patient/')
			.concat(result.subjectReference)
	};
	return resource;
}

/**
	Builds Flag FHIR Resource that adheres to its Care-Connect profile,
	see https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Flag-1 for more info.

	@author Frazer Smith
	@param {Object} data - Java RowSet object.
	@return {Object} Flag FHIR resource.
 */
function buildFlagResource(data) {
	var result = getResultSet(data);
	/**
	 * Hard-coding meta profile and resourceType into resource as this should not
	 * be changed for this resource, ever.
	 */

	var resource = {
		meta: {
			profile: [
				'https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Flag-1'
			]
		},
		resourceType: 'Flag'
	};
	resource.id = newStringOrUndefined(result.flagId);
	resource.status = newStringOrUndefined(result.flagStatusCode);

	if (result.flagCategoryCodingCode != undefined) {
		resource.category = {
			coding: [
				{
					system: 'https://trakcare.ydh.nhs.uk',
					code: newStringOrUndefined(result.flagCategoryCodingCode),
					display: newStringOrUndefined(
						result.flagCategoryCodingDisplay
					)
				}
			]
		};
	}

	resource.code = {
		coding: []
	};

	if (result.flagCodeCodingCode != undefined) {
		var ydhCode = {
			system: 'https://trakcare.ydh.nhs.uk',
			code: newStringOrUndefined(result.flagCodeCodingCode),
			display: newStringOrUndefined(result.flagCodeCodingDisplay)
		};
		resource.code.coding.push(ydhCode);
	}

	if (result.flagCodeCodingSnomedCode != undefined) {
		var snomedCode = {
			system: 'http://snomed.info/sct',
			code: newStringOrUndefined(result.flagCodeCodingSnomedCode),
			display: newStringOrUndefined(result.flagCodeCodingSnomedDisplay)
		};
		resource.code.coding.push(snomedCode);
	}

	resource.period = {};

	if (
		result.periodStart != undefined &&
		result.periodStart.substring(0, 1) != 'T' &&
		result.periodStart.substring(0, 4) != '1900'
	) {
		resource.period.start = result.periodStart;
	}

	if (
		result.periodStart != undefined &&
		result.periodStart.substring(0, 1) != 'T' &&
		result.periodStart.substring(0, 4) != '1900'
	) {
		resource.period.end = result.periodStart;
	}

	resource.subject = {
		reference: ''
			.concat($cfg('apiUrl'), '/r3/Patient/')
			.concat(result.flagSubjectReference)
	};
	return resource;
}

/**
	Builds MedicationStatement FHIR Resource that adheres to its Care-Connect profile,
	see https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-MedicationStatement-1 for more info.

	@author Frazer Smith
	@param {Object} data - Java RowSet object.
	@return {Object} MedicationStatement FHIR resource.
 */
function buildMedicationStatementResource(data) {
	var result = getResultSet(data);
	/**
	 * Hard-coding meta profile and resourceType into resource as this should not
	 * be changed for this resource, ever.
	 */

	var resource = {
		meta: {
			profile: [
				'https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-MedicationStatement-1'
			]
		},
		resourceType: 'MedicationStatement'
	};
	resource.id = newStringOrUndefined(result.medstatId);
	resource.status = newStringOrUndefined(result.medstatStatusCode);
	resource.medicationReference = {
		reference: newStringOrUndefined('#'.concat(result.medicationId))
	}; // Add contained Medication resource

	var contained = [];

	if (result.medicationId != undefined) {
		var containedMedication = {
			resourceType: 'Medication',
			id: result.medicationId,
			code: {
				coding: [
					{
						code: newStringOrUndefined(
							result.medicationCodeCodingCode
						),
						display: newStringOrUndefined(
							result.medicationCodeCodingDisplay
						)
					}
				],
				text: result.medicationCodeText
			}
		};
		contained.push(containedMedication);

		if (
			result.medicationCodeCodingCode != undefined &&
			result.medicationCodeCodingDisplay != undefined
		) {
			containedMedication.code.coding[0].system =
				'http://snomed.info/sct';
		}
	}

	if (contained.length > 0) {
		resource.contained = contained;
	} // Add dosages

	var dosage = [];
	var dosageObject = {
		patientInstruction: newStringOrUndefined(
			result.medstatDosagePatientinstruction
		),
		route: {
			text: newStringOrUndefined(result.medstatDosageRouteText)
		}
	};

	if (
		result.medstatDosageDoseQuantityValue != undefined &&
		result.medstatDosageDoseQuantityUnit != undefined
	) {
		dosageObject.doseQuantity = {
			value: newStringOrUndefined(result.medstatDosageDoseQuantityValue),
			unit: newStringOrUndefined(
				result.medstatDosageDoseQuantityUnit
			).toLowerCase()
		};
	}

	if (
		result.medstatDosageTimingRepeatDuration != undefined &&
		result.medstatDosageTimingRepeatDurationUnit != undefined &&
		result.medstatDosageTimingRepeatDurationUnit != 'DO'
	) {
		dosageObject.timing = {
			repeat: {
				duration: newStringOrUndefined(
					result.medstatDosageTimingRepeatDuration
				),
				durationUnit: newStringOrUndefined(
					result.medstatDosageTimingRepeatDurationUnit
				).toLowerCase()
			}
		};
	}

	dosage.push(dosageObject);

	if (dosage.length > 0) {
		resource.dosage = dosage;
	}

	resource.effectivePeriod = {};

	if (
		result.medstatEffectiveStart != undefined &&
		result.medstatEffectiveStart.substring(0, 1) != 'T' &&
		result.medstatEffectiveStart.substring(0, 4) != '1900'
	) {
		resource.effectivePeriod.start = result.medstatEffectiveStart;
	}

	if (
		result.medstatEffectiveEnd != undefined &&
		result.medstatEffectiveEnd.substring(0, 1) != 'T' &&
		result.medstatEffectiveEnd.substring(0, 4) != '1900'
	) {
		resource.effectivePeriod.end = result.medstatEffectiveEnd;
	}

	resource.subject = {
		reference: ''
			.concat($cfg('apiUrl'), '/r3/Patient/')
			.concat(result.medstatSubjectReference)
	}; // Hard-coded as TrakCare doesn't record whether a patient has taken medication

	resource.taken = 'unk';
	return resource;
}

/**
	Builds Patient FHIR resource that adheres to its Care-Connect profile,
	see https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Patient-1 for more info.

	@author Frazer Smith
	@param {Object} data - Java RowSet object.
	@return {Object} Patient FHIR resource.
 */
function buildPatientResource(data) {
	var result = getResultSet(data);

	if (result.nhsNumberTraceStatusCode == undefined) {
		result.nhsNumberTraceStatusCode = '2';
		result.nhsNumberTraceStatusDesc = 'Number present but not traced';
	}

	if (result.deceased == undefined) {
		result.deceased = false;
	} else {
		result.deceased = true;
	}
	/**
	 * Hard-coding meta profile and resourceType into resource as this should not
	 * be changed for this resource, ever.
	 */

	var resource = {
		fullUrl: ''
			.concat($cfg('apiUrl') + $('contextPath'), '/')
			.concat(result.patientNo),
		meta: {
			profile: [
				'https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Patient-1'
			]
		},
		resourceType: newStringOrUndefined('Patient'),
		identifier: [
			{
				use: newStringOrUndefined('usual'),
				system: newStringOrUndefined(
					'https://fhir.ydh.nhs.uk/Id/local-patient-identifier'
				),
				value: newStringOrUndefined(result.patientNo)
			}
		],
		name: [
			{
				use: newStringOrUndefined('usual'),
				family: newStringOrUndefined(result.nameFamily),
				given: result.nameGiven1First,
				prefix: result.namePrefix
			}
		],
		gender: newStringOrUndefined(result.gender),
		birthDate: newStringOrUndefined(result.birthdate),
		deceasedBoolean: newBooleanOrUndefined(result.deceased),
		address: [
			{
				use: newStringOrUndefined('home'),
				type: newStringOrUndefined('postal'),
				line: [
					newStringOrUndefined(result.addressLine1),
					newStringOrUndefined(result.addressLine2)
				],
				city: newStringOrUndefined(result.city),
				district: newStringOrUndefined(result.district),
				postalCode: newStringOrUndefined(result.postalCode)
			}
		],
		id: newStringOrUndefined(result.patientNo),
		language: newStringOrUndefined('English (Great Britain)')
	}; // Add meta data

	if (
		result.lastUpdated != undefined &&
		result.lastUpdated.substring(0, 1) != 'T' &&
		result.lastUpdated.substring(0, 1) != '1900'
	) {
		resource.meta.lastUpdated = newStringOrUndefined(result.lastUpdated);
	} // Add NHS No

	if (result.nhsNumber != undefined) {
		var nhsIdentifier = {
			use: newStringOrUndefined('official'),
			system: newStringOrUndefined('https://fhir.nhs.uk/Id/nhs-number'),
			value: newStringOrUndefined(result.nhsNumber),
			extension: [
				{
					url: newStringOrUndefined(
						'https://fhir.hl7.org.uk/STU3/StructureDefinition/Extension-CareConnect-NHSNumberVerificationStatus-1'
					),
					valueCodeableConcept: {
						coding: [
							{
								system: newStringOrUndefined(
									'https://fhir.hl7.org.uk/STU3/CodeSystem/CareConnect-NHSNumberVerificationStatus-1'
								),
								code: newStringOrUndefined(
									result.nhsNumberTraceStatusCode
								),
								display: newStringOrUndefined(
									result.nhsNumberTraceStatusDesc
								)
							}
						]
					}
				}
			]
		};
		resource.identifier.push(nhsIdentifier);
	} // Add Next of kin contact details

	if (result.contactName != undefined) {
		var contact = {
			relationship: {
				coding: [
					{
						system: 'http://hl7.org/fhir/v2/0131',
						code: 'N',
						display: 'Next-of-kin'
					}
				]
			},
			name: [
				{
					use: 'usual',
					text: newStringOrUndefined(result.contactName)
				}
			]
		};

		if (result.contactPhone != undefined) {
			var contactTelecom = [
				{
					system: 'phone',
					value: newStringOrUndefined(result.contactPhone)
				}
			];
			contact.telecom = contactTelecom;
		}

		resource.contact = contact;
	} // Add Telecom contact details

	var telecom = [];

	if (result.homePhone != undefined) {
		var homePhone = {
			system: newStringOrUndefined('phone'),
			value: newStringOrUndefined(result.homePhone),
			use: newStringOrUndefined('home')
		};
		telecom.push(homePhone);
	}

	if (result.mobilePhone != undefined) {
		var mobilePhone = {
			system: newStringOrUndefined('phone'),
			value: newStringOrUndefined(result.mobilePhone),
			use: newStringOrUndefined('mobile')
		};
		telecom.push(mobilePhone);
	}

	if (result.email != undefined) {
		var email = {
			system: newStringOrUndefined('email'),
			value: newStringOrUndefined(result.email)
		};
		telecom.push(email);
	}

	if (telecom.length > 0) {
		resource.telecom = telecom;
	} // Add Ethnical Category

	var extension = [];

	if (result.ethnicCategoryCode != undefined) {
		var ethCatExtension = {
			url: newStringOrUndefined(
				'https://fhir.hl7.org.uk/STU3/StructureDefinition/Extension-CareConnect-EthnicCategory-1'
			),
			valueCodeableConcept: {
				coding: [
					{
						system: newStringOrUndefined(
							'https://fhir.hl7.org.uk/STU3/CodeSystem/CareConnect-EthnicCategory-1'
						),
						code: newStringOrUndefined(result.ethnicCategoryCode),
						display: newStringOrUndefined(result.ethnicCategoryDesc)
					}
				]
			}
		};
		extension.push(ethCatExtension);
	}

	if (extension.length > 0) {
		resource.extension = extension;
	} // Add contained GP organization resource

	var contained = [];

	if (result.gpIdentifier != undefined) {
		var containedOrganisation = {
			resourceType: 'Organization',
			id: newStringOrUndefined(result.gpIdentifier),
			meta: {
				profile: [
					'https://fhir.hl7.org.uk/STU3/StructureDefinition/CareConnect-Organization-1'
				]
			},
			name: newStringOrUndefined(result.gpDesc),
			address: [
				{
					use: newStringOrUndefined('work'),
					type: newStringOrUndefined('both'),
					line: [
						newStringOrUndefined(result.gpAddressLine1),
						newStringOrUndefined(result.gpAddressLine2)
					],
					city: newStringOrUndefined(result.gpCity),
					postalCode: newStringOrUndefined(result.gpPostalCode)
				}
			]
		};
		contained.push(containedOrganisation);
	}

	if (contained.length > 0) {
		resource.contained = contained;
	} // Add GP

	var generalPractitioner = [];

	if (result.gpIdentifier != undefined) {
		var gpReference = {
			reference: newStringOrUndefined('#'.concat(result.gpIdentifier)),
			display: newStringOrUndefined(result.gpDesc)
		};
		generalPractitioner.push(gpReference);
	}

	if (generalPractitioner.length > 0) {
		resource.generalPractitioner = generalPractitioner;
	} // Add Marital Status

	if (
		result.maritalStatusCode != undefined &&
		result.maritalStatusDesc != undefined
	) {
		resource.maritalStatus = {
			coding: [
				{
					system: newStringOrUndefined(
						'http://hl7.org/fhir/stu3/v3/MaritalStatus'
					),
					code: newStringOrUndefined(result.maritalStatusCode),
					display: newStringOrUndefined(result.maritalStatusDesc)
				}
			]
		};
	} // If patient has a 'Do Not Distribute Patient Address' alert, strip out contact details

	if (result.DND != undefined) {
		delete resource.telecom;
		delete resource.address;
		resource.meta.security = [
			{
				system: 'http://hl7.org/fhir/ValueSet/v3-Confidentiality',
				code: 'R',
				display: 'restricted'
			}
		];
	}

	return resource;
}

/* eslint-disable quotes */

/**
	Queries database for data needed to build FHIR resource.

	@author Frazer Smith
	@param {string} type - Resource name.
	@param {Object} params - An array of predicates to be added to SQL queries.
	@return {Object} Java ResultSet object.
 */
function buildResourceQuery(type, params) {
	var firstParams = '';
	var secondParams = '';
	var thirdParams = '';

	if (params[0]) {
		firstParams = 'AND '.concat(params[0]);
	}

	if (params[1]) {
		secondParams = 'AND '.concat(params[1]);
	}

	if (params[2]) {
		thirdParams = 'AND '.concat(params[2]);
	} // type is Java object so has to be coerced to a JS object by adding an empty string

	switch (''.concat(type)) {
		case 'allergyintolerance':
			return executeCachedQuery(
				"SELECT DISTINCT id, patientReference, allergyGroupDesc, allergyCodingDesc, allergyDrugDesc, allergyDrugGenericDesc, allergyDrugCategoryDesc, allergyDrugFormDesc, allergyDrugIngredientDesc, allergyComment, clinicalStatusCode, verificationStatusCode, typeCode, criticalityCode, CONCAT(COALESCE(assertedDate, ''), 'T', COALESCE(assertedTime, '')) AS assertedDate, CONCAT(COALESCE(lastUpdateDate, ''), 'T', COALESCE(lastUpdateTime, '')) AS lastUpdated FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT DISTINCT REPLACE(alle.ALG_RowId, ''||'', ''-'') AS id, alle.ALG_PAPMI_ParRef->PAPMI_No AS patientReference, alle.ALG_AllergyGrp_DR->ALGR_Desc AS allergyGroupDesc, alle.ALG_TYPE_DR->ALG_Desc AS allergyCodingDesc, alle.ALG_PHCDM_DR->PHCD_ProductName AS allergyDrugDesc, alle.ALG_PHCGE_DR->PHCGE_Name AS allergyDrugGenericDesc, alle.ALG_PHCSC_DR->PHCSC_Desc AS allergyDrugCategoryDesc, alle.ALG_PHCDRGForm_DR->PHCDF_Description AS allergyDrugFormDesc, alle.ALG_Ingred_DR->INGR_Desc AS allergyDrugIngredientDesc, alle.ALG_Comments AS allergyComment, CASE alle.ALG_Status WHEN ''A'' THEN ''active'' WHEN ''I'' THEN ''inactive'' WHEN ''R'' THEN ''resolved'' ELSE NULL END AS clinicalStatusCode, CASE WHEN alle.ALG_Status = ''C'' THEN ''unconfirmed'' WHEN alle.ALG_ConfirmedDate IS NOT NULL OR (alle.ALG_Status != ''C'' AND alle.ALG_Status IS NOT NULL) THEN ''confirmed'' ELSE ''unconfirmed'' END as verificationStatusCode, CASE alle.ALG_Category_DR->ALRGCAT_DESC WHEN ''ALLERGY'' THEN ''allergy'' WHEN ''SIDEEFFECT'' THEN ''intolerance'' ELSE NULL END AS typeCode, CASE alle.ALG_Severity_DR WHEN 1 THEN ''high'' WHEN 2 THEN ''low'' WHEN 5 THEN ''high'' WHEN 4 THEN ''unable-to-assess'' ELSE NULL END AS criticalityCode, alle.ALG_Date AS assertedDate, alle.ALG_Time AS assertedTime, alle.ALG_LastUpdateDate AS lastUpdateDate, alle.ALG_LastUpdateTime as lastUpdateTime FROM %ALLINDEX PA_Allergy alle WHERE (alle.ALG_PAPMI_ParRef->PAPMI_No IS NOT NULL) ".concat(
					firstParams,
					"');"
				)
			);

		case 'condition':
			return executeCachedQuery();

		case 'documentreference':
			return executeCachedQuery();

		case 'encounter':
			return executeCachedQuery(
				"WITH encounter_CTE(encounterIdentifier, encounterClassDesc, encounterClassCode, encounterTypeDesc, encounterTypeCode, encounterPeriodStartDate, encounterPeriodStartTime, encounterPeriodEndDate, encounterPeriodEndTime, encounterParticipantIndividualCode_opattending, encounterParticipantIndividualDisplay_opattending, encounterHospitalizationAdmitsourceCodingCode, encounterHospitalizationAdmitsourceCodingDesc, encounterHospitalizationDischargedispositionCodingCode, encounterHospitalizationDischargedispositionCodingDesc, encounterAdmissionmethodCodingCode, encounterAdmissionmethodCodingDesc, encounterDischargemethodCodingCode, encounterDischargemethodCodingDesc, subjectReference, encounterStatus, lastUpdateDate, lastUpdateTime) AS (SELECT DISTINCT * FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT REPLACE(app.APPT_RowId, ''||'', ''-'') AS encounterIdentifier, ''ambulatory'' AS encounterClassDesc, ''AMB'' AS encounterClassCode, app.APPT_AS_ParRef->AS_RES_ParRef->RES_CTLOC_DR->CTLOC_Desc AS encounterTypeDesc, app.APPT_AS_ParRef->AS_RES_ParRef->RES_CTLOC_DR->CTLOC_Code AS encounterTypeCode, COALESCE(app.APPT_ArrivalDate, app.APPT_DateComp) AS encounterPeriodStartDate, COALESCE(app.APPT_ArrivalTime, app.APPT_TimeComp) AS encounterPeriodStartTime, NULL AS encounterPeriodEndDate, NULL AS encounterPeriodEndTime, app.APPT_SeenDoctor_DR->CTPCP_Code AS encounterParticipantIndividualCode_opattending, app.APPT_SeenDoctor_DR->CTPCP_Desc AS encounterParticipantIndividualDisplay_opattending, NULL AS encounterHospitalizationAdmitsourceCodingCode, NULL AS encounterHospitalizationAdmitsourceCodingDesc, NULL AS encounterHospitalizationDischargedispositionCodingCode, NULL AS encounterHospitalizationDischargedispositionCodingDesc, NULL AS encounterAdmissionmethodCodingCode, NULL AS encounterAdmissionmethodCodingDesc, NULL AS encounterDischargemethodCodingCode, NULL AS encounterDischargemethodCodingDesc, app.APPT_Adm_DR->PAADM_PAPMI_DR->PAPMI_No AS subjectReference, app.APPT_Status AS encounterStatus, app.APPT_LastUpdateDate AS lastUpdateDate, NULL AS lastUpdateTime FROM RB_Appointment app WHERE app.APPT_Adm_DR->PAADM_PAPMI_DR->PAPMI_No IS NOT NULL "
					.concat(
						firstParams,
						"') UNION SELECT DISTINCT * FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT REPLACE(PAADM_ADMNo, ''/'', ''-'') AS encounterIdentifier, CASE PAADM_Type WHEN ''I'' THEN ''inpatient'' WHEN ''E'' THEN ''emergency'' END as encounterClassDesc, CASE PAADM_Type WHEN ''I'' THEN ''IMP'' WHEN ''E'' THEN ''EMER'' END as encounterClassCode, PAADM_DepCode_DR->CTLOC_Desc AS encounterTypeDesc, PAADM_DepCode_DR->CTLOC_Code AS encounterTypeCode, PAADM_AdmDate AS encounterPeriodStartDate, PAADM_AdmTime AS encounterPeriodStartTime, PAADM_DischgDate AS encounterPeriodEndDate, PAADM_DischgTime AS encounterPeriodEndTime, NULL AS encounterParticipantIndividualCode_opattending, NULL AS encounterParticipantIndividualDisplay_opattending, PAADM_AdmSrc_DR->ADSOU_Code AS encounterHospitalizationAdmitsourceCodingCode, PAADM_AdmSrc_DR->ADSOU_Desc AS encounterHospitalizationAdmitsourceCodingDesc, CASE PAADM_Type WHEN ''I'' THEN PAADM_MainMRADM_DR->MRADM_DischDestin_DR->DDEST_Code ELSE NULL END AS encounterHospitalizationDischargedispositionCodingCode, CASE PAADM_Type WHEN ''I'' THEN PAADM_MainMRADM_DR->MRADM_DischDestin_DR->DDEST_Desc ELSE NULL END AS encounterHospitalizationDischargedispositionCodingDesc, PAADM_AdmMethod_DR->ADMETH_Code AS encounterAdmissionmethodCodingCode, PAADM_AdmMethod_DR->ADMETH_Desc AS encounterAdmissionmethodCodingDesc, CASE PAADM_Type WHEN ''I'' THEN PAADM_MainMRADM_DR->MRADM_ConditAtDisch_DR->DISCON_Code ELSE NULL END AS encounterDischargemethodCodingCode, CASE PAADM_Type WHEN ''I'' THEN PAADM_MainMRADM_DR->MRADM_ConditAtDisch_DR->DISCON_Desc ELSE NULL END AS encounterDischargemethodCodingDesc, PAADM_PAPMI_DR->PAPMI_No AS subjectReference, PAADM_VisitStatus AS encounterStatus, PAADM_UpdateDate AS lastUpdateDate, PAADM_UpdateTime AS lastUpdateTime FROM PA_Adm WHERE PAADM_Type IN (''I'', ''E'') AND PAADM_PAPMI_DR->PAPMI_No IS NOT NULL "
					)
					.concat(
						secondParams,
						"')) SELECT encounterIdentifier, CASE WHEN encounterStatus IN ('C', 'N', 'X', 'T', 'J', 'H') THEN 'cancelled' WHEN encounterStatus IN ('D', 'R') OR (encounterStatus IN ('A') AND encounterPeriodStartDate IS NOT NULL AND encounterPeriodEndDate IS NOT NULL) THEN 'finished' WHEN (encounterPeriodStartDate > CURRENT_TIMESTAMP AND encounterPeriodStartDate IS NOT NULL) OR encounterStatus IN ('P') THEN 'planned' WHEN encounterStatus IN ('A', 'S', 'W') THEN 'arrived' WHEN encounterPeriodStartDate IS NOT NULL AND encounterPeriodEndDate IS NULL THEN 'in-progress' ELSE 'unknown' END AS encounterStatusMapped, encounterStatus, encounterClassDesc, encounterClassCode, CASE WHEN ISNUMERIC(encounterTypeCode) <> 1 THEN NULL ELSE UPPER(encounterTypeDesc) END AS encounterTypeDesc, CASE WHEN ISNUMERIC(encounterTypeCode) <> 1 THEN NULL ELSE encounterTypeCode END AS encounterTypeCode, CONCAT(COALESCE(encounterPeriodStartDate, ''), 'T', COALESCE(encounterPeriodStartTime, '')) AS encounterPeriodStart, CONCAT(COALESCE(encounterPeriodEndDate, ''), 'T', COALESCE(encounterPeriodEndTime, '')) AS encounterPeriodEnd, encounterParticipantIndividualCode_opattending, encounterParticipantIndividualDisplay_opattending, wards.admissionWardCode AS encounterLocation1Identifier, UPPER(wards.admissionWardDesc) AS encounterLocation1Display, wards.dischargeWardCode AS encounterLocation2Identifier, UPPER(wards.dischargeWardDesc) AS encounterLocation2Display, consultants.admissionConsultantCode AS encounterParticipantIndividualCode_admitting, consultants.admissionConsultantDesc AS encounterParticipantIndividualDisplay_admitting, consultants.admissionConsultantSpecCode AS encounterTypeCodeAdm, consultants.admissionConsultantSpecDesc AS encounterTypeDescAdm, consultants.dischargeConsultantCode AS encounterParticipantIndividualCode_discharging, consultants.dischargeConsultantDesc AS encounterParticipantIndividualDisplay_discharging, consultants.dischargeConsultantSpecCode AS encounterTypeCodeDis, consultants.dischargeConsultantSpecDesc AS encounterTypeDescDis, encounterHospitalizationAdmitsourceCodingCode, encounterHospitalizationAdmitsourceCodingDesc, encounterHospitalizationDischargedispositionCodingCode, encounterHospitalizationDischargedispositionCodingDesc, encounterAdmissionmethodCodingCode, encounterAdmissionmethodCodingDesc, encounterDischargemethodCodingCode, encounterDischargemethodCodingDesc, subjectReference, CONCAT(COALESCE(lastUpdateDate, ''), 'T', COALESCE(lastUpdateTime, '')) AS lastUpdated FROM encounter_CTE LEFT JOIN (SELECT * FROM (SELECT REPLACE(PAADM_ADMNo, '/', '-') AS PAADM_ADMNo, dischargeConsultantCode, dischargeConsultantDesc, dischargeConsultantSpecCode, dischargeConsultantSpecDesc, admissionConsultantCode, admissionConsultantDesc, admissionConsultantSpecCode, admissionConsultantSpecDesc, row_number() over (partition by PAADM_ADMNo order by TRANS_ChildSub)\tAS transOrder FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT TRANS_ParRef->PAADM_AdmDocCodeDR->CTPCP_Code AS dischargeConsultantCode, TRANS_ParRef->PAADM_AdmDocCodeDR->CTPCP_Desc AS dischargeConsultantDesc, TRANS_ParRef->PAADM_AdmDocCodeDR->CTPCP_Spec_DR->CTSPC_Code dischargeConsultantSpecCode, TRANS_ParRef->PAADM_AdmDocCodeDR->CTPCP_Spec_DR->CTSPC_Desc dischargeConsultantSpecDesc, TRANS_CTCP_DR->CTPCP_Code AS admissionConsultantCode, TRANS_CTCP_DR->CTPCP_Desc AS admissionConsultantDesc, TRANS_CTCP_DR->CTPCP_Spec_DR->CTSPC_Code admissionConsultantSpecCode, TRANS_CTCP_DR->CTPCP_Spec_DR->CTSPC_Desc admissionConsultantSpecDesc, TRANS_ChildSub, TRANS_ParRef->PAADM_ADMNo FROM PA_AdmTransaction WHERE TRANS_ParRef->PAADM_Type = ''I'' AND TRANS_ParRef->PAADM_Epissubtype_DR->SUBT_Code = ''1'' AND TRANS_CTCP_DR IS NOT NULL "
					)
					.concat(
						thirdParams,
						"')) a WHERE transOrder = 1) consultants ON encounter_CTE.encounterIdentifier = consultants.PAADM_ADMNo LEFT JOIN (SELECT * FROM (SELECT REPLACE(PAADM_ADMNo, '/', '-') AS PAADM_ADMNo, dischargeWardCode, dischargeWardDesc, admissionWardCode, admissionWardDesc, row_number() over (partition by PAADM_ADMNo order by TRANS_ChildSub)\tAS transOrder FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT TRANS_ParRef->PAADM_CurrentWard_DR->WARD_Code AS dischargeWardCode, TRANS_ParRef->PAADM_CurrentWard_DR->WARD_Desc AS dischargeWardDesc, TRANS_Ward_DR->WARD_Code AS admissionWardCode, TRANS_Ward_DR->WARD_Desc AS admissionWardDesc, TRANS_ChildSub, TRANS_ParRef->PAADM_ADMNo FROM PA_AdmTransaction WHERE TRANS_ParRef->PAADM_Type = ''I'' AND TRANS_ParRef->PAADM_Epissubtype_DR->SUBT_Code = ''1'' AND TRANS_Ward_DR IS NOT NULL "
					)
					.concat(
						thirdParams,
						"')) a WHERE transOrder = 1) wards ON encounter_CTE.encounterIdentifier = wards.PAADM_ADMNo;"
					)
			);

		case 'flag':
			return executeCachedQuery(
				"WITH flag_CTE AS (SELECT DISTINCT flagId, CASE ALM_Status WHEN 'I' THEN 'inactive' WHEN 'A' THEN 'active' END AS flagStatusCode, flagCategoryCodingDisplay, flagCategoryCodingCode, flagCodeCodingDisplay, flagCodeCodingCode, flagCodeText, flagSubjectReference, CONCAT(COALESCE(periodStartDate, ''),'T', COALESCE(periodStartTime, '')) AS periodStart, CONCAT(COALESCE(periodEndDate, ''),'T00:00:00') AS periodEnd FROM OPENQUERY( [ENYH-PRD-ANALYTICS], 'SELECT DISTINCT TOP 100 ALM_Status, COALESCE(ALM_OnsetDate, ALM_CreateDate) AS periodStartDate, COALESCE(ALM_OnsetTime, ALM_CreateTime) AS periodStartTime, COALESCE(ALM_ExpiryDate, ALM_ClosedDate) AS periodEndDate, ALM_ClosedTime AS periodEndTime,     REPLACE(alert.ALM_RowID, ''||'', ''-'') AS flagId, alert.ALM_Alert_DR->ALERT_Desc AS flagCodeCodingDisplay, alert.ALM_Alert_DR->ALERT_Code AS flagCodeCodingCode, alert.ALM_Message AS flagCodeText, alert.ALM_AlertCategory_DR->ALERTCAT_Desc AS flagCategoryCodingDisplay, alert.ALM_AlertCategory_DR->ALERTCAT_Code AS flagCategoryCodingCode, alert.ALM_PAPMI_ParRef->PAPMI_No AS flagSubjectReference FROM PA_AlertMsg alert WHERE alert.ALM_Alert_DR IS NOT NULL "
					.concat(
						firstParams,
						"')) SELECT flag_CTE.*, snom.SNOMED_Code AS flagCodeCodingSnomedCode, snom.SNOMED_Display AS flagCodeCodingSnomedDisplay FROM flag_CTE LEFT JOIN lookup.dbo.ydh_alert_list snom ON flag_CTE.flagCodeCodingCode = snom.YDH_TrakCare_Code WHERE flagStatusCode IS NOT NULL "
					)
					.concat(secondParams, ';')
			);

		case 'medicationstatement':
			return executeCachedQuery(
				"WITH medicationStatement_CTE AS (SELECT DISTINCT medstatId, CONCAT(COALESCE(medstatDateassertedDate, ''),'T', COALESCE(medstatDateassertedTime, '')) AS medstatDateasserted, CONCAT(COALESCE(medstatEffectiveStart_Datepart, ''),'T', COALESCE(medstatEffectiveStart_Timepart, '')) AS medstatEffectiveStart, CONCAT(COALESCE(medstatEffectiveEnd_Datepart, ''),'T', COALESCE(medstatEffectiveEnd_Timepart, '')) AS medstatEffectiveEnd, medstatSubjectReference, medstatDosageTimingRepeatDuration, CASE medstatDosageTimingRepeatDurationUnit WHEN 'DO' THEN NULL WHEN 'M' THEN 'min' WHEN 'H' THEN 'h' WHEN 'D' THEN 'd' WHEN 'W' THEN 'wk' END AS medstatDosageTimingRepeatDurationUnit, medstatDosagePatientinstruction, medstatDosageRouteText, medstatDosageDoseQuantityValue, medstatDosageDoseQuantityUnit, CASE WHEN (CURRENT_TIMESTAMP BETWEEN CAST(medstatEffectiveStart_Datepart AS DATETIME) + CAST(medstatEffectiveStart_Timepart AS DATETIME) AND CAST(medstatEffectiveEnd_Datepart AS DATETIME) + CAST(medstatEffectiveEnd_Timepart AS DATETIME)) AND orderItemStatus != 'Discontinued' THEN 'active' WHEN CURRENT_TIMESTAMP < CAST(medstatEffectiveStart_Datepart AS DATETIME) + CAST(medstatEffectiveStart_Timepart AS DATETIME) AND orderItemStatus != 'Discontinued' THEN 'intended' WHEN CURRENT_TIMESTAMP > CAST(medstatEffectiveEnd_Datepart AS DATETIME) + CAST(medstatEffectiveEnd_Timepart AS DATETIME) AND orderItemStatus != 'Discontinued' THEN 'completed' WHEN orderItemStatus = 'Discontinued' AND orderItemVariance IN ('DATA', 'ERROR') THEN 'entered-in-error' WHEN orderItemStatus = 'Discontinued' THEN 'stopped' END AS medstatStatusCode, medicationId, medicationCodeText, medicationCodeCodingDisplay, medicationCodeCodingCode FROM OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT DISTINCT REPLACE(oi.OEORI_RowID, ''||'', ''-'') AS medstatId, oi.OEORI_Date AS medstatDateassertedDate, oi.OEORI_TimeOrd AS medstatDateassertedTime, oi.OEORI_SttDat AS medstatEffectiveStart_Datepart, oi.OEORI_SttTim AS medstatEffectiveStart_Timepart, oi.OEORI_EndDate AS medstatEffectiveEnd_Datepart, oi.OEORI_EndTime AS medstatEffectiveEnd_Timepart, oi.OEORI_OEORD_ParRef->OEORD_Adm_DR->PAADM_PAPMI_DR->PAPMI_No AS medstatSubjectReference, oi.OEORI_OEOrdItem2_DR->ITM2_DurationValue AS medstatDosageTimingRepeatDuration, oi.OEORI_OEOrdItem2_DR->ITM2_DurationUnit AS medstatDosageTimingRepeatDurationUnit, oi.OEORI_Instr_DR->PHCIN_Desc1 AS medstatDosagePatientinstruction, oi.OEORI_AdminRoute_DR->ADMR_Desc AS medstatDosageRouteText, oi.OEORI_DoseQty AS medstatDosageDoseQuantityValue, oi.OEORI_Unit_DR->CTUOM_Desc AS medstatDosageDoseQuantityUnit, oi.OEORI_ItemStat_DR->OSTAT_Desc AS orderItemStatus, oi.OEORI_VarianceReason_DR->VR_Code AS orderItemVariance, NULL AS RESOURCE_LINEBREAK, oi.OEORI_ItmMast_DR->ARCIM_Abbrev AS medicationCodeText, REPLACE(oi.OEORI_ItmMast_DR->ARCIM_RowID, ''||'', ''-'') AS medicationId, arcex.EXT_Desc AS medicationCodeCodingDisplay, arcex.EXT_Code AS medicationCodeCodingCode FROM OE_OrdItem oi LEFT JOIN ARC_ItmMast arc ON oi.OEORI_ItmMast_DR = arc.ARCIM_RowId LEFT JOIN ARC_ItemExternalCodes arcex ON arc.ARCIM_RowId = arcex.EXT_ParRef AND EXT_HL7SendingFacility = ''FDB'' AND EXT_HL7SendingApp IN (''AMPP'', ''VMPP'') WHERE oi.OEORI_Categ_DR->ORCAT_Desc IN (''PHARMACY'', ''PHARM'') "
					.concat(
						firstParams,
						"')) SELECT * FROM medicationStatement_CTE WHERE medstatStatusCode IS NOT NULL "
					)
					.concat(secondParams, ';')
			);

		case 'patient':
			return executeCachedQuery(
				"SELECT DISTINCT nhsNumber, nhsNumberTraceStatusDesc, nhsNumberTraceStatusCode, patientNo, active, ethnicCategoryDesc, ethnicCategoryCode, homePhone, businessPhone, mobilePhone, appointmentSMS, email, preferredContactMethod, preferredLanguage, interpreterRequired, nameFamily, nameGiven1First, nameGiven2Middle, namePrefix, maritalStatusDesc, maritalStatusCode, addressLine1, addressLine2, city, district, postalCode, LOWER(gender) AS gender, birthdate, deceased, gpDesc, gpAddressLine1, gpAddressLine2, gpCity, gpPostalCode, gpIdentifier, contactName, contactPhone, contactText, dnd.DND, CONCAT(COALESCE(lastUpdateDate, ''), 'T', COALESCE(lastUpdateTime, '')) AS lastUpdated FROM OPENQUERY( [ENYH-PRD-ANALYTICS], 'SELECT DISTINCT patmas.PAPMI_PAPER_DR->PAPER_ID AS nhsNumber, patmas.PAPMI_TraceStatus_DR->TRACE_Desc AS nhsNumberTraceStatusDesc, patmas.PAPMI_TraceStatus_DR AS nhsNumberTraceStatusCode, patmas.PAPMI_No AS patientNo, patmas.PAPMI_Active, CASE WHEN patmas.PAPMI_Active IS NULL THEN ''true'' WHEN patmas.PAPMI_Active = ''Y'' THEN ''true'' ELSE NULL END AS active, patmas.PAPMI_PAPER_DR->PAPER_IndigStat_DR->INDST_Desc AS ethnicCategoryDesc, patmas.PAPMI_PAPER_DR->PAPER_IndigStat_DR->INDST_Code AS ethnicCategoryCode, patmas.PAPMI_PAPER_DR->PAPER_TelH AS homePhone, patmas.PAPMI_PAPER_DR->PAPER_TelO AS businessPhone, patmas.PAPMI_PAPER_DR->PAPER_MobPhone AS mobilePhone, patmas.PAPMI_PAPER_DR->PAPER_AppointmentSMS AS appointmentSMS, patmas.PAPMI_PAPER_DR->PAPER_Email AS Email, patmas.PAPMI_PAPER_DR->PAPER_PreferredContactMethod AS PreferredContactMethod, patmas.PAPMI_PAPER_DR->PAPER_PrefLanguage_DR->PREFL_Desc AS PreferredLanguage, patmas.PAPMI_PAPER_DR->PAPER_InterpreterRequired AS InterpreterRequired, patmas.PAPMI_PAPER_DR->PAPER_UpdateDate AS lastUpdateDate, patmas.PAPMI_PAPER_DR->PAPER_UpdateTime AS lastUpdateTime, patmas.PAPMI_PAPER_DR->PAPER_Name AS nameFamily, patmas.PAPMI_PAPER_DR->PAPER_Name2 AS nameGiven1First, patmas.PAPMI_PAPER_DR->PAPER_Name3 AS nameGiven2Middle, patmas.PAPMI_PAPER_DR->PAPER_Title_DR->TTL_Desc AS nameprefix, patmas.PAPMI_PAPER_DR->PAPER_NokName AS contactName, patmas.PAPMI_PAPER_DR->PAPER_NokPhone AS contactPhone, patmas.PAPMI_PAPER_DR->PAPER_NokText AS contactText, CASE patmas.PAPMI_PAPER_DR->PAPER_Marital_DR->CTMAR_RowId WHEN 1 THEN ''Married'' WHEN 2 THEN ''unknown'' WHEN 3 THEN ''Widowed'' WHEN 4 THEN ''unmarried'' WHEN 5 THEN ''Legally Seperated'' WHEN 6 THEN ''Divorced'' END AS maritalStatusDesc, CASE patmas.PAPMI_PAPER_DR->PAPER_Marital_DR->CTMAR_Code WHEN ''N'' THEN ''U'' ELSE patmas.PAPMI_PAPER_DR->PAPER_Marital_DR->CTMAR_Code END AS maritalStatusCode, patmas.PAPMI_PAPER_DR->PAPER_StName AS addressLine1, patmas.PAPMI_PAPER_DR->PAPER_ForeignAddress AS addressLine2, patmas.PAPMI_PAPER_DR->PAPER_CityCode_DR->CTCIT_Desc AS city, patmas.PAPMI_PAPER_DR->PAPER_CT_Province_DR->PROV_Desc AS district, patmas.PAPMI_PAPER_DR->PAPER_Zip_DR->CTZIP_Code AS postalCode, CASE patmas.PAPMI_PAPER_DR->PAPER_Sex_DR->CTSEX_RowId WHEN 1 THEN ''female'' WHEN 2 THEN ''unknown'' WHEN 3 THEN ''other'' WHEN 4 THEN ''male'' END AS gender, patmas.PAPMI_DOB AS birthDate, CASE WHEN patmas.PAPMI_PAPER_DR->PAPER_Deceased = ''Y'' THEN ''true'' WHEN patmas.PAPMI_PAPER_DR->PAPER_Deceased = ''N'' THEN NULL END AS deceased, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctor_DR->REFD_Desc AS gpDesc, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctorClinic_DR->CLN_Address1 AS gpAddressLine1, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctorClinic_DR->CLN_Address2 AS gpAddressLine2, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctorClinic_DR->CLN_City_DR->CTCIT_Desc AS gpCity, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctorClinic_DR->CLN_Zip_DR->CTZIP_Code AS gpPostalCode, patmas.PAPMI_PAPER_DR->PAPER_FamilyDoctorClinic_DR->CLN_Code AS gpIdentifier, patmas.PAPMI_VIPFlag AS restrictedPatient FROM %ALLINDEX PA_PatMas patmas WHERE COALESCE(PAPMI_Active,''Y'') = ''Y'' AND (patmas.PAPMI_PAPER_DR->PAPER_ID IS NOT NULL OR patmas.PAPMI_No IS NOT NULL) ".concat(
					firstParams,
					"') AS patient LEFT JOIN OPENQUERY([ENYH-PRD-ANALYTICS], 'SELECT DISTINCT ALM_PAPMI_ParRef->PAPMI_PAPER_DR->PAPER_ID AS DND FROM PA_AlertMsg WHERE ALM_Alert_DR->ALERT_Desc IN (''Do not disclose patient address'') AND (ALM_ClosedDate IS NULL OR ALM_ClosedDate < CURRENT_TIMESTAMP)') AS dnd ON patient.nhsNumber = dnd.DND;"
				)
			);

		default:
			break;
	}
}
